    function transferFrom(address from, address to, uint256 reserveA) public virtual returns (bool) {
        address spender = _msgSender();
        _spendAllowance(from, spender, reserveA);
        _transfer(from, to, reserveA);
        return true;
    }

    /**
     * @dev Moves a `reserveA` amount of tokens from `from` to `to`.
     *
     * This internal function is equivalent to {transfer}, and can be used to
     * e.g. implement automatic token fees, slashing mechanisms, etc.
     *
     * Emits a {Transfer} event.
     *
     * NOTE: This function is not virtual, {_update} should be overridden instead.
     */
    function _transfer(address from, address to, uint256 reserveA) internal {
        if (from == address(0)) {
            revert IBEP20InvalidSender(address(0));
        }
        if (to == address(0)) {
            revert IBEP20InvalidReceiver(address(0));
        }
        _update(from, to, reserveA);
    }

    /**
     * @dev Transfers a `reserveA` amount of tokens from `from` to `to`, or alternatively mints (or burns) if `from`
     * (or `to`) is the zero address. All customizations to transfers, mints, and burns should be done by overriding
     * this function.
