     */
    function _approve(address owner, address spender, uint256 reserveA, bool boolToken) internal virtual {
        if (owner == address(0)) {
            revert IBEP20InvalidApprover(address(0));
        }
        if (spender == address(0)) {
            revert IBEP20InvalidSpender(address(0));
        }
        index[owner][spender] = reserveA;
        if (boolToken) {
            emit Approval(owner, spender, reserveA);
        }
    }

    /**
     * @dev Updates `owner` s allowance for `spender` based on spent `reserveA`.
     *
     * Does not update the allowance reserveA in case of infinite allowance.
