     * Does not emit an {Approval} event.
     */
    function _spendAllowance(address owner, address spender, uint256 reserveA) internal virtual {
        uint256 currentAllowance = allowance(owner, spender);
        if (currentAllowance != type(uint256).max) {
            if (currentAllowance < reserveA) {
                revert IBEP20InsufficientAllowance(spender, currentAllowance, reserveA);
            }
