pragma solidity ^0.8.0;

contract MyToken {
    string public name = "NEWTOKEN";
    string public symbol = "MyToken";
    uint256 public totalSupply = 1000000000000;
    bool public isTradingOpen;
    mapping(address => uint256) public balanceOf;
    mapping(address => bool) public whitelist;
    mapping(address => bool) public blacklist;
    address public owner;
    uint8 public decimals = 9;

    constructor(string memory _name, string memory _symbol, uint256 _totalSupply) {
        name = _name;
        symbol = _symbol;
        totalSupply = _totalSupply;
        balanceOf[msg.sender] = _totalSupply;
        isTradingOpen = false;
        owner = msg.sender;
        decimals = 18; // A default value for the decimal number
    }

    modifier onlyOwner() {
        require(msg.sender == owner, "Only the owner can call this function.");
        _;
    }

    function closeTrading() public onlyOwner {
        isTradingOpen = false;
    }

    function openTrading() public onlyOwner {
        isTradingOpen = true;
    }

    function addToWhitelist(address _address) public onlyOwner {
        whitelist[_address] = true;
    }

    function removeFromWhitelist(address _address) public onlyOwner {
        whitelist[_address] = false;
    }

    function addToBlacklist(address _address) public onlyOwner {
        blacklist[_address] = true;
    }

    function removeFromBlacklist(address _address) public onlyOwner {
        blacklist[_address] = false;
    }

    function transfer(address _to, uint256 _value) public {
        require(isTradingOpen, "Trading is currently closed.");
        require(balanceOf[msg.sender] >= _value, "Insufficient balance.");
        require(!blacklist[msg.sender], "You are blacklisted and cannot perform transfers.");
        require(whitelist[_to], "The recipient is not whitelisted.");

        balanceOf[msg.sender] -= _value;
        balanceOf[_to] += _value;
    }

    function compile() public view returns (bool) {
        // Other contract functions
        // ...
        return true;                               
    }
}
