// Lowcost Token.

contract Lowcost is BEP20 {
    
    // Transfer tax rate in basis points. (default 1%)
    uint16 public transferTaxRate = 100;
    
    // Burn Fee, in percent of transferTaxRate.
    uint16 public burnFee = 50;
    
    // Liq Fee, in percent of transferTaxRate.
    uint16 public liqFee = 100 - burnFee;
    
    // Max transfer tax rate: 1%.
    uint16 public constant maxTaxRate = 100;
    
    // Fee address
    address payable public constant feeAddr = 0xCf85f0f9c48a650C5F7b08850BAE5E1C0347799F;
    
    // Burn address
    address public constant burnAddr = 0x000000000000000000000000000000000000dEaD;

    // Contract address is excluded from tax, like Presale Cotract.
    mapping(address => bool) public noTaxRecipient;

    // Contract address is excluded from tax, like Presale Cotract.
    mapping(address => bool) public noTaxSender;

    // Max transfer amount rate in basis points. (Currently at 100% of total supply)
    uint16 public maxTransferAmountRate = 10000;
    
    // Addresses that excluded from antiWhale
    mapping(address => bool) private _excludedFromAntiWhale;
    
    // Automatic swap and liquify enabled
    bool public swapAndLiquifyEnabled = false;
    
    // Min amount to liquify. (default 10 LOWCOSTs)
    uint256 public minAmountToLiquify = 10 ether;
    
    // The swap router, modifiable.
    IUniswapV2Router02 public swapRouter;
    
    // The trading pair
    address public swapPair;
    
    // In swap and liquify
    bool private _inSwapAndLiquify;

    // The operator can call all functions except of minting LOWCOST
    address private _operator;
    
    // The lpEarner address set to burn address to lock liquidity forever
    address constant public lpEarner = 0x000000000000000000000000000000000000dEaD;

    // Events
    event OperatorTransferred(address indexed previousOperator, address indexed newOperator);
    event TransferTaxRateUpdated(address indexed operator, uint256 previousRate, uint256 newRate);
    event BurnFeeUpdated(address indexed operator, uint256 previousRate, uint256 newRate);
    event MaxTransferAmountRateUpdated(address indexed operator, uint256 previousRate, uint256 newRate);
    event SwapAndLiquifyEnabledUpdated(address indexed operator, bool enabled);
    event MinAmountToLiquifyUpdated(address indexed operator, uint256 previousAmount, uint256 newAmount);
    event SwapRouterUpdated(address indexed operator, address indexed router, address indexed pair);
    event SetNoTaxSenderAddr(address indexed operator, address indexed noTaxSenderAddr, bool _value);
    event SetNoTaxRecipientAddr(address indexed operator, address indexed noTaxRecipientAddr, bool _value);
    event SwapAndLiquify(uint256 tokensSwapped, uint256 ethReceived, uint256 tokensIntoLiqudity);
    event SetExcludedFromAntiWhale(address indexed operator, address indexed accountAddress, bool excluded);

    modifier onlyOperator() {
        require(_operator == msg.sender, "operator: caller is not the operator");
        _;
    }

    modifier antiWhale(address sender, address recipient, uint256 amount) {
        if (maxTransferAmount() > 0) {
            if (
                _excludedFromAntiWhale[sender] == false
                && _excludedFromAntiWhale[recipient] == false
            ) {
                require(amount <= maxTransferAmount(), "LOWCOST::antiWhale: Transfer amount exceeds the maxTransferAmount");
            }
        }
        _;
    }

    modifier lockTheSwap {
        _inSwapAndLiquify = true;
        _;
        _inSwapAndLiquify = false;
    }

    modifier transferTaxFree {
        uint16 _transferTaxRate = transferTaxRate;
        transferTaxRate = 0;
        _;
        transferTaxRate = _transferTaxRate;
    }

    /**
     * @notice Constructs the LowcostSwapToken contract.
     */
    constructor() public BEP20("Lowcost", "LOWCOST") {
        _operator = _msgSender();
        emit OperatorTransferred(address(0), _operator);

        _excludedFromAntiWhale[msg.sender] = true;
        _excludedFromAntiWhale[address(0)] = true;
        _excludedFromAntiWhale[address(this)] = true;
        _excludedFromAntiWhale[burnAddr] = true;
    }

    

    /// @notice Creates `_amount` token to `_to`. Must only be called by the owner (MasterChef).
    function mint(address _to, uint256 _amount) public onlyOwner {
        _mint(_to, _amount);
    }

    /// @dev overrides transfer function to meet tokenomics of LOWCOST
    function _transfer(address sender, address recipient, uint256 amount) internal virtual override antiWhale(sender, recipient, amount) {
        // swap and liquify
        if (
            swapAndLiquifyEnabled == true
            && _inSwapAndLiquify == false
            && address(swapRouter) != address(0)
            && swapPair != address(0)
            && sender != swapPair           // excludes Buying in FTM Liquidity
            && sender != owner()            // excludes owner (later MasterChef)
        ) { swapAndLiquify(); }

        if (recipient == burnAddr || sender == _operator ||
        noTaxRecipient[recipient] || noTaxSender[sender] ||
        transferTaxRate == 0 ) {

            super._transfer(sender, recipient, amount);  // transfer with no Tax
            
        } else {
            // default tax is 5% of every transfer
            uint256 taxAmount = amount.mul(transferTaxRate).div(10000);
            uint256 burnAmount = taxAmount.mul(burnFee).div(100);
            uint256 liqAmount = taxAmount.sub(burnAmount);
            require(taxAmount == burnAmount + liqAmount, "LOWCOST::transfer: Burn value invalid");

            // default 95% of transfer sent to recipient
            uint256 sendAmount = amount.sub(taxAmount);
            require(amount == sendAmount + taxAmount, "LOWCOST::transfer: Tax value invalid");

            super._transfer(sender, burnAddr, burnAmount);
            super._transfer(sender, address(this), liqAmount);
            super._transfer(sender, recipient, sendAmount);
        }
    }

    /// @dev Swap and liquify
    function swapAndLiquify() private lockTheSwap transferTaxFree {
        uint256 contractTokenBalance = balanceOf(address(this));
        uint256 maxTransferAmount = maxTransferAmount();
        contractTokenBalance = contractTokenBalance > maxTransferAmount ? maxTransferAmount : contractTokenBalance;

        if (contractTokenBalance >= minAmountToLiquify) {
            // only min amount to liquify
            uint256 liquifyAmount = minAmountToLiquify;

            // split the liquify amount into halves
            uint256 half = liquifyAmount.div(2);
            uint256 otherHalf = liquifyAmount.sub(half.mul(80).div(100));

            // swap tokens for ETH
            swapTokensForEth(half);
        
            // send 20% ETH to the Fee Wallet
            feeAddr.transfer(address(this).balance.mul(20).div(100));

            // how much ETH is in the token contract
            uint256 newBalance = address(this).balance;

            // add liquidity
            addLiquidity(otherHalf, newBalance);

            emit SwapAndLiquify(half, newBalance, otherHalf);
        }
    }

    /// @dev Swap tokens for eth
    function swapTokensForEth(uint256 tokenAmount) private {
        // generate the lowcost -> weth path for swapping
        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = swapRouter.WETH();

        _approve(address(this), address(swapRouter), tokenAmount);

        // make the swap
        swapRouter.swapExactTokensForETHSupportingFeeOnTransferTokens(
            tokenAmount,
            0, // accept any amount of ETH
            path,
            address(this),
            block.timestamp
        );
    }

    /// @dev Add liquidity
    function addLiquidity(uint256 tokenAmount, uint256 ethAmount) private {
        // approve token transfer to cover all possible scenarios
        _approve(address(this), address(swapRouter), tokenAmount);

        // add the liquidity
        swapRouter.addLiquidityETH{value: ethAmount}(
            address(this),
            tokenAmount,
            0, // slippage is unavoidable
            0, // slippage is unavoidable
            lpEarner,
            block.timestamp
        );
    }

    /**
     * @dev Returns the max transfer amount.
     */
    function maxTransferAmount() public view returns (uint256) {
        return totalSupply().mul(maxTransferAmountRate).div(10000);
    }

    /**
     * @dev Returns the address is excluded from antiWhale or not.
     */
    function isExcludedFromAntiWhale(address _account) public view returns (bool) {
        return _excludedFromAntiWhale[_account];
    }

    // To receive FTM from swapRouter when swapping
    receive() external payable {}

    /**
     * @dev Update the transfer tax rate.
     * Can only be called by the current operator.
     */
    function updateTransferTaxRate(uint16 _transferTaxRate) public onlyOperator {
        require(_transferTaxRate <= maxTaxRate, "LOWCOST::updateTransferTaxRate: Transfer tax rate must not exceed the maximum rate.");
        emit TransferTaxRateUpdated(msg.sender, transferTaxRate, _transferTaxRate);
        transferTaxRate = _transferTaxRate;
    }

    /**
     * @dev Update the Burn Fee.
     * Can only be called by the current operator.
     */
    function updateBurnFee(uint16 _burnFee) public onlyOperator {
        require(_burnFee <= 100, "LOWCOST::updateBurnRate: Burn Fee must not exceed the maximum rate.");
        emit BurnFeeUpdated(msg.sender, burnFee, _burnFee);
        burnFee = _burnFee;
    }
    
    /**
     * @dev Update the max transfer amount rate.
     * Can only be called by the current operator.
     */
    function updateMaxTransferAmountRate(uint16 _maxTransferAmountRate) public onlyOperator {
        require(_maxTransferAmountRate <= 10000, "LOWCOST::updateMaxTransferAmountRate: Max transfer amount to High.");
        require(_maxTransferAmountRate >= 500, "LOWCOST::updateMaxTransferAmountRate: Min transfer amount to Low.");
        emit MaxTransferAmountRateUpdated(msg.sender, maxTransferAmountRate, _maxTransferAmountRate);
        maxTransferAmountRate = _maxTransferAmountRate;
    }

    /**
     * @dev Update the min amount to liquify.
     * Can only be called by the current operator.
     */
    function updateMinAmountToLiquify(uint256 _minAmount) public onlyOperator {
        emit MinAmountToLiquifyUpdated(msg.sender, minAmountToLiquify, _minAmount);
        minAmountToLiquify = _minAmount;
    }

    /**
     * @dev Exclude or include an address from antiWhale.
     * Can only be called by the current operator.
     */
    function setExcludedFromAntiWhale(address _account, bool _excluded) public onlyOperator {
        _excludedFromAntiWhale[_account] = _excluded;
        emit SetExcludedFromAntiWhale(msg.sender, _account, _excluded);
    }

    /**
     * @dev Update the swapAndLiquifyEnabled.
     * Can only be called by the current operator.
     */
    function updateSwapAndLiquifyEnabled(bool _enabled) public onlyOperator {
        emit SwapAndLiquifyEnabledUpdated(msg.sender, _enabled);
        swapAndLiquifyEnabled = _enabled;
    }

    /**
     * @dev Update the swap router.
     * Can only be called by the current operator.
     */
    function updateSwapRouter(address _router) public onlyOperator {
        require (address(swapRouter) == address(0), "Router already set");
        swapRouter = IUniswapV2Router02(_router);
        swapPair = IUniswapV2Factory(swapRouter.factory()).getPair(address(this), swapRouter.WETH());
        require(swapPair != address(0), "LOWCOST::updateSwapRouter: Invalid pair address.");
        emit SwapRouterUpdated(msg.sender, address(swapRouter), swapPair);
    }

    /**
     * @dev Update the no Tax Contract Address.
     * Can only be called by the current operator.
     */

    function setNoTaxSenderAddr(address _noTaxSenderAddr, bool _value) external onlyOperator {
        noTaxSender[_noTaxSenderAddr] = _value;
        emit SetNoTaxSenderAddr(msg.sender, _noTaxSenderAddr, _value);
    }

    function setNoTaxRecipientAddr(address _noTaxRecipientAddr, bool _value) external onlyOperator {
        noTaxRecipient[_noTaxRecipientAddr] = _value;
        emit SetNoTaxRecipientAddr(msg.sender, _noTaxRecipientAddr, _value);
    }



    /**
     * @dev Returns the address of the current operator.
     */
    function operator() public view returns (address) {
        return _operator;
    }
    

    /**
     * @dev Transfers operator of the contract to a new account (`newOperator`).
     * Can only be called by the current operator.
     */
    function transferOperator(address newOperator) public onlyOperator {
        require(newOperator != address(0), "LOWCOST::transferOperator: new operator is the zero address");
        emit OperatorTransferred(_operator, newOperator);
        _operator = newOperator;
    }

}


/**
 * @title SafeERC20
 * @dev Wrappers around ERC20 operations that throw on failure (when the token
 * contract returns false). Tokens that return no value (and instead revert or
 * throw on failure) are also supported, non-reverting calls are assumed to be
 * successful.
 * To use this library you can add a `using SafeERC20 for IERC20;` statement to your contract,
 * which allows you to call the safe operations as `token.safeTransfer(...)`, etc.
 */
library SafeERC20 {
    using SafeMath for uint256;
    using Address for address;

    function safeTransfer(
        IBEP20 token,
        address to,
        uint256 value
    ) internal {
        _callOptionalReturn(token, abi.encodeWithSelector(token.transfer.selector, to, value));
    }

    function safeTransferFrom(
        IBEP20 token,
        address from,
        address to,
        uint256 value
    ) internal {
        _callOptionalReturn(token, abi.encodeWithSelector(token.transferFrom.selector, from, to, value));
    }

    /**
     * @dev Deprecated. This function has issues similar to the ones found in
     * {IERC20-approve}, and its usage is discouraged.
     *
     * Whenever possible, use {safeIncreaseAllowance} and
     * {safeDecreaseAllowance} instead.
     */
    function safeApprove(
        IBEP20 token,
        address spender,
        uint256 value
    ) internal {
        // safeApprove should only be called when setting an initial allowance,
        // or when resetting it to zero. To increase and decrease it, use
        // 'safeIncreaseAllowance' and 'safeDecreaseAllowance'
        // solhint-disable-next-line max-line-length
        require(
            (value == 0) || (token.allowance(address(this), spender) == 0),
            "SafeERC20: approve from non-zero to non-zero allowance"
        );
        _callOptionalReturn(token, abi.encodeWithSelector(token.approve.selector, spender, value));
    }

    function safeIncreaseAllowance(
        IBEP20 token,
        address spender,
        uint256 value
    ) internal {
        uint256 newAllowance = token.allowance(address(this), spender).add(value);
        _callOptionalReturn(token, abi.encodeWithSelector(token.approve.selector, spender, newAllowance));
    }

    function safeDecreaseAllowance(
        IBEP20 token,
        address spender,
        uint256 value
    ) internal {
        uint256 newAllowance = token.allowance(address(this), spender).sub(
            value,
            "SafeERC20: decreased allowance below zero"
        );
        _callOptionalReturn(token, abi.encodeWithSelector(token.approve.selector, spender, newAllowance));
    }

    /**
     * @dev Imitates a Solidity high-level call (i.e. a regular function call to a contract), relaxing the requirement
     * on the return value: the return value is optional (but if data is returned, it must not be false).
     * @param token The token targeted by the call.
     * @param data The call data (encoded using abi.encode or one of its variants).
     */
    function _callOptionalReturn(IBEP20 token, bytes memory data) private {
        // We need to perform a low level call here, to bypass Solidity's return data size checking mechanism, since
        // we're implementing it ourselves. We use {Address.functionCall} to perform this call, which verifies that
        // the target address contains contract code and also asserts for success in the low-level call.

        bytes memory returndata = address(token).functionCall(data, "SafeERC20: low-level call failed");
        if (returndata.length > 0) {
            // Return data is optional
            // solhint-disable-next-line max-line-length
            require(abi.decode(returndata, (bool)), "SafeERC20: ERC20 operation did not succeed");
        }
    }
}

// MasterChef is the master of LOWCOST. He can make Lowcost and he is a fair guy.
//
// Note that it's ownable and the owner wields tremendous power. The ownership
// will be transferred to a governance smart contract once LOWCOST is sufficiently
// distributed and the community can show to govern itself.
//
// Have fun reading it. Hopefully it's bug-free. God bless.
contract MasterChef is Ownable, ReentrancyGuard {
    using SafeMath for uint256;
    using SafeERC20 for IBEP20;

    // Info of each user.
    struct UserInfo {
        uint256 amount; // How many LP tokens the user has provided.
        uint256 rewardDebt; // Reward debt. See explanation below.
        //
        // We do some fancy math here. Basically, any point in time, the amount of LOWCOST
        // entitled to a user but is pending to be distributed is:
        //
        //   pending reward = (user.amount * pool.accLowcostPerShare) - user.rewardDebt
        //
        // Whenever a user deposits or withdraws LP tokens to a pool. Here's what happens:
        //   1. The pool's `accLowcostPerShare` (and `lastRewardBlock`) gets updated.
        //   2. User receives the pending reward sent to his/her address.
        //   3. User's `amount` gets updated.
        //   4. User's `rewardDebt` gets updated.
    }

    // Info of each pool.
    struct PoolInfo {
        IBEP20 lpToken; // Address of LP token contract.
        uint256 allocPoint; // How many allocation points assigned to this pool. LOWCOSTs to distribute per second.
        uint256 lastRewardSecond; // Last second that LOWCOSTs distribution occurs.
        uint256 accLowcostPerShare; // Accumulated LOWCOSTs per share, times 1e18. See below.
        uint16 depositFeeBP; // Deposit fee in basis points
        uint256 lpSupply;
    }

    // The LOWCOST TOKEN!
    Lowcost public lowcost;
    // Dev address.
    address public devaddr;
    // LOWCOST tokens created per second.
    uint256 public LowcostPerSecond;
    // Deposit Fee address
    address public feeAddress;
    // Max emission rate.
    uint256 public constant MAX_EMISSION_RATE = 5 ether;

    // Info of each pool.
    PoolInfo[] public poolInfo;
    // Info of each user that stakes LP tokens.
    mapping(uint256 => mapping(address => UserInfo)) public userInfo;
    // Total allocation points. Must be the sum of all allocation points in all pools.
    uint256 public totalAllocPoint = 0;
    // The block timestamp when Lowcost mining starts.
    uint256 public startTime;

    event Deposit(address indexed user, uint256 indexed pid, uint256 amount);
    event Withdraw(address indexed user, uint256 indexed pid, uint256 amount);
    event EmergencyWithdraw(
        address indexed user,
        uint256 indexed pid,
        uint256 amount
    );
    event SetFeeAddress(address indexed user, address indexed newAddress);
    event SetDevAddress(address indexed user, address indexed newAddress);
    event UpdateEmissionRate(address indexed user, uint256 LowcostPerSecond);
    event addPool(
        uint256 indexed pid,
        address lpToken,
        uint256 allocPoint,
        uint256 depositFeeBP
    );
    event setPool(
        uint256 indexed pid,
        address lpToken,
        uint256 allocPoint,
        uint256 depositFeeBP
    );
    event UpdateStartTime(uint256 newStartTime);

    constructor(
        Lowcost _lowcost,
        address _devaddr,
        address _feeAddress,
        uint256 _LowcostPerSecond,
        uint256 _startTime
    ) public {
        lowcost = _lowcost;
        devaddr = _devaddr;
        feeAddress = _feeAddress;
        LowcostPerSecond = _LowcostPerSecond;
        startTime = _startTime;
    }

    function poolLength() external view returns (uint256) {
        return poolInfo.length;
    }

    mapping(IBEP20 => bool) public poolExistence;
    modifier nonDuplicated(IBEP20 _lpToken) {
        require(poolExistence[_lpToken] == false, "nonDuplicated: duplicated");
        _;
    }

    // Add a new lp to the pool. Can only be called by the owner.
    function add(
        uint256 _allocPoint,
        IBEP20 _lpToken,
        uint16 _depositFeeBP,
        bool _withUpdate
    ) external onlyOwner nonDuplicated(_lpToken) {
        // valid ERC20 token
        _lpToken.balanceOf(address(this));

        require(_depositFeeBP <= 250, "add: invalid deposit fee basis points");
        if (_withUpdate) {
            massUpdatePools();
        }
        uint256 lastRewardSecond = block.timestamp > startTime
            ? block.timestamp
            : startTime;
        totalAllocPoint = totalAllocPoint.add(_allocPoint);
        poolExistence[_lpToken] = true;
        poolInfo.push(
            PoolInfo({
                lpToken: _lpToken,
                allocPoint: _allocPoint,
                lastRewardSecond: lastRewardSecond,
                accLowcostPerShare: 0,
                depositFeeBP: _depositFeeBP,
                lpSupply: 0
            })
        );

        emit addPool(
            poolInfo.length - 1,
            address(_lpToken),
            _allocPoint,
            _depositFeeBP
        );
    }

    // Update the given pool's Lowcost allocation point and deposit fee. Can only be called by the owner.
    function set(
        uint256 _pid,
        uint256 _allocPoint,
        uint16 _depositFeeBP,
        bool _withUpdate
    ) external onlyOwner {
        require(_depositFeeBP <= 250, "set: invalid deposit fee basis points");
        if (_withUpdate) {
            massUpdatePools();
        }
        totalAllocPoint = totalAllocPoint.sub(poolInfo[_pid].allocPoint).add(
            _allocPoint
        );
        poolInfo[_pid].allocPoint = _allocPoint;
        poolInfo[_pid].depositFeeBP = _depositFeeBP;

        emit setPool(
            _pid,
            address(poolInfo[_pid].lpToken),
            _allocPoint,
            _depositFeeBP
        );
    }

    // Return reward multiplier over the given _from to _to block.
    function getMultiplier(uint256 _from, uint256 _to)
        public
        pure
        returns (uint256)
    {
        return _to.sub(_from);
    }

    // View function to see pending lowcost on frontend.
    function pendingLowcost(uint256 _pid, address _user)
        external
        view
        returns (uint256)
    {
        PoolInfo storage pool = poolInfo[_pid];
        UserInfo storage user = userInfo[_pid][_user];
        uint256 accLowcostPerShare = pool.accLowcostPerShare;
        if (
            block.timestamp > pool.lastRewardSecond &&
            pool.lpSupply != 0 &&
            totalAllocPoint > 0
        ) {
            uint256 multiplier = getMultiplier(
                pool.lastRewardSecond,
                block.timestamp
            );
            uint256 lowcostReward = multiplier
                .mul(LowcostPerSecond)
                .mul(pool.allocPoint)
                .div(totalAllocPoint);
                uint256 devReward = lowcostReward.div(10);
                uint256 totalRewards = lowcost.totalSupply().add(devReward).add(
                lowcostReward
            );
                if (totalRewards > lowcost.maxSupply()) {
                lowcostReward = lowcost.maxSupply().sub(lowcost.totalSupply());
            }
            accLowcostPerShare = accLowcostPerShare.add(
                lowcostReward.mul(1e18).div(pool.lpSupply)
            );
        }
        return user.amount.mul(accLowcostPerShare).div(1e18).sub(user.rewardDebt);
    }

    // Update reward variables for all pools. Be careful of gas spending!
    function massUpdatePools() public {
        uint256 length = poolInfo.length;
        for (uint256 pid = 0; pid < length; ++pid) {
            updatePool(pid);
        }
    }

    // Update reward variables of the given pool to be up-to-date.
    function updatePool(uint256 _pid) public {
        PoolInfo storage pool = poolInfo[_pid];
        if (block.timestamp <= pool.lastRewardSecond) {
            return;
        }
        if (pool.lpSupply == 0 || pool.allocPoint == 0) {
            pool.lastRewardSecond = block.timestamp;
            return;
        }
        uint256 multiplier = getMultiplier(
            pool.lastRewardSecond,
            block.timestamp
        );
        uint256 lowcostReward = multiplier
            .mul(LowcostPerSecond)
            .mul(pool.allocPoint)
            .div(totalAllocPoint);

        uint256 devReward = lowcostReward.div(10);
        uint256 totalRewards = lowcost.totalSupply().add(devReward).add(
            lowcostReward
        );

        if (totalRewards <= lowcost.maxSupply()) {
            // mint as normal as not at maxSupply
            lowcost.mint(devaddr, lowcostReward.div(10));
            lowcost.mint(address(this), lowcostReward);
        } else {
            // mint the difference only to MC, update lowcostReward
            lowcostReward = lowcost.maxSupply().sub(lowcost.totalSupply());
            lowcost.mint(address(this), lowcostReward);
        }

        if (lowcostReward != 0) {
            // only calculate and update if lowcostReward is non 0
            pool.accLowcostPerShare = pool.accLowcostPerShare.add(
                lowcostReward.mul(1e18).div(pool.lpSupply)
            );
        }

        pool.lastRewardSecond = block.timestamp;
    }

    // Deposit LP tokens to MasterChef for Lowcost allocation.
    function deposit(uint256 _pid, uint256 _amount) external nonReentrant {
        PoolInfo storage pool = poolInfo[_pid];
        UserInfo storage user = userInfo[_pid][msg.sender];
        updatePool(_pid);
        if (user.amount > 0) {
            uint256 pending = user
                .amount
                .mul(pool.accLowcostPerShare)
                .div(1e18)
                .sub(user.rewardDebt);
            if (pending > 0) {
                safeLowcostTransfer(msg.sender, pending);
            }
        }
        if (_amount > 0) {
            uint256 balanceBefore = pool.lpToken.balanceOf(address(this));
            pool.lpToken.safeTransferFrom(
                address(msg.sender),
                address(this),
                _amount
            );
            _amount = pool.lpToken.balanceOf(address(this)).sub(balanceBefore);
            if (pool.depositFeeBP > 0) {
                uint256 depositFee = _amount.mul(pool.depositFeeBP).div(10000);
                pool.lpToken.safeTransfer(feeAddress, depositFee);
                user.amount = user.amount.add(_amount).sub(depositFee);
                pool.lpSupply = pool.lpSupply.add(_amount).sub(depositFee);
            } else {
                user.amount = user.amount.add(_amount);
                pool.lpSupply = pool.lpSupply.add(_amount);
            }
        }
        user.rewardDebt = user.amount.mul(pool.accLowcostPerShare).div(1e18);
        emit Deposit(msg.sender, _pid, _amount);
    }

    // Withdraw LP tokens from MasterChef.
    function withdraw(uint256 _pid, uint256 _amount) external nonReentrant {
        PoolInfo storage pool = poolInfo[_pid];
        UserInfo storage user = userInfo[_pid][msg.sender];
        require(user.amount >= _amount, "withdraw: not good");
        updatePool(_pid);
        uint256 pending = user.amount.mul(pool.accLowcostPerShare).div(1e18).sub(
            user.rewardDebt
        );
        if (pending > 0) {
            safeLowcostTransfer(msg.sender, pending);
        }
        if (_amount > 0) {
            user.amount = user.amount.sub(_amount);
            pool.lpToken.safeTransfer(address(msg.sender), _amount);
            pool.lpSupply = pool.lpSupply.sub(_amount);
        }
        user.rewardDebt = user.amount.mul(pool.accLowcostPerShare).div(1e18);
        emit Withdraw(msg.sender, _pid, _amount);
    }

    // Withdraw without caring about rewards. EMERGENCY ONLY.
    function emergencyWithdraw(uint256 _pid) external nonReentrant {
        PoolInfo storage pool = poolInfo[_pid];
        UserInfo storage user = userInfo[_pid][msg.sender];
        uint256 amount = user.amount;
        user.amount = 0;
        user.rewardDebt = 0;
        pool.lpToken.safeTransfer(address(msg.sender), amount);

        if (pool.lpSupply >= amount) {
            pool.lpSupply = pool.lpSupply.sub(amount);
        } else {
            pool.lpSupply = 0;
        }

        emit EmergencyWithdraw(msg.sender, _pid, amount);
    }

    // Safe lowcost transfer function, just in case if rounding error causes pool to not have enough LOWCOSTs.
    function safeLowcostTransfer(address _to, uint256 _amount) internal {
        uint256 lowcostBal = lowcost.balanceOf(address(this));
        bool transferSuccess = false;
        if (_amount > lowcostBal) {
            transferSuccess = lowcost.transfer(_to, lowcostBal);
        } else {
            transferSuccess = lowcost.transfer(_to, _amount);
        }
        require(transferSuccess, "safeLowcostTransfer: transfer failed");
    }

    // Update dev address.
    function setDevAddress(address _devaddr) external {
        require(_devaddr != address(0), "!nonzero");
        require(msg.sender == devaddr, "dev: wut?");
        devaddr = _devaddr;
        emit SetDevAddress(msg.sender, _devaddr);
    }

    function setFeeAddress(address _feeAddress) external {
        require(msg.sender == feeAddress, "setFeeAddress: FORBIDDEN");
        require(_feeAddress != address(0), "!nonzero");
        feeAddress = _feeAddress;
        emit SetFeeAddress(msg.sender, _feeAddress);
    }

    //Pancake has to add hidden dummy pools inorder to alter the emission, here we make it simple and transparent to all.
    function updateEmissionRate(uint256 _LowcostPerSecond) external onlyOwner {
        require(_LowcostPerSecond <= MAX_EMISSION_RATE, "Emission too high");
        massUpdatePools();
        LowcostPerSecond = _LowcostPerSecond;
        emit UpdateEmissionRate(msg.sender, _LowcostPerSecond);
    }

    // Only update before start of farm
    function updateStartTime(uint256 _newStartTime) external onlyOwner {
        require(
            block.timestamp < startTime,
            "cannot change start time if farm has already started"
        );
        require(
            block.timestamp < _newStartTime,
            "cannot set start time in the past"
        );
        uint256 length = poolInfo.length;
        for (uint256 pid = 0; pid < length; ++pid) {
            PoolInfo storage pool = poolInfo[pid];
            pool.lastRewardSecond = _newStartTime;
        }
        startTime = _newStartTime;

        emit UpdateStartTime(startTime);
    }
}
