    "contracts/stakingTier.sol": {
      "content": "// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.4;

import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/security/Pausable.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/utils/Counters.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721.sol";

contract StakingTier is ReentrancyGuard, Ownable, Pausable {
    using Counters for Counters.Counter;

    mapping(address => bool) private admin;
    mapping(uint256 => Pool) public pools;
    mapping(uint256 => mapping(address => UserInfo)) userInfos;

    UserInfo[] internal totalUser;

    Counters.Counter public _poolIdCount;

    event Newbie(address indexed user, uint256 registerTime);
    event NewDeposit(address indexed user, uint256 poolId, uint256 amount, address tokenAddress);
    event UnStake(address indexed user, uint256 poolId, uint256 amount, address tokenAddress);

    struct Pool {
        uint256 poolId;
        uint256 startTime;
        uint256 endTime;
        uint256 totalDeposit;
        address tokenAddress;
        uint256 maxDeposit;
        uint256 duration;
        bool isPaused;
    }

    struct UserInfo {
        uint256 totalPayout;
        uint256 registerTime;
        uint256 lastStake;
        uint256 poolId;
        uint256 totalAmount;
        Deposit[] deposits;
    }

    struct Deposit {
        uint256 poolId;
        uint256 createdAt;
        uint256 start;
        uint256 amount;
        bool isUnstake;
    }

    constructor() public {
        _poolIdCount.increment();
    }

    function deposit(
        uint8 _poolId,
        uint256 _amount
    ) external nonReentrant whenNotPaused {
        require(_amount > 0, "Amount must be greater than 0");
        Pool memory pool = pools[_poolId];
        require(!pool.isPaused, "Pool is paused or not exists");
        require(pool.tokenAddress != address(0), "Invalid pool id");
        require(block.timestamp >= pool.startTime, "Can not participate");
        require(block.timestamp < pool.endTime, "Can not participate");
        require(
            pool.totalDeposit + _amount <= pool.maxDeposit,
            "Pool deposit full fill"
        );
        require(
            IERC20(pool.tokenAddress).allowance(_msgSender(), address(this)) >=
                _amount,
            "Token allowance too low"
        );
        _deposit(_poolId, _amount);
    }

    function _deposit(uint256 _poolId, uint256 _amount) internal {
        UserInfo storage user = userInfos[_poolId][_msgSender()];
        Pool storage pool = pools[_poolId];
        _safeTransferFrom(
            _msgSender(),
            address(this),
            _amount,
            pool.tokenAddress
        );

        user.lastStake = block.timestamp;
        user.totalAmount = user.totalAmount + _amount;
        pool.totalDeposit = pool.totalDeposit + _amount;
        user.deposits.push(
            Deposit(_poolId, block.timestamp, block.timestamp, _amount, false)
        );

        if (user.registerTime == 0) {
            user.registerTime = block.timestamp;
            totalUser.push(user);
            emit Newbie(_msgSender(), block.timestamp);
        }
        emit NewDeposit(_msgSender(), _poolId, _amount, pool.tokenAddress);
    }

    function unStake(uint256 _poolId) external nonReentrant {
        UserInfo storage user = userInfos[_poolId][_msgSender()];
        Pool storage pool = pools[_poolId];
        require(pool.tokenAddress != address(0), "Invalid pool id");
        require(pool.endTime <= block.timestamp, "Pool is not finished yet");

        uint256 totalCurrentDividend = getUserDividends(_msgSender(), _poolId);
        require(totalCurrentDividend > 0, "Reward must be more than 0");

        for (uint256 index = 0; index < user.deposits.length; index++) {
            if (!user.deposits[index].isUnstake) {
                user.deposits[index].isUnstake = true;
            }
        }

        IERC20(pool.tokenAddress).transfer(_msgSender(), totalCurrentDividend);
        emit UnStake(_msgSender(), _poolId, totalCurrentDividend, pool.tokenAddress);
    }

    function _safeTransferFrom(
        address _sender,
        address _recipient,
        uint256 _amount,
        address _token
    ) private {
        bool sent = IERC20(_token).transferFrom(_sender, _recipient, _amount);
        require(sent, "Token transfer failed");
    }

    function createPoolInfo(
        uint256 startTime,
        uint256 endTime,
        address tokenAddress,
        uint256 maxDeposit,
        uint256 duration
    ) external onlyWhitelister whenNotPaused {
        uint256 _poolId = _poolIdCount.current();
        Pool storage pool = pools[_poolId];

        require(tokenAddress != address(0), "Invalid address");
        require(startTime < endTime, "Invalid from time and to time");
        require(startTime > block.timestamp, "Invalid startTime");
        require(duration > 0, "Invalid duration");
        require(maxDeposit > 0, "Invalid maxDeposit");

        pool.poolId = _poolId;
        pool.startTime = startTime;
        pool.endTime = endTime;
        pool.totalDeposit = 0;
        pool.tokenAddress = tokenAddress;
        pool.maxDeposit = maxDeposit;
        pool.duration = duration;
        pool.isPaused = false;

        _poolIdCount.increment();
    }

    function updatePoolInfo(
        uint256 poolId,
        uint256 startTime,
        uint256 endTime,
        address tokenAddress,
        uint256 maxDeposit,
        uint256 duration
    ) external onlyWhitelister whenNotPaused {
        Pool storage pool = pools[poolId];
        require(pool.tokenAddress != address(0), "Invalid pool id");

        require(tokenAddress != address(0), "Invalid address");
        require(startTime < endTime, "Invalid from time and to time");
        require(duration > 0, "Invalid duration");
        require(maxDeposit > 0, "Invalid maxDeposit");
        require(pool.endTime > block.timestamp, "Pool ended");
        require(!pool.isPaused, "Pausable: paused");

        if (pool.startTime < block.timestamp) {
            pool.endTime = endTime;
            pool.tokenAddress = tokenAddress;
            pool.maxDeposit = maxDeposit;
        } else {
            pool.startTime = startTime;
            pool.endTime = endTime;
            pool.tokenAddress = tokenAddress;
            pool.maxDeposit = maxDeposit;
            pool.duration = duration;
            pool.isPaused = false;
        }
    }

    function updatePoolsStatus(
        uint256[] calldata _poolIds,
        bool isPaused
    ) external onlyWhitelister whenNotPaused {
        for (uint256 i = 0; i < _poolIds.length; i++) {
            pools[_poolIds[i]].isPaused = isPaused;
        }
    }

    function withdraw(
        address _rewardAddress,
        uint256 _amount,
        address to
    ) external onlyOwner {
        IERC20(_rewardAddress).transfer(to, _amount);
    }

    function setAdmin(
        address[] calldata users,
        bool remove
    ) external onlyOwner whenNotPaused {
