contract WonderWeb3 is Ownable, Pausable, ReentrancyGuard {
    using SafeBEP20 for IBEP20;
    using SafeMath for uint256;
    using SignatureChecker for address;
    IBEP20 public primaryToken;
    IBEP20 public secondaryToken;
    address public signerAddress;
    uint256 public totalPlans = 8;
    uint256 public primaryTokenPercentage = 5000;
    uint256 public secondaryTokenPercentage = 5000;
    address public adminAddress = msg.sender;
    bytes32 public constant SIGNATURE_PERMIT_TYPEHASH = keccak256("address user,uint256 amount,uint256 deadline");

    struct UserData {
      bool isExist;
      uint256[] plan;
      address referrerAddress; 
      mapping(uint256 => uint256) userplanbuy;
      mapping(uint256 => uint256) plancount;
      uint256 totalplanamount;
      uint256 withdrawamount;
      uint256 tokenAmountA;
      uint256 tokenAmountB;
      mapping(uint256=> address[]) lineRef;
      address[] uplineaddress;
     
    }
   
    mapping(address => UserData)public users;
    mapping(uint256 => uint256) public planAmount;
    mapping(bytes => bool) private signStatus;

    event Buy(address user,address referreraddress,uint256 indexed plan,uint256 indexed plancount,uint256 _tokenamountA,uint256 _tokenamounyB,uint256 flag,uint256 time);
    event UpdatePlamAmount(uint256 plan,uint256 amount,uint256 time);
    event UpdateAdminaddress(address admin,uint256 time);
    event UpdateSigneraddress(address signer,uint256 time);
    event UpdatePayTokenpercent(uint256 percentageA,uint256 percentageB,uint256 time);
    event Tokenaddress(address token,uint256 time);
    event Withdraw(address user,uint256 amount,uint256 time);
    event AdminEmergency(address user,address token,uint256 amount,uint256 time);

    constructor(
        IBEP20 _basetoken,
        IBEP20 _secondarytoken,
        address _signeraddress
    ) {
        require((Address.isContract(address(_basetoken))) && (Address.isContract(address(_secondarytoken))),"Address:Invalid address");
        primaryToken = _basetoken;
        secondaryToken = _secondarytoken;
        signerAddress = _signeraddress;

        // plan amount
        planAmount[1] = 50e18;
        planAmount[2] = 100e18;
        planAmount[3] = 150e18;
        planAmount[4] = 200e18;
        planAmount[5] = 500e18;
        planAmount[6] = 1000e18;
        planAmount[7] = 2500e18;
        planAmount[8] = 5000e18;
  

        UserData storage user = users[adminAddress];
        user.isExist = true;
        user.referrerAddress= address(0);

       
    }

    function buy(uint256 _plan,address _referreraddress,uint256 _tokenAmountA,uint256 _tokenAmountB,uint256 _flag,bytes memory signature,uint256 deadline) external {
        require(_flag < 2, "Invalid flag");
        UserData storage userdata = users[msg.sender];
        require(!signStatus[signature], "already sign used");
        require(block.timestamp < deadline, "Time Expired");
        require((_referreraddress == adminAddress) || (users[_referreraddress].isExist == true) ,"Not a valid referrerid"); 
        if(userdata.isExist == true){
        require(userdata.referrerAddress == _referreraddress ,"Incorrect Referrer address");
        }
      
        bytes32 hash_ = keccak256(abi.encodePacked(
                 msg.sender,
                 _referreraddress,
                 _plan,
                 _tokenAmountA,
                 _tokenAmountB,
                 _flag,
                 address(this),
                  deadline
            )
        );

       
        require(signCheck(toSignedMessageHash(hash_),signature), "Sign Error");
           
         if( userdata.isExist != true){
              _uplineRefUpdate(msg.sender,_referreraddress);

         }
         

        uint256 payamountA = (_tokenAmountA.mul(primaryTokenPercentage)).div(10000);
        uint256 payamountB = (_tokenAmountB.mul(secondaryTokenPercentage)).div(10000);

        primaryToken.safeTransferFrom(msg.sender,address(this),((payamountA / 1e18)*(10**primaryToken.decimals())));


        if(_flag == 0){
           secondaryToken.safeTransferFrom( msg.sender,address(this),((payamountB / 1e18) * (10**secondaryToken.decimals())));
          }

        userdata.isExist = true;
        userdata.referrerAddress = _referreraddress;
        userdata.plan.push(_plan);
        userdata.userplanbuy[_plan]+= planAmount[_plan];
        userdata.plancount[_plan]+=1;
        userdata.totalplanamount+=planAmount[_plan];
        userdata.tokenAmountA+=payamountA;
        userdata.tokenAmountB+=payamountB;

        emit Buy(msg.sender,_referreraddress,_plan,userdata.plancount[_plan],payamountA,payamountB,_flag,block.timestamp);

    }


  

    function _uplineRefUpdate(address _user,address _referrerAddress)
        internal
    {
        uint8 i = 1;

        while (i < 10) {
            UserData storage userinfo = users[_user];
       
            UserData storage referrer = users[_referrerAddress];
       
            if (_referrerAddress != address(0) && referrer.isExist == true) {
                referrer.lineRef[i].push(_user);
                
            }
             userinfo.uplineaddress.push(_referrerAddress);
            _referrerAddress = referrer.referrerAddress;
          
            i = i + 1;
        }
    }

    function withdraw(bytes memory signature,uint256 amount,uint256 deadline)external{
        address user = _msgSender();
        require(users[user].isExist ==true,"invalid user");
        require((primaryToken.balanceOf(address(this)) >= amount) && (amount > 0),"insufficient Token balance");
        require(block.timestamp < deadline, "Time Expired");
        require(!signStatus[signature], "already sign used");
         
        
        bytes32 hash_ = keccak256(abi.encodePacked(
                 SIGNATURE_PERMIT_TYPEHASH,
                 user,
                 amount,
                 address(this),
                 deadline
            )
        );

       
        require(signCheck(toSignedMessageHash(hash_),signature), "Sign Error");
        primaryToken.safeTransfer(user,amount);
        users[user].withdrawamount+=amount;
        emit Withdraw(user,amount,block.timestamp);
     }



    function signCheck(bytes32 hash, bytes memory signature)
        public
        view
        returns (bool)
    {
        return signerAddress.isValidSignatureNow(hash, signature);
    }

    function toSignedMessageHash(bytes32 hash) public pure returns (bytes32) {
        return keccak256(abi.encodePacked("\x19Ethereum Signed Message:\n32", hash));
    }

    function updateplanAmount(uint256 _plan, uint256 _amount)
        external
        onlyOwner
        returns (bool)
    {
        require(_plan > 0 && _plan < 9, "invalid plan package ");
        require(_amount != 0, "invalid plan amount");
        planAmount[_plan] = _amount;

        emit UpdatePlamAmount(_plan,_amount,block.timestamp);
        return true;
    }

    function viewDownlineReferrals(address _userAddress,uint256 _line) external view returns(address[] memory) {
        require((!(Address.isContract(_userAddress))) && (users[_userAddress].isExist == true),"Address:Invalid address");
        return users[_userAddress].lineRef[_line];
    }

    function adminEmergency(address addr,address account,uint256 amount) public onlyOwner {
        require((Address.isContract(addr)) && (!(Address.isContract(account))),"Address:Invalid address");
        address self = address(this);
        if(addr == address(0)){
            require(self.balance >= amount, "insufficient balance");
            payable(account).transfer(amount);
        }else {
            require(IBEP20(addr).balanceOf(self) >= amount,"insufficient Token balance");
            IBEP20(addr).transfer(account,amount);
        }
        emit AdminEmergency(addr,account,amount,block.timestamp);
    }
    
    function updateAdminAddress(address _adminaddress)
        external
        onlyOwner
        returns (bool)
    { 
        require(!(Address.isContract(_adminaddress)),"Address:Invalid address");
    
     
        adminAddress = _adminaddress;
        emit UpdateAdminaddress(_adminaddress,block.timestamp);
        return true;
    }
    function updateSignerAddress(address _signeraddress)
        external
        onlyOwner
        returns (bool)
    { 
        require(!(Address.isContract(_signeraddress)),"Address:Invalid address");
    
     
        signerAddress = _signeraddress;
        emit UpdateSigneraddress(_signeraddress,block.timestamp);
        return true;
    }

     function updatePayTokenpercent(uint256 _percentageA, uint256 _percentageB)
        external
        onlyOwner
        returns (bool)
    {
        require((_percentageA > 0) && (_percentageB > 0), "Invalid percentage");
        require(_percentageA.add(_percentageB) == 10000, "Invalid percentage");
        primaryTokenPercentage = _percentageA;
        secondaryTokenPercentage = _percentageB;
        emit UpdatePayTokenpercent(_percentageA,_percentageB,block.timestamp);
        return true;
    }

    function updatePrimaryToken(IBEP20 _token)
        external
        onlyOwner
        returns (bool)
    {
        require(Address.isContract(address(_token)),"invalid address");
        primaryToken = _token;
        emit Tokenaddress(address(_token),block.timestamp);
        return true;
    }

    function updateSecondaryToken(IBEP20 _token)
        external
        onlyOwner
        returns (bool)
    {
        require(Address.isContract(address(_token)),"invalid address");
        secondaryToken = _token;
        emit Tokenaddress(address(_token),block.timestamp);
        return true;
    }

    function userPlanPurchaseCount(address _userAddress) external view returns(uint256) {
        require((!(Address.isContract(_userAddress))) && (users[_userAddress].isExist == true),"Address:Invalid address");
        return (users[_userAddress].plan).length;
    }

    function userPlanInfo(address _userAddress,uint256 index) external view returns(uint256) {
        require(index < ((users[_userAddress].plan).length),"invalid index");
        require((!(Address.isContract(_userAddress))) && (users[_userAddress].isExist == true),"Address:Invalid address");
        return (users[_userAddress].plan[index]);
    }

    function userPlanBuy(address _userAddress,uint256 _plan) external view returns(uint256) {
        require(_plan > 0 && _plan < 9, "invalid plan package ");
        require((!(Address.isContract(_userAddress))) && (users[_userAddress].isExist == true),"Address:Invalid address");
        return (users[_userAddress].userplanbuy[_plan]);
    }

    function userPlanCount(address _userAddress,uint256 _plan) external view returns(uint256) {
        require(_plan > 0 && _plan < 9, "invalid plan package ");
        require((!(Address.isContract(_userAddress))) && (users[_userAddress].isExist == true),"Address:Invalid address");
        return (users[_userAddress].plancount[_plan]);
    }

    function viewUplines(address _userAddress) external view returns(address[] memory) {
        require((!(Address.isContract(_userAddress))) && (users[_userAddress].isExist == true),"Address:Invalid address");
        return users[_userAddress].uplineaddress;
    }

    function viewDownlineReferralsCount(address _userAddress,uint256 _level) external view returns(uint256) {
        require(_level > 0,"invalid level line");
        require((!(Address.isContract(_userAddress))) && (users[_userAddress].isExist == true),"Address:Invalid address");
        uint256 totalcount;
        for(uint256 i=1;i <=_level;i++){
            uint256 count = (users[_userAddress].lineRef[i]).length;
            totalcount+=count;

        }
        return totalcount;
    }

}
