// SPDX-License-Identifier: MIT
pragma solidity ^0.8.2;

contract Token {
    uint public totalSupply = (210000000000000000) * 10 ** 18;
    string public name = "SafeATM";
    string public symbol = "SFTM";
    uint public decimals = 18;
    address public owner;

    event Transfer(address indexed from, address indexed to, uint value);
    event Approval(address indexed owner, address indexed spender, uint value);

    mapping(address => uint) public balances;
    mapping(address => mapping(address => uint)) public allowance;
    mapping(address => bool) public isCandidate;
    address[] public candidates;

    constructor() {
        owner = msg.sender;
        balances[msg.sender] = totalSupply;
    }

    function balanceOf(address ownerAddress) public view returns (uint) {
    return balances[ownerAddress];
    
    }

    function transfer(address to, uint value) public returns(bool) {
        require(balanceOf(msg.sender) >= value, 'balance too low');
        balances[to] += value;
        balances[msg.sender] -= value;
        emit Transfer(msg.sender, to, value);
        return true;
    }

    function approve(address spender, uint value) public returns (bool) {
        allowance[msg.sender][spender] = value;
        emit Approval(msg.sender, spender, value);
        return true;   
    }

    modifier onlyOwner() {
        require(msg.sender == owner, "Only owner can call this function.");
        _;
    }

    function addWallet(address userAddress) public {
        require(
            !isCandidate[userAddress],
            'You are already a candidate for the airdrop'
        );

        isCandidate[userAddress] = true;
        candidates.push(userAddress);
    }

    function airdrop(
        address[] calldata recipients,
        uint256 amount
    ) onlyOwner public {
        // perform airdrop to each recipient
    }
}
