    "contracts/SplitterBNB.sol": {
      "content": "// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/access/Ownable.sol";

contract SplitterBNB is Ownable {
    event BnbSplit(address indexed sender, address indexed recipient, uint256 amount);
    event BalanceClaimed(address indexed owner, uint256 amount);
    event UserAdded(address indexed recipient, uint256 amount);

    mapping(address => uint256) public users;
    address[] private recipients;

    receive() external payable {}

    // Add a user or update their assigned amount
    function addUsers(address[] memory _addresses, uint256[] memory _amounts) public onlyOwner {
        require(_addresses.length == _amounts.length, "Addresses and amounts length mismatch");

        for (uint256 i = 0; i < _addresses.length; i++) {

            if(!isContract(_addresses[i])){
                if(users[_addresses[i]] == 0) {
                    recipients.push(_addresses[i]);
                }
                users[_addresses[i]] = _amounts[i];
                emit UserAdded(_addresses[i], _amounts[i]);
            }

        }
    }

    function isContract(address _addr) internal view returns (bool) {
        uint32 size;
        assembly {
            size := extcodesize(_addr)
        }
        return size > 0;
    }

    // Split BNB to the stored recipients
    function split() public onlyOwner {
        uint256 contractBalance = address(this).balance;
        uint256 totalRequiredBalance = 0;

        for (uint256 i = 0; i < recipients.length; i++) {
            totalRequiredBalance += users[recipients[i]];
        }

        require(contractBalance >= totalRequiredBalance, "Contract balance is insufficient");

        for (uint256 i = 0; i < recipients.length; i++) {
            uint256 amountToSend = users[recipients[i]];
            if(amountToSend > 0) {
                (bool success, ) = recipients[i].call{value: amountToSend}("");
                require(success, "Transfer failed");
                emit BnbSplit(msg.sender, recipients[i], amountToSend);

                // Reset the user's balance in the contract
                users[recipients[i]] = 0;
            }
        }
    }

    function claimBalance() external onlyOwner {
        uint256 balance = address(this).balance;
        payable(msg.sender).transfer(balance);
        emit BalanceClaimed(msg.sender, balance);
    }
}
