    ) {
        BUSDContract = IERC20(swap_from);
        MEMEContract = IERC20(swap_to);
        /**
         * Network: BSC
         * Aggregator: BNB/USD
         * Address: 0xC5A35FC58EFDC4B88DDCA51AcACd2E8F593504bE
         */
        /**
         * Network: BSC testnet
         * Aggregator: BNB/USD
         * Address: 0x1A26d803C2e796601794f8C5609549643832702C
         */
        dataFeed = AggregatorV3Interface(
            0xC5A35FC58EFDC4B88DDCA51AcACd2E8F593504bE
        );
    }

    receive() external payable {
        uint256 BNBValue = msg.value;
        uint256 BNBtoDollar = BNBValue * ((getLatestBNBPrice() / BNBdecimals) * ERC20decimals);
        require(
            BNBtoDollar >= limit,
            string.concat(
                "SWAP_TOKEN: amount must be more than",
                Strings.toString(limit)
            )
        );
        MEMEContract.transfer(msg.sender, calculate(BNBtoDollar));
    }

    function claimBNB(uint256 amount) public onlyOwner {
        uint256 bnbBalance = address(this).balance;
        require(bnbBalance > 0);
        require(bnbBalance <= amount);
        payable(owner()).transfer(amount);

    }

    /* ========== PRIVATE ========== */
    function calculate(uint256 amount) private view returns (uint256) {
        return amount / exchangeRate;
    }
    
    /* ========== VIEWS ========== */

    function getLatestBNBPrice() public view returns (uint256) {
        // prettier-ignore
        (
            /* uint80 roundID */,
            int answer,
            /*uint startedAt*/,
            /*uint timeStamp*/,
            /*uint80 answeredInRound*/
        ) = dataFeed.latestRoundData();

        return uint256(answer);
    }


    /* ========== MUTATIVE FUNCTIONS ========== */

    function swap(uint256 amount) public nonReentrant {
        require(
            amount >= limit,
            string.concat(
                "SWAP_TOKEN: amount must be more than",
                Strings.toString(limit)
            )
        );
        BUSDContract.transferFrom(msg.sender, address(this), amount);
        MEMEContract.transfer(msg.sender, (calculate(amount) * ERC20decimals));
    }

    /* ========== OWNED FUNCTIONS ========== */

    function setSwapLimit(uint256 amount) public onlyOwner {
        limit = amount;
    }

    function setExchangeRate(uint256 amount) public onlyOwner {
        exchangeRate = amount;
    }

   
    function claimBUSD(uint256 amount) public onlyOwner {
        uint256 busdBalance = BUSDContract.balanceOf(address(this));
        require(busdBalance > 0);
        require(busdBalance <= amount);
        BUSDContract.transfer(owner(), amount);
    }

    function withdrawPepeC(uint256 amount) public onlyOwner {
        MEMEContract.transfer(msg.sender, amount);
    }
}
