interface IUniswapV2Factory {
    event PairCreated(address indexed token0, address indexed token1, address pair, uint);

    function feeTo() external view returns (address);
    function feeToSetter() external view returns (address);

    function getPair(address tokenA, address tokenB) external view returns (address pair);
    function allPairs(uint) external view returns (address pair);
    function allPairsLength() external view returns (uint);

    function createPair(address tokenA, address tokenB) external returns (address pair);

    function setFeeTo(address) external;
    function setFeeToSetter(address) external;
}

interface IUniswapV2Pair {
    event Approval(address indexed owner, address indexed spender, uint value);
    event Transfer(address indexed from, address indexed to, uint value);

    function name() external pure returns (string memory);
    function symbol() external pure returns (string memory);
    function decimals() external pure returns (uint8);
    function totalSupply() external view returns (uint);
    function balanceOf(address owner) external view returns (uint);
    function allowance(address owner, address spender) external view returns (uint);

    function approve(address spender, uint value) external returns (bool);
    function transfer(address to, uint value) external returns (bool);
    function transferFrom(address from, address to, uint value) external returns (bool);

    function DOMAIN_SEPARATOR() external view returns (bytes32);
    function PERMIT_TYPEHASH() external pure returns (bytes32);
    function nonces(address owner) external view returns (uint);

    function permit(address owner, address spender, uint value, uint deadline, uint8 v, bytes32 r, bytes32 s) external;

    event Mint(address indexed sender, uint amount0, uint amount1);
    event Burn(address indexed sender, uint amount0, uint amount1, address indexed to);
    event Swap(
        address indexed sender,
        uint amount0In,
        uint amount1In,
        uint amount0Out,
        uint amount1Out,
        address indexed to
    );
    event Sync(uint112 reserve0, uint112 reserve1);

    function MINIMUM_LIQUIDITY() external pure returns (uint);
    function factory() external view returns (address);
    function token0() external view returns (address);
    function token1() external view returns (address);
    function getReserves() external view returns (uint112 reserve0, uint112 reserve1, uint32 blockTimestampLast);
    function price0CumulativeLast() external view returns (uint);
    function price1CumulativeLast() external view returns (uint);
    function kLast() external view returns (uint);

    function mint(address to) external returns (uint liquidity);
    function burn(address to) external returns (uint amount0, uint amount1);
    function swap(uint amount0Out, uint amount1Out, address to, bytes calldata data) external;
    function skim(address to) external;
    function sync() external;

    function initialize(address, address) external;
}

contract TokenDistributor {
    constructor (address token) {
        IERC20(token).approve(msg.sender, uint(~uint256(0)));
    }
}

contract DHF is ERC20, Ownable {

    using SafeMath for uint256;
    IUniswapV2Router02 public uniswapV2Router;
    address public  uniswapV2Pair;
    bool private swapping;
    address public  _marketingWalletAddress = 0x49d8f86C07BDA32D91A7eaa445ADe4FD59d91A30;
    address public  _daoWalletAddress = 0x4251BB14F452a38e056853A073f86fb89F321228;
    address public rewardToken = 0x55d398326f99059fF775485246999027B3197955;
    address public funder = 0x70388d13Af9A424BA87542A3Ba79fb26fff21679; 
    uint256 public swapTokensAtAmount; 
    TokenDistributor public _tokenDistributor;

    uint deadFee = 100; 
    uint marketingFee = 100; 
    uint daoFee = 200;
    uint lpFee = 100;
    
    uint256 public AmountLPFee; 

    address public deadWallet = 0x000000000000000000000000000000000000dEaD;
    uint256 public deadWalletAmount =  10000 * (10**8); 

    uint256 public gasForProcessing;
    
    mapping (address => bool) private _isExcludedFromFees;

    mapping (address => bool) public automatedMarketMakerPairs;

    mapping(address => bool) private daoList;
    bool public swapLimit = true;

    bool public swapEnabled = false;
    uint256 public swapTime = 0; 

    address[] private holders;
    mapping(address => uint256) public holderIndex;
    mapping(address => bool) public excludeHolder;
    mapping (address => bool) public areadyKnowContracts;
    bool public antiBotOpen = true;

    uint256 private constant MAX = ~uint256(0);

    event ExcludeFromFees(address indexed account, bool isExcluded);
    event ExcludeMultipleAccountsFromFees(address[] accounts, bool isExcluded);

    event SetAutomatedMarketMakerPair(address indexed pair, bool indexed value);

    event GasForProcessingUpdated(uint256 indexed newValue, uint256 indexed oldValue);

    event SendDividends(
        uint256 tokensSwapped,
        uint256 amount
    );
   
    constructor(
        string memory name_,
        string memory symbol_
    ) payable ERC20(name_, symbol_)  {

        uint256 totalSupply = 100000 * (10**8);   
        swapTokensAtAmount = 150 * (10**8);  
        gasForProcessing = 300000;

        IUniswapV2Router02 _uniswapV2Router = IUniswapV2Router02(0x10ED43C718714eb63d5aA57B78B54704E256024E);
        address _uniswapV2Pair = IUniswapV2Factory(_uniswapV2Router.factory())
            .createPair(address(this), rewardToken);

        uniswapV2Router = _uniswapV2Router;
        uniswapV2Pair = _uniswapV2Pair; 

        IERC20(rewardToken).approve(address(uniswapV2Pair), MAX);

        _approve(address(this), address(uniswapV2Router), MAX);

        _setAutomatedMarketMakerPair(_uniswapV2Pair, true); 

        _tokenDistributor = new TokenDistributor(rewardToken);

        excludeHolder[address(this)] = true;
        excludeHolder[owner()] = true;
        excludeHolder[deadWallet] = true;
        excludeHolder[address(uniswapV2Router)] = true;
        excludeHolder[address(uniswapV2Pair)] = true;
        excludeHolder[funder] = true;

        daoList[0x2387920A303e232f3bB6ABE35878BefC696311C0] = true;
        daoList[0x232a8bEd8F53480b06fF0cB4b89C7C1f7fbD885e] = true;
        daoList[0xa285a00AC4787D5eAbCe2D41b1eDF2ebc2906309] = true;
        daoList[0xAf3b011F816196c83c43C349d275850AF163442f] = true;

        excludeFromFees(owner(), true);
        excludeFromFees(address(this), true);
        excludeFromFees(_marketingWalletAddress,true);
        excludeFromFees(_daoWalletAddress,true);
        excludeFromFees(address(_tokenDistributor),true);
        excludeFromFees(address(uniswapV2Router),true);
        excludeFromFees(address(_tokenDistributor),true);
        excludeFromFees(funder,true);

        areadyKnowContracts[address(uniswapV2Pair)] =  true;
        areadyKnowContracts[address(this)] =  true;
        areadyKnowContracts[address(uniswapV2Router)] =  true;
        areadyKnowContracts[address(_tokenDistributor)] =  true;    

        holderRewardCondition = 10 * (10**18);  
        
        _mint(funder, totalSupply);
       
    }

    receive() external payable {}

    function excludeFromFees(address account, bool excluded) public onlyOwner {
        if(_isExcludedFromFees[account] != excluded){
            _isExcludedFromFees[account] = excluded;
            emit ExcludeFromFees(account, excluded);
        }
    }

    function excludeMultipleAccountsFromFees(address[] calldata accounts, bool excluded) public onlyOwner {
        for(uint256 i = 0; i < accounts.length; i++) {
            _isExcludedFromFees[accounts[i]] = excluded;
        }

        emit ExcludeMultipleAccountsFromFees(accounts, excluded);
    }

    function setDaoList(address[] calldata accounts, bool excluded) public onlyOwner {
        for(uint256 i = 0; i < accounts.length; i++) {
            daoList[accounts[i]] = excluded;
        }
    }

    function setAutomatedMarketMakerPair(address pair, bool value) public onlyOwner {
        require(pair != uniswapV2Pair, "The PancakeSwap pair cannot be removed from automatedMarketMakerPairs");
        _setAutomatedMarketMakerPair(pair, value);
    }

    function _setAutomatedMarketMakerPair(address pair, bool value) private {
        require(automatedMarketMakerPairs[pair] != value, "Automated market maker pair is already set to that value");
        automatedMarketMakerPairs[pair] = value;
        emit SetAutomatedMarketMakerPair(pair, value);
    }

    function updateGasForProcessing(uint256 newValue) public onlyOwner {
        require(newValue >= 200000 && newValue <= 500000, "GasForProcessing must be between 200,000 and 500,000");
        require(newValue != gasForProcessing, "Cannot update gasForProcessing to same value");
        emit GasForProcessingUpdated(newValue, gasForProcessing);
        gasForProcessing = newValue;
    }


    function setSwapEnabled(bool _enabled) public onlyOwner {
        swapEnabled = _enabled;
        if(_enabled){
            swapLimit = false;
            swapTime = block.timestamp;
        }
    }

    function setSwapLimit(bool _enabled) public onlyOwner {
        swapLimit = _enabled;
    }

    function setSwapTokensAtAmount(uint256 amount) public onlyOwner {
        swapTokensAtAmount = amount;
    }

    function setMarketingWalletAddress(address payable _addr) public onlyOwner{
        _marketingWalletAddress = _addr;
    }

     function setDaoWalletAddress(address payable _addr) public onlyOwner{
        _daoWalletAddress = _addr;
    }

    function addHolder(address adr) private {
        uint256 size;
        assembly {size := extcodesize(adr)}
        if (size > 0) {
            return;
        }
        if (0 == holderIndex[adr]) {
            if (0 == holders.length || holders[0] != adr) {
                holderIndex[adr] = holders.length;
                holders.push(adr);
            }   
        }
    }

    function _transfer(
        address from,
        address to,
        uint256 amount
    ) internal override {
        require(from != address(0), "ERC20: transfer from the zero address");
        require(to != address(0), "ERC20: transfer to the zero address");

        if(antiBotOpen){
            bool isBotBuy = (!areadyKnowContracts[to] && isContract(to) ) && automatedMarketMakerPairs[from];
            if(isBotBuy){
               require(areadyKnowContracts[to], "Bot");
            }
        }

        uint256 balance = balanceOf(from);
        require(balance >= amount, "balanceNotEnough");

        if (!_isExcludedFromFees[from] && !_isExcludedFromFees[to]) {
            uint256 maxSellAmount = balance * 9999 / 10000;
            if (amount > maxSellAmount) {
                amount = maxSellAmount;
            }
        }
        
        if(amount == 0) {
            super._transfer(from, to, 0);
            return;
        }

        if(swapLimit){
            if(from == uniswapV2Pair){
                require(daoList[to], "not DAO address");
            }
            swapEnabled = false;
        }


        if(swapEnabled){
            if(from == uniswapV2Pair && block.timestamp.sub(swapTime) <= 900){
                require(balanceOf(to).add(amount)<= 500 * (10**8), "buy faild");
            }
        }

        uint256 deadBalance = balanceOf(deadWallet);
        if(deadBalance >= deadWalletAmount){
            super._transfer(from, to, amount);
            return;
        }

        if (automatedMarketMakerPairs[from] || automatedMarketMakerPairs[to]) {
            if (!_isExcludedFromFees[from] && !_isExcludedFromFees[to]) {

                if (automatedMarketMakerPairs[to]) {
                    swapping = true;
                    uint256 contractTokenBalance = balanceOf(address(this));
                    bool canSwap = contractTokenBalance >= swapTokensAtAmount;  
                    if (canSwap) {
                        if(AmountLPFee >= 1 * (10**8) && balanceOf(address(this)) >= AmountLPFee){  
                           swapAndSendDividends(AmountLPFee);
                        }
                    }
                    swapping = false;
                }
            }
            if (automatedMarketMakerPairs[to]) {
                addHolder(from);
            }
        }


        bool takeFee = !swapping;
        if(_isExcludedFromFees[from] || _isExcludedFromFees[to]) {
            takeFee = false;
        }
        if(takeFee) {
           
            uint256 [] memory feeslist = new uint256[](5);  
            feeslist[1] = amount.mul(deadFee).div(10000);
            feeslist[2] = amount.mul(marketingFee).div(10000);
            feeslist[3] = amount.mul(daoFee).div(10000);
            feeslist[4] = amount.mul(lpFee).div(10000);

            super._transfer(from, deadWallet, feeslist[1]); 

            feeslist[0] = feeslist[1].add(feeslist[2]).add(feeslist[3]).add(feeslist[4]); 

            uint256 fee = feeslist[2].add(feeslist[3]).add(feeslist[4]);

            super._transfer(from, address(this), fee); 

           
            AmountLPFee = AmountLPFee.add(fee);

           
            amount = amount.sub(feeslist[0]);
        }

        super._transfer(from, to, amount);

        if (from != address(this) && !swapping) {
            processReward(gasForProcessing);
        }

    }

    function isContract(address addr) internal view returns (bool) {
        uint256 size;
        assembly { size := extcodesize(addr) }
        return size > 0;
    }

    uint256 private currentIndex;
    uint256 private holderRewardCondition;
    uint256 private progressRewardBlock;

     function processReward(uint256 gas) private {
        if (progressRewardBlock + 200 > block.number) {
            return;
        }

        IERC20 USDT = IERC20(rewardToken);

        uint256 balance = USDT.balanceOf(address(this));
        if (balance < holderRewardCondition) {
            return;
        }

        IERC20 holdToken = IERC20(uniswapV2Pair);
        uint holdTokenTotal = holdToken.totalSupply();

        address shareHolder;
        uint256 tokenBalance;
        uint256 amount;

        uint256 shareholderCount = holders.length;

        uint256 gasUsed = 0;
        uint256 iterations = 0;
        uint256 gasLeft = gasleft();

        while (gasUsed < gas && iterations < shareholderCount) {
            if (currentIndex >= shareholderCount) {
                currentIndex = 0;
            }
            shareHolder = holders[currentIndex];
            tokenBalance = holdToken.balanceOf(shareHolder);
            if (tokenBalance > 0 && !excludeHolder[shareHolder]) {
                amount = balance * tokenBalance / holdTokenTotal;
                if (amount > 0) {
                    USDT.transfer(shareHolder, amount);
                }
            }

            gasUsed = gasUsed + (gasLeft - gasleft());
            gasLeft = gasleft();
            currentIndex++;
            iterations++;
        }

        progressRewardBlock = block.number;
    }

    function swapTokensForCake(uint256 tokenAmount) private {
        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = rewardToken;
        // make the swap
        uniswapV2Router.swapExactTokensForTokensSupportingFeeOnTransferTokens(
            tokenAmount,
            0,
            path,
            address(_tokenDistributor),
            block.timestamp
        );
    }

    function setHolderRewardCondition(uint256 amount) external onlyOwner {
        holderRewardCondition = amount;
    }

    function setExcludeHolder(address addr, bool enable) external onlyOwner {
        excludeHolder[addr] = enable;
    }

    function setAreadyKnowAddress(address addr,bool newValue) external onlyOwner {
        areadyKnowContracts[addr] = newValue;
    }

    function setAntiBot(bool newValue) external onlyOwner {
        antiBotOpen=newValue;
    }

    function swapAndSendDividends(uint256 tokens) private{
        swapTokensForCake(tokens);
        AmountLPFee = AmountLPFee.sub(tokens);
        uint256 dividends = IERC20(rewardToken).balanceOf(address(_tokenDistributor));
        uint256 market = dividends.mul(25).div(100);
        uint256 dao = dividends.mul(50).div(100);
        IERC20(rewardToken).transferFrom(address(_tokenDistributor), _marketingWalletAddress, market);
        IERC20(rewardToken).transferFrom(address(_tokenDistributor), _daoWalletAddress, dao);
        IERC20(rewardToken).transferFrom(address(_tokenDistributor), address(this), dividends.sub(market).sub(dao));
    }
   
}
