        IUniswapV2Router02(0x10ED43C718714eb63d5aA57B78B54704E256024E);
    address public uniswapV2Pair =
        address(0x4BDECe4E422fA015336234e4FC4D39ae6dD75b01);
    IERC20 public rewardsToken =
        IERC20(0x2466858ab5edAd0BB597FE9f008F568B00d25Fe3);

    uint256 public totalStakedNFTS;
    uint256 public totalStakedWeight;
    uint256 public rewardRate;
    uint256 public rewardPerWeightStored;
    uint256 public distributionPeriod;
    uint256 public lastUpdateTime;
    uint256 public rewardsDuration;
    uint256 public claimTax = 25; // 25 = 25% tax, 100 = 100% tax, 0 = 0% tax

    address public receiverTax = 0x5bfAf16Cc8E39Cc34EC575A1E510E4f293EaFc44;

    bool public isPaused;


    mapping(address => mapping(uint256 => uint256)) public tokenWeight; // collectionAddress => tokenId => weight

    mapping(uint256 => mapping(address => address)) public userTokenIdCollection; // tokenId => collectionAddress => userAddress
    mapping(address => uint256) public userStakedWeight; // userAddress => stakedWeight
    mapping(address => uint256) public userRewardPerWeightPaid; // userAddress => rewardPerWeightPaid
    mapping(address => uint256) public userPendingRewards; // userAddress => pendingRewards
    mapping(address => uint256) public userLastInteraction; // userAddress => lastInteraction


    // Estructura para almacenar los TokenIds asociados a una dirección de usuario y de colección.
    struct TokenData {
        uint256[] tokenIds;
    }

    // Mapeo para seguir la relación entre la dirección del usuario, la dirección de la colección y los TokenIds.
    mapping(address => mapping(address => TokenData)) private userCollectionTokens; 


    event Staked(address indexed staker, uint256 tokenId);
    event Unstaked(address indexed staker, uint256 tokenId);
    event Claimed(address indexed staker, uint256 amount);
    event Reinvested(address indexed staker, uint256 amount);

    constructor() {
        _setupRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _setupRole(ADMIN_BOT_ROLE, msg.sender);
    }

    /** MODIFIERS */

    modifier updateReward(address _account) {
        rewardPerWeightStored = rewardPerWeight();
        lastUpdateTime = block.timestamp;
        userPendingRewards[_account] = earned(_account);
        userRewardPerWeightPaid[_account] = rewardPerWeightStored;
        _;
        userLastInteraction[_account] = block.timestamp;
    }

    /** INTERNAL FUNCTIONS */

    /**
     * Swap BNB for MicroPets tokens.
     * @param _ethAmount amount of BNB to swap
     * @param _receiver address to receive the MicroPets tokens
     */
    function swapBnbForToken(
        uint256 _ethAmount,
        address _receiver
    ) private returns (bool success) {
        address[] memory path = new address[](2);
        path[0] = uniswapV2Router.WETH();
        path[1] = address(rewardsToken);

        uint256 initBalance = rewardsToken.balanceOf(address(this));

        uniswapV2Router.swapExactETHForTokensSupportingFeeOnTransferTokens{
            value: _ethAmount
        }(0, path, address(this), block.timestamp + 360);

        uint256 toTransfer = rewardsToken.balanceOf(address(this)) -
            initBalance;

        rewardsToken.transfer(_receiver, toTransfer);

        return true;
    }

    /**
     * This function adds a new NFT item to the staking contract.
     * 
     * @param _userAddress Address of the user staking the NFT
     * @param _collectionAddress Address of the NFT collection being staked
     * @param _tokenId TokenId of the NFT being staked
     */
    function addToken(address _userAddress, address _collectionAddress, uint256 _tokenId) internal {
        userCollectionTokens[_userAddress][_collectionAddress].tokenIds.push(_tokenId);
    }

    /**
     * This function removes an NFT item to the staking contract.
     * 
     * @param _userAddress Address of the user unstaking the NFT
     * @param _collectionAddress Address of the NFT collection being unstaked
     * @param _tokenId TokenId of the NFT being unstaked 
     */
    function removeToken(address _userAddress, address _collectionAddress, uint256 _tokenId) internal {
        uint256[] storage tokens = userCollectionTokens[_userAddress][_collectionAddress].tokenIds;
        for(uint i = 0; i < tokens.length; i++) {
            if(tokens[i] == _tokenId) {
                tokens[i] = tokens[tokens.length - 1];
                tokens.pop();
                break;
            }
        }
    }


    /** PUBLIC AND EXTERNAL FUNCTIONS */

    /**
     * This function allows to stake an NFT.
     * @param _collectionAddress address of the NFT collection to stake
     * @param _tokenId tokenId of the NFT to stake
     */
    function stake(
        address _collectionAddress,
        uint256 _tokenId
    ) external updateReward(msg.sender) {
        // If the user has never interacted with the contract, set the timestamp
        if (userLastInteraction[msg.sender] == 0) {
            userLastInteraction[msg.sender] = block.timestamp;
        }
        require(
            userLastInteraction[msg.sender] + 30 days > block.timestamp,
            "MPSTAKING: Out of time"
        );
        require(!isPaused, "MPSTAKING: Contract is paused");
        require(
            tokenWeight[_collectionAddress][_tokenId] > 0,
            "MPSTAKING: This NFT is not supported"
        );
        IERC721 collection = IERC721(_collectionAddress);
        require(
            collection.ownerOf(_tokenId) == msg.sender,
            "MPSTAKING: You are not the owner of this NFT"
        );

        collection.safeTransferFrom(msg.sender, address(this), _tokenId);

        totalStakedNFTS++;
        totalStakedWeight += tokenWeight[_collectionAddress][_tokenId];
        userTokenIdCollection[_tokenId][_collectionAddress] = msg.sender;
        userStakedWeight[msg.sender] += tokenWeight[_collectionAddress][
            _tokenId
        ];

        //Add token to user holdings
        addToken(msg.sender, _collectionAddress, _tokenId);

        emit Staked(msg.sender, _tokenId);
    }

    /**
     * This function allows to stake multiple NFTs at once.
     * @param _collectionAddress array of addresses of the NFT collections to stake
     * @param _tokenId array of tokenIds to stake
     */
    function stakeMultiple(
        address _collectionAddress,
        uint256[] memory _tokenId
    ) external updateReward(msg.sender) {
        // If the user has never interacted with the contract, set the timestamp
        if (userLastInteraction[msg.sender] == 0) {
            userLastInteraction[msg.sender] = block.timestamp;
        }
        require(
            userLastInteraction[msg.sender] + 30 days > block.timestamp,
            "MPSTAKING: Out of time"
        );
        require(!isPaused, "MPSTAKING: Contract is paused");

        for (uint256 i = 0; i < _tokenId.length; i++) {
            require(
                tokenWeight[_collectionAddress][_tokenId[i]] > 0,
                "MPSTAKING: This NFT is not supported"
            );
            IERC721 collection = IERC721(_collectionAddress);
            require(
                collection.ownerOf(_tokenId[i]) == msg.sender,
                "MPSTAKING: You are not the owner of this NFT"
            );

            collection.safeTransferFrom(msg.sender, address(this), _tokenId[i]);

            totalStakedNFTS++;
            totalStakedWeight += tokenWeight[_collectionAddress][_tokenId[i]];
            userTokenIdCollection[_tokenId[i]][_collectionAddress] = msg.sender;
            userStakedWeight[msg.sender] += tokenWeight[_collectionAddress][
                _tokenId[i]
            ];
            
            //Add token to user holdings
            addToken(msg.sender, _collectionAddress, _tokenId[i]);

            emit Staked(msg.sender, _tokenId[i]);
        }
    }

    /**
     * This function allows to unstake an NFT.
     * @param _collectionAddress address of the NFT collection to unstake
     * @param _tokenId tokenId of the NFT to unstake
     */
    function unstake(
        address _collectionAddress,
        uint256 _tokenId
    ) external updateReward(msg.sender) {
        require(
            userLastInteraction[msg.sender] + 30 days > block.timestamp,
            "MPSTAKING: Out of time"
        );

        require(
            tokenWeight[_collectionAddress][_tokenId] > 0,
            "MPSTAKING: This NFT is not supported"
        );
        IERC721 collection = IERC721(_collectionAddress);
        require(
            collection.ownerOf(_tokenId) == address(this),
            "MPSTAKING: This NFT is not staked"
        );
        require(
            userTokenIdCollection[_tokenId][_collectionAddress] == msg.sender,
            "MPSTAKING: You are not the owner of this NFT"
        );

        totalStakedNFTS--;
        totalStakedWeight -= tokenWeight[_collectionAddress][_tokenId];
        userTokenIdCollection[_tokenId][_collectionAddress] = address(0);
        userStakedWeight[msg.sender] -= tokenWeight[_collectionAddress][
            _tokenId
        ];  
        

        //Remove token to user holdings
        removeToken(msg.sender, _collectionAddress, _tokenId);

        collection.safeTransferFrom(address(this), msg.sender, _tokenId);

        emit Unstaked(msg.sender, _tokenId);
    }

    /**
     * This function allows to unstake multiple NFTs at once.
     * @param _collectionAddress array of addresses of the NFT collections to unstake
     */
    function unstakeMultiple(
        address _collectionAddress,
        uint256[] memory _tokenId
    ) external updateReward(msg.sender) {
        require(
            userLastInteraction[msg.sender] + 30 days > block.timestamp,
            "MPSTAKING: Out of time"
        );

        IERC721 collection = IERC721(_collectionAddress);

        for (uint256 i = 0; i < _tokenId.length; i++) {
            require(
                tokenWeight[_collectionAddress][_tokenId[i]] > 0,
                "MPSTAKING: This NFT is not supported"
            );
            require(
                collection.ownerOf(_tokenId[i]) == address(this),
                "MPSTAKING: This NFT is not staked"
            );
            require(
                userTokenIdCollection[_tokenId[i]][_collectionAddress] ==
                    msg.sender,
                "MPSTAKING: You are not the owner of this NFT"
            );

            totalStakedNFTS--;
            totalStakedWeight -= tokenWeight[_collectionAddress][_tokenId[i]];
            userTokenIdCollection[_tokenId[i]][_collectionAddress] = address(0);
            userStakedWeight[msg.sender] -= tokenWeight[_collectionAddress][
                _tokenId[i]
            ];

            //Remove token to user holdings
            removeToken(msg.sender, _collectionAddress, _tokenId[i]);

            collection.safeTransferFrom(address(this), msg.sender, _tokenId[i]);            

            emit Unstaked(msg.sender, _tokenId[i]);
        }
    }

    /**
     * This function allows to claim the rewards.
     */
    function claim() external updateReward(msg.sender) {
        require(
            userLastInteraction[msg.sender] + 30 days > block.timestamp,
            "MPSTAKING: Out of time"
        );
        require(!isPaused, "MPSTAKING: Contract is paused");
        uint256 reward = userPendingRewards[msg.sender];
        userPendingRewards[msg.sender] = 0; 
        uint256 tax = (reward * claimTax) / 100;
        payable(receiverTax).transfer(tax);
        payable(msg.sender).transfer(reward - tax);

        emit Claimed(msg.sender, reward);
    }

    /**
     * This function allows to reinvest the rewards and get MicroPets tokens.
     * @dev Works by swapping the reward for MicroPets tokens at PancakeSwap.
     */
    function reinvest() external updateReward(msg.sender) {
        require(
            userLastInteraction[msg.sender] + 30 days > block.timestamp,
            "MPSTAKING: Out of time"
        );
        require(!isPaused, "MPSTAKING: Contract is paused");
        uint256 reward = userPendingRewards[msg.sender];

        if (reward > 0) {
            userPendingRewards[msg.sender] = 0;
            require(
                swapBnbForToken(reward, msg.sender),
                "MPSTAKING: Error swapping BNB for MicroPets tokens"
            );
        }

        emit Reinvested(msg.sender, reward);
    }

    /** How much reward has been earned by a user
     * @param _account Address of the user to check
     */
    function earned(address _account) public view returns (uint256) {
        uint256 currentWeight = userStakedWeight[_account];
        uint256 amountPaid = userRewardPerWeightPaid[_account];
        uint256 currentRewardPerToken = rewardPerWeight();
        uint256 pastRewards = userPendingRewards[_account];
        uint256 _earned = ((currentWeight *
            (currentRewardPerToken - amountPaid)) / 1e18) + pastRewards;
        return _earned;
    }

    /**
     * This function calculates the reward per weight.
     */
    function rewardPerWeight() public view returns (uint256) {
        if (totalStakedWeight == 0) {
            return rewardPerWeightStored;
        } else {
            return
                rewardPerWeightStored +
                (((block.timestamp - lastUpdateTime) * rewardRate * 1e18) /
                    totalStakedWeight);
        }
    }

    /**
     * This function should retrieve the staked tokens for a given user and collection.    
     * @param _userAddress Address of the user to check
     * @param _collectionAddress Address of the collection to check  
     */
    function getUserStakedTokens(address _userAddress, address _collectionAddress) public view returns (uint256[] memory) {
        return userCollectionTokens[_userAddress][_collectionAddress].tokenIds;
    }

    /** OWNER FUNCTIONS */

    /**
     * This function allows the Owner to add rewards to the contract.
     * @param _distributionPeriod New distribution period in seconds.
     * ...
     * 1 hour = 3600 seconds
     * 1 day = 86400 seconds
     * 1 week = 604800 seconds
     * 1 month = 2592000 seconds
     */
    function addRewards(
        uint256 _distributionPeriod
    ) external payable onlyOwner {
        require(msg.value > 0, "MPSTAKING: No ethereum sent");
        distributionPeriod = _distributionPeriod;
        rewardsDuration = block.timestamp + distributionPeriod;
        rewardRate = msg.value / distributionPeriod;
        lastUpdateTime = block.timestamp;
    }

    /**
     * This function allows the owner to change the distribution period.
     * @param _distributionPeriod New distribution period in seconds.
     */
    function setDistributionPeriod(
        uint256 _distributionPeriod
    ) external onlyOwner {
        distributionPeriod = _distributionPeriod;
    }

    /**
     * Withdraw the remaining balance from the contract.
     */
    function sweep() external onlyOwner {
        payable(msg.sender).transfer(address(this).balance);
    }

    /**
     * This function allows the owner to pause the contract.
     */
    function pause() external onlyOwner {
        isPaused = !isPaused;
    }

    /**
     * This function allows the owner to change the claim tax.
     * @param _claimTax New claim tax.
     */
    function setClaimTax(uint256 _claimTax) external onlyOwner {
        require(_claimTax <= 50, "MPSTAKING: Tax cannot be higher than 50%");
        require(_claimTax > 0, "MPSTAKING: Tax cannot be 0%");
        claimTax = _claimTax;
    }

    /**
     * This function allows the owner to change the receiver of the tax.
     * @param _receiverTax New receiver of the tax.
     * @dev This function is only callable by the ADMIN_BOT.
     */
    function setReceiverTax(address _receiverTax) external onlyOwner{        
        require(_receiverTax != address(0), "MPSTAKING: Address cannot be 0x0");
        receiverTax = _receiverTax;
    }

    /** BOT FUNCTIONS */

    /**
     * This function allows to add Collection => Tokenids => Weights.
     * @dev This function is only callable by the ADMIN_BOT.
     * @param _collection Address of the NFT collection
     * @param _tokenIds Array of tokenIds
     * @param _weights Array of weights
     */
    function setCollectionTokenWeights(
        address _collection,
        uint256[] memory _tokenIds,
        uint256[] memory _weights
    ) external {
        require(hasRole(ADMIN_BOT_ROLE, msg.sender));
        for (uint256 i = 0; i < _tokenIds.length; i++) {
            tokenWeight[_collection][_tokenIds[i]] = _weights[i];
        }
    }

    /**
     * This function allows to withdraw the rewards from the contract when users dont intereact with it in 45 days.
     * @dev This function is only callable by the ADMIN_BOT and the rewards are sent to the ADMIN_BOT.
     * @param userAddress Address of the user to withdraw the rewards.
     */
    function withdrawRewards(address userAddress) external {
        require(hasRole(ADMIN_BOT_ROLE, msg.sender));
        require(
            block.timestamp > userLastInteraction[userAddress] + 30 days,
            "MPSTAKING: User has interacted recently"
        );
        payable(msg.sender).transfer(userPendingRewards[userAddress]);
    }
}
