contract ADVERB is Context, IERC20 {
      string public name = "ADVERB";
    string public symbol = "ADVB";
    uint8 public decimals = 18;
    uint256 public totalTokenSupply = 125000000 * 10 ** uint256(decimals);
    uint256 public lockedTokenSupply = totalTokenSupply / 2;
    uint256 public unlockedTime;
     address public owner;
    mapping(address => uint256) public balances;
    mapping(address => mapping(address => uint256)) public allowances;

     modifier onlyOwner() {
        require(msg.sender == owner, "Only the owner can call this function");
        _;
    }


    constructor() {
        balances[msg.sender] = totalTokenSupply - lockedTokenSupply;
       
         unlockedTime = block.timestamp + (157680000);
        owner=msg.sender;
    }

    function totalSupply() external view override returns (uint256) {
        return totalTokenSupply;
    }

    function balanceOf(address account) external view override returns (uint256) {
        return balances[account];
    }

    function transfer(address recipient, uint256 amount) external override returns (bool) {
        require(amount <= balances[msg.sender], "Insufficient balance");
    

        balances[msg.sender] -= amount;
        balances[recipient] += amount;
        emit Transfer(msg.sender, recipient, amount);
        return true;
    }

    function allowance(address owner, address spender) external view override returns (uint256) {
        return allowances[owner][spender];
    }

    function approve(address spender, uint256 amount) external override returns (bool) {
        allowances[msg.sender][spender] = amount;
        emit Approval(msg.sender, spender, amount);
        return true;
    }

    function transferFrom(address sender, address recipient, uint256 amount) external override returns (bool) {
        require(amount <= balances[sender], "Insufficient balance");
        require(amount <= allowances[sender][msg.sender], "Insufficient allowance");
      

        balances[sender] -= amount;
        balances[recipient] += amount;
        allowances[sender][msg.sender] -= amount;
        emit Transfer(sender, recipient, amount);
        return true;
    }

    function mintLockedTokens() external onlyOwner{
        require(block.timestamp >= unlockedTime, "Tokens still locked");
        require(lockedTokenSupply>0,"Tokens already minted");
        
        
        balances[msg.sender] =balances[msg.sender]+lockedTokenSupply;
        lockedTokenSupply=0;
        emit Transfer(address(0), msg.sender, lockedTokenSupply);
    }


    function getLockedBalance(address account) public view returns (uint256) {
        if (block.timestamp < unlockedTime) {
            return lockedTokenSupply;
        } else {
            return 0;
        }
    }
    function updateOwner(address newOwner) external onlyOwner{
        owner=newOwner;
    }
     function withdrawDumpedTokens(IERC20 _token, address _receiver)
        external
        onlyOwner
    {
        _token.transfer(_receiver, _token.balanceOf(address(this)));
    }
   
}
