// File contracts/token/EXOToken.sol


pragma solidity 0.8.17;
contract EXOToken is
    Initializable,
    IEXOToken,
    ERC20Upgradeable,
    ERC20BurnableUpgradeable,
    PausableUpgradeable,
    AccessControlUpgradeable
{
    bytes32 public constant PAUSER_ROLE = keccak256("PAUSER_ROLE");
    bytes32 public constant MINTER_ROLE = keccak256("MINTER_ROLE");
    bytes32 public constant BRIDGE_ROLE = keccak256("BRIDGE_ROLE");

    uint256 internal constant DECIMAL = 1e18;
    uint256 public constant MAX_SUPPLY = 2.7e9 * 1e18;

    address public stakingReward;

    // Bridge contract address that can mint or burn
    address public bridge;
    address public plannetAddress;
    uint256 public startMdAirdropTIme;
    uint256 public founderStartTime;
    uint256 public startPrivateAirdropTime;
    enum Type {General, Founder, Metaverse, Private}
    struct Aidrop {
        uint256 lastAirdropTime;
        uint256 rate;
        uint256 endTime;
    }
    mapping (address => Aidrop) public airdrops;
    mapping(address => Type) public accountType; 
    mapping(address => bool) public isTradable;
    bool public isClosedTrading;
    mapping(address => uint256) public lockTimestamps;

    /**
     * @dev Modifier to check if the amount being minted does not exceed the total supply limit.
     * @param amount The amount being minted.
     */
    modifier ifExceedTotalSupply(uint256 amount) {
        require(
            totalSupply() + amount <= MAX_SUPPLY,
            "EXO: Exceeds TotalSupply"
        );
        _;
    }

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }
    /**
     * @dev Mints new tokens and assigns them to the specified address.
     * @param to The address to mint tokens to.
     * @param amount The amount of tokens to mint.
     */
    function mint(
        address to,
        uint256 amount
    ) external onlyRole(MINTER_ROLE) ifExceedTotalSupply(amount) {
        _mint(to, amount);
    }

    /**
     * @inheritdoc IEXOToken
     * @dev Mints new tokens through the bridge contract and assigns them to the specified address.
     * @param to The address to mint tokens to.
     * @param amount The amount of tokens to mint.
     */
    function bridgeMint(
        address to,
        uint256 amount
    ) external override onlyRole(BRIDGE_ROLE) ifExceedTotalSupply(amount) {
        _mint(to, amount);
    }

    /**
     * @inheritdoc IEXOToken
     * @dev Burns tokens owned by the specified address through the bridge contract.
     * @param owner The address that owns the tokens to be burned.
     * @param amount The amount of tokens to burn.
     */
    function bridgeBurn(
        address owner,
        uint256 amount
    ) external override onlyRole(BRIDGE_ROLE) {
        _burn(owner, amount);
    }

    /**
     * @inheritdoc IEXOToken
     * @dev Sets the bridge contract address that can mint or burn tokens.
     * @param _bridge The address of the bridge contract.
     */
    function setBridge(
        address _bridge
    ) external override onlyRole(DEFAULT_ADMIN_ROLE) {
        require(_bridge != address(0x0), "Can not be zero address");
        _revokeRole(BRIDGE_ROLE, bridge);
        bridge = _bridge;
        _grantRole(BRIDGE_ROLE, bridge);
    }

    /**
     * @inheritdoc IEXOToken
     * @dev Sets the staking reward contract address that can mint tokens.
     * @param _stakingReward The address of the staking reward contract.
     */
    function setStakingReward(
        address _stakingReward
    ) external override onlyRole(DEFAULT_ADMIN_ROLE) {
        require(_stakingReward != address(0x0), "Can not be zero address");
        _revokeRole(MINTER_ROLE, stakingReward);
        stakingReward = _stakingReward;
        _grantRole(MINTER_ROLE, stakingReward);
    }

    /**
     * @dev Initializes the EXOToken contract.
     */
    function initialize() public initializer {
        __ERC20_init("EXO Token", "EXO");
        __ERC20Burnable_init();
        __Pausable_init();
        __AccessControl_init();

        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(PAUSER_ROLE, msg.sender);
        _grantRole(MINTER_ROLE, msg.sender);
    }
    /**
     * @dev Pauses all transaction
     */
    function pause() external onlyRole(PAUSER_ROLE) {
        _pause();
    }
    /**
     *@dev Unpauses all transactions
    */
    function unpause() external onlyRole(PAUSER_ROLE) {
        _unpause();
    }
    function _beforeTokenTransfer(
        address from,
        address to,
        uint256 amount
    ) internal override whenNotPaused {
        if(isClosedTrading) {
            require(from == address(0) || isTradable[from] || isTradable[to], "Trading is not open yet");
        }
        // Allow transfers to stakingReward even if sender is locked
        if(from != stakingReward && to != stakingReward) {
            if (lockTimestamps[from] > block.timestamp) {
                uint256 remainingTimeInSeconds = lockTimestamps[from] - block.timestamp;
                uint256 remainingTimeInDays = remainingTimeInSeconds / 60 / 60 / 24;
                string memory str = Strings.toString(remainingTimeInDays);
                revert(string(abi.encodePacked("This address is locked for another ", str, " day(s)")));
            }
        }
        super._beforeTokenTransfer(from, to, amount);
    }

    /**
     * @dev Check Token Holder's Balance to see if Token Holer's tier status is downgraded or not
     * @param from Token Holder
     * @param to Target Token Holder
     * @param amount Transferred Amount
     * Need to check Total Balance for Token Holder. This will include both Staking amount and Remained amount in wallet
     * If Total Balance doesn't meet requirement for Tier, it downgrades until 0.
     */
    function _afterTokenTransfer(
        address from,
        address to,
        uint256 amount
    ) internal override whenNotPaused {
        super._afterTokenTransfer(from, to, amount);
        address user = from;
        uint256 totalStakedAmount = IStakingReward(stakingReward)
            .getTotalStakedAmount(user);
        uint8 tier = IStakingReward(stakingReward).getTier(user);
        // Check if should downgrade user's tier
        if (tier > 0) {
            uint88[5] memory minimumAmount = IStakingReward(stakingReward)
                .getTierMinAmount();
            uint256 balance = balanceOf(user);
            if (
                balance + totalStakedAmount <
                uint256(minimumAmount[tier]) * DECIMAL
            ) IStakingReward(stakingReward).setTier(user, 0);
        }
    }
    /**
     * @dev mint new tokens through the Planet NFT contract and assigns them to caller.
     * @param user The addressto mint tokens.
     * @param amount The amount of tokens to mint
     */
    function mintForAirdrop(uint256 amount, address user) public {
        require(msg.sender == plannetAddress, "not minter role");
        // airdropAmount+=amount;
        _mint(user, amount);
    }

    function unlocking() external {
        uint256 _amount = getAvaiableUnlockingAmount(msg.sender);
        airdrops[msg.sender].lastAirdropTime = block.timestamp;
        _mint(msg.sender, _amount);
    }

    function setNFTAddress(address _nftAddress) external onlyRole(DEFAULT_ADMIN_ROLE)  {
        require(_nftAddress != address(0x0), "Can not be zero address");
        plannetAddress = _nftAddress;
    }
    function setUnLockingType(address _address, Type  _type, uint256 _rate, uint256 _endTime) external onlyRole(DEFAULT_ADMIN_ROLE) {
        accountType[_address] = _type;
        airdrops[_address] = Aidrop(0,_rate, _endTime) ;
    }
    
    function startUnlocking(uint256 _startTime, Type _type) external onlyRole(DEFAULT_ADMIN_ROLE) {
        if(_type == Type.Founder) {
            founderStartTime = _startTime;
        } else if(_type == Type.Metaverse) {
            startMdAirdropTIme = _startTime;
        } else if (_type == Type.Private) {
            startPrivateAirdropTime = _startTime;
        }
    }

    function getAvaiableUnlockingAmount(address _addr) public view returns(uint256) {
        uint256 _lastAidropTime = airdrops[msg.sender].lastAirdropTime;
        uint256 _rate = airdrops[_addr].rate;
        uint256 _endTime = airdrops[_addr].endTime;
        uint256 _period = 0;
        uint256 currTimeStamp = block.timestamp > _endTime?_endTime:block.timestamp;
        if(accountType[_addr] == Type.Founder) {
            require(founderStartTime != 0, "not yet stated!");

            if(_lastAidropTime == 0) {
                _period = currTimeStamp - founderStartTime;
            } else {
                _period = currTimeStamp - _lastAidropTime;
            }
        } else if (accountType[_addr] == Type.Metaverse) {
            require(startMdAirdropTIme != 0, "not yet stated!");
            if(_lastAidropTime == 0) {
                _period = currTimeStamp - startMdAirdropTIme;
            } else {
                _period = currTimeStamp - _lastAidropTime;
            }
        } else if (accountType[_addr] == Type.Private) {
            require(startPrivateAirdropTime != 0, "not yet stated!");
            if(_lastAidropTime == 0) {
                _period = currTimeStamp - startPrivateAirdropTime;
            } else {
                _period = currTimeStamp - _lastAidropTime;
            }
        } else {
            revert("is not allowed to airdrop");
        }
        uint256 _amount = _period * _rate;
        return _amount;
    }

    function allowTrading(address _user) external onlyRole(DEFAULT_ADMIN_ROLE) {
        isTradable[_user] = true;
    }
    function closeTrading(bool _isTradable) external onlyRole(DEFAULT_ADMIN_ROLE) {
        isClosedTrading = _isTradable;
    }


    /**
     * @dev This function sets the lock timestamps for a batch of addresses.
     * @param addresses The array of addresses for which to set the lock timestamps.
     * @param timestamps The array of timestamps to set for each address. The length of this array must match the length of the addresses array.
     * Only callable by an account with the DEFAULT_ADMIN_ROLE.
     */
    function setTeamWalletLocks(address[] memory addresses, uint256[] memory timestamps) external onlyRole(DEFAULT_ADMIN_ROLE) {
        require(addresses.length == timestamps.length, "Addresses and timestamps array length must match");
        for (uint256 i = 0; i < addresses.length; i++) {
            lockTimestamps[addresses[i]] = timestamps[i];
        }
    }

    function getLockTimestamp(address _address) public view returns (uint256) {
        return lockTimestamps[_address];
    }


}
