    "contracts/QLqdr.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity 0.8.17;
pragma experimental ABIEncoderV2;

import "./libraries/MathUtil.sol";
import "./interfaces/IStakingProxy.sol";
import "./interfaces/IRewardStaking.sol";
import "./libraries/BoringMath.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/utils/math/Math.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/security/ReentrancyGuardUpgradeable.sol";
import "./QLqdrGateway.sol";

/*
LQDR Locking contract
*/
contract QLqdr is ReentrancyGuardUpgradeable, OwnableUpgradeable {
    using BoringMath for uint256;
    using BoringMath224 for uint224;
    using BoringMath112 for uint112;
    using BoringMath32 for uint32;
    using SafeERC20 for IERC20;

    /* ========== AXELAR VARIABLES ========== */
    QLqdrGateway public qLqdrGateway;
    uint256 public minValue;

    /* ========== STATE VARIABLES ========== */

    struct Reward {
        uint40 periodFinish;
        uint208 rewardRate;
        uint40 lastUpdateTime;
        uint208 rewardPerTokenStored;
    }
    struct Balances {
        uint112 locked;
        uint32 nextUnlockId;
    }
    struct LockedBalance {
        uint112 amount;
        uint32 unlockTime;
    }
    struct EarnedData {
        address token;
        uint256 amount;
    }
    struct Epoch {
        uint224 supply; //epoch supply
        uint32 date; //epoch start date
    }

    //token
    IERC20 public stakingToken;

    //rewards
    address[] public rewardTokens;
    mapping(address => Reward) public rewardData;

    // Duration that rewards are streamed over
    uint256 public rewardsDuration;

    // Duration of lock/earned penalty period
    uint256 public lockDuration;

    // reward token -> distributor -> is approved to add rewards
    mapping(address => mapping(address => bool)) public rewardDistributors;

    // user -> reward token -> amount
    mapping(address => mapping(address => uint256))
        public userRewardPerTokenPaid;
    mapping(address => mapping(address => uint256)) public rewards;

    //supplies and epochs
    uint256 public lockedSupply;
    Epoch[] public epochs;

    //mappings for balance data
    mapping(address => Balances) public balances;
    mapping(address => LockedBalance[]) public userLocks;

    uint256 public denominator;

    //management
    uint256 public kickRewardPerEpoch;
    uint256 public kickRewardEpochDelay;

    //shutdown
    bool public isShutdown;

    //erc20-like interface
    string private _name;
    string private _symbol;
    uint8 private _decimals;

    //instant unlock variables
    uint256 public penalty;
    bool public instantUnlockEnabled;

    /* ========== CONSTRUCTOR ========== */

    constructor() {}

    function initialize(
        IERC20 _stakingToken,
        uint256 _rewardsDuration,
        uint256 _minValue,
        uint256 _penalty,
        bool _instantUnlockEnabled
    ) public initializer {
        __Ownable_init();
        __ReentrancyGuard_init();

        denominator = 10000;
        kickRewardPerEpoch = 100;
        kickRewardEpochDelay = 4;

        require(_penalty > 0 && _penalty <= 10000, "Invalid penalty fee");
        penalty = _penalty;
        instantUnlockEnabled = _instantUnlockEnabled;

        minValue = _minValue;

        _name = "Vote Locked LQDR Token";
        _symbol = "QLQDR";
        _decimals = 18;
        stakingToken = _stakingToken;

        rewardsDuration = _rewardsDuration;
        lockDuration = rewardsDuration * 16;
        uint256 currentEpoch = block.timestamp.div(rewardsDuration).mul(
            rewardsDuration
        );
        epochs.push(Epoch({supply: 0, date: uint32(currentEpoch)}));

        isShutdown = false;
    }

    function decimals() public view returns (uint8) {
        return _decimals;
    }

    function name() public view returns (string memory) {
        return _name;
    }

    function symbol() public view returns (string memory) {
        return _symbol;
    }

    function version() public pure returns (uint256) {
        return 1;
    }

    /* ========== ADMIN CONFIGURATION ========== */
    function setQLqdrGateway(address payable _qLqdrGateway) public onlyOwner {
        qLqdrGateway = QLqdrGateway(_qLqdrGateway);
    }

    function setMinValue(uint256 _minValue) external onlyOwner {
        minValue = _minValue;
    }

    function setPenaltyFee(uint256 _penalty) external onlyOwner {
        require(_penalty > 0 && _penalty <= 10000, "Invalid penalty fee");
        penalty = _penalty;
    }

    function enableInstantUnlock(bool _enabled) external onlyOwner {
        require(instantUnlockEnabled != _enabled, "Already set");
        instantUnlockEnabled = _enabled;
    }

    // Add a new reward token to be distributed to stakers
    function addReward(
        address _rewardsToken,
        address _distributor
    ) public onlyOwner {
        require(rewardData[_rewardsToken].lastUpdateTime == 0);
        require(_rewardsToken != address(stakingToken));
        rewardTokens.push(_rewardsToken);
        rewardData[_rewardsToken].lastUpdateTime = uint40(block.timestamp);
        rewardData[_rewardsToken].periodFinish = uint40(block.timestamp);
        rewardDistributors[_rewardsToken][_distributor] = true;
    }

    // Modify approval for an address to call notifyRewardAmount
    function approveRewardDistributor(
        address _rewardsToken,
        address _distributor,
        bool _approved
    ) external onlyOwner {
        require(rewardData[_rewardsToken].lastUpdateTime > 0);
        rewardDistributors[_rewardsToken][_distributor] = _approved;
    }

    //set kick incentive
    function setKickIncentive(
        uint256 _rate,
        uint256 _delay
    ) external onlyOwner {
        require(_rate <= 500, "over max rate"); //max 5% per epoch
        require(_delay >= 2, "min delay"); //minimum 2 epochs of grace
        kickRewardPerEpoch = _rate;
        kickRewardEpochDelay = _delay;
    }

    //shutdown the contract. unstake all tokens. release all locks
    function shutdown() external onlyOwner {
        isShutdown = true;
    }

    /* ========== VIEWS ========== */

    function _rewardPerToken(
        address _rewardsToken
    ) internal view returns (uint256) {
        if (lockedSupply == 0) {
            return rewardData[_rewardsToken].rewardPerTokenStored;
        }
        return
            uint256(rewardData[_rewardsToken].rewardPerTokenStored).add(
                _lastTimeRewardApplicable(
                    rewardData[_rewardsToken].periodFinish
                )
                    .sub(rewardData[_rewardsToken].lastUpdateTime)
                    .mul(rewardData[_rewardsToken].rewardRate)
                    .mul(1e18)
                    .div(lockedSupply)
            );
    }

    function _earned(
        address _user,
        address _rewardsToken,
        uint256 _balance
    ) internal view returns (uint256) {
        return
            _balance
                .mul(
                    _rewardPerToken(_rewardsToken).sub(
                        userRewardPerTokenPaid[_user][_rewardsToken]
                    )
                )
                .div(1e18)
                .add(rewards[_user][_rewardsToken]);
    }

    function _lastTimeRewardApplicable(
        uint256 _finishTime
    ) internal view returns (uint256) {
        return Math.min(block.timestamp, _finishTime);
    }

    function lastTimeRewardApplicable(
        address _rewardsToken
    ) public view returns (uint256) {
        return
            _lastTimeRewardApplicable(rewardData[_rewardsToken].periodFinish);
    }

    function rewardPerToken(
        address _rewardsToken
    ) external view returns (uint256) {
        return _rewardPerToken(_rewardsToken);
    }

    function getRewardForDuration(
        address _rewardsToken
    ) external view returns (uint256) {
        return
            uint256(rewardData[_rewardsToken].rewardRate).mul(rewardsDuration);
    }

    // Address and claimable amount of all reward tokens for the given account
    function claimableRewards(
        address _account
    ) external view returns (EarnedData[] memory userRewards) {
        userRewards = new EarnedData[](rewardTokens.length);
        Balances storage userBalance = balances[_account];
        for (uint256 i = 0; i < userRewards.length; i++) {
            address token = rewardTokens[i];
            userRewards[i].token = token;
            userRewards[i].amount = _earned(
                _account,
                token,
                userBalance.locked
            );
        }
        return userRewards;
    }

    // total token balance of an account, including unlocked but not withdrawn tokens
    function lockedBalanceOf(
        address _user
    ) external view returns (uint256 amount) {
        return balances[_user].locked;
    }

    //balance of an account which only includes properly locked tokens as of the most recent eligible epoch
    function balanceOf(address _user) external view returns (uint256 amount) {
        LockedBalance[] storage locks = userLocks[_user];
        Balances storage userBalance = balances[_user];
        uint256 nextUnlockId = userBalance.nextUnlockId;

        //start with current locked amount
        amount = balances[_user].locked;

        uint256 locksLength = locks.length;
        //remove old records only (will be better gas-wise than adding up)
        for (uint i = nextUnlockId; i < locksLength; i++) {
            if (locks[i].unlockTime <= block.timestamp) {
                amount = amount.sub(locks[i].amount);
            } else {
                //stop now as no futher checks are needed
                break;
            }
        }

        //also remove amount locked in the next epoch
        uint256 currentEpoch = block.timestamp.div(rewardsDuration).mul(
            rewardsDuration
        );
        if (
            locksLength > 0 &&
            uint256(locks[locksLength - 1].unlockTime).sub(lockDuration) >
            currentEpoch
        ) {
            amount = amount.sub(locks[locksLength - 1].amount);
        }

        return amount;
    }

    //balance of an account which only includes properly locked tokens at the given epoch
    function balanceAtEpochOf(
        uint256 _epoch,
        address _user
    ) external view returns (uint256 amount) {
        LockedBalance[] storage locks = userLocks[_user];

        //get timestamp of given epoch index
        uint256 epochTime = epochs[_epoch].date;
        //get timestamp of first non-inclusive epoch
        uint256 cutoffEpoch = epochTime.sub(lockDuration);

        //need to add up since the range could be in the middle somewhere
        //traverse inversely to make more current queries more gas efficient
        for (uint i = locks.length - 1; i + 1 != 0; i--) {
            uint256 lockEpoch = uint256(locks[i].unlockTime).sub(lockDuration);
            //lock epoch must be less or equal to the epoch we're basing from.
            if (lockEpoch <= epochTime) {
                if (lockEpoch > cutoffEpoch) {
                    amount = amount.add(locks[i].amount);
                } else {
                    //stop now as no futher checks matter
                    break;
                }
            }
        }

        return amount;
    }

    //return currently locked but not active balance
    function pendingLockOf(
        address _user
    ) external view returns (uint256 amount) {
        LockedBalance[] storage locks = userLocks[_user];

        uint256 locksLength = locks.length;

        //return amount if latest lock is in the future
        uint256 currentEpoch = block.timestamp.div(rewardsDuration).mul(
            rewardsDuration
        );
        if (
            locksLength > 0 &&
            uint256(locks[locksLength - 1].unlockTime).sub(lockDuration) >
            currentEpoch
        ) {
            return locks[locksLength - 1].amount;
        }

        return 0;
    }

    function pendingLockAtEpochOf(
        uint256 _epoch,
        address _user
    ) external view returns (uint256 amount) {
        LockedBalance[] storage locks = userLocks[_user];

        //get next epoch from the given epoch index
        uint256 nextEpoch = uint256(epochs[_epoch].date).add(rewardsDuration);

        //traverse inversely to make more current queries more gas efficient
        for (uint i = locks.length - 1; i + 1 != 0; i--) {
            uint256 lockEpoch = uint256(locks[i].unlockTime).sub(lockDuration);

            //return the next epoch balance
            if (lockEpoch == nextEpoch) {
                return locks[i].amount;
            } else if (lockEpoch < nextEpoch) {
                //no need to check anymore
                break;
            }
        }

        return 0;
    }

    //supply of all properly locked balances at most recent eligible epoch
    function totalSupply() external view returns (uint256 supply) {
        uint256 currentEpoch = block.timestamp.div(rewardsDuration).mul(
            rewardsDuration
        );
        uint256 cutoffEpoch = currentEpoch.sub(lockDuration);
        uint256 epochindex = epochs.length;

        //do not include next epoch's supply
        if (uint256(epochs[epochindex - 1].date) > currentEpoch) {
            epochindex--;
        }

        //traverse inversely to make more current queries more gas efficient
        for (uint i = epochindex - 1; i + 1 != 0; i--) {
            Epoch storage e = epochs[i];
            if (uint256(e.date) <= cutoffEpoch) {
                break;
            }
            supply = supply.add(e.supply);
        }

        return supply;
    }

    //supply of all properly locked balances at the given epoch
    function totalSupplyAtEpoch(
        uint256 _epoch
    ) external view returns (uint256 supply) {
        uint256 epochStart = uint256(epochs[_epoch].date)
            .div(rewardsDuration)
            .mul(rewardsDuration);
        uint256 cutoffEpoch = epochStart.sub(lockDuration);

        //traverse inversely to make more current queries more gas efficient
        for (uint i = _epoch; i + 1 != 0; i--) {
            Epoch storage e = epochs[i];
            if (uint256(e.date) <= cutoffEpoch) {
                break;
            }
            supply = supply.add(epochs[i].supply);
        }

        return supply;
    }

    //find an epoch index based on timestamp
    function findEpochId(uint256 _time) external view returns (uint256 epoch) {
        uint256 max = epochs.length - 1;
        uint256 min = 0;

        //convert to start point
        _time = _time.div(rewardsDuration).mul(rewardsDuration);

        for (uint256 i = 0; i < 128; i++) {
            if (min >= max) break;

            uint256 mid = (min + max + 1) / 2;
            uint256 midEpochBlock = epochs[mid].date;
            if (midEpochBlock == _time) {
                //found
                return mid;
            } else if (midEpochBlock < _time) {
                min = mid;
            } else {
                max = mid - 1;
            }
        }
        return min;
    }

    // Information on a user's locked balances
    function lockedBalances(
        address _user
    )
        external
        view
        returns (
            uint256 total,
            uint256 unlockable,
            uint256 locked,
            LockedBalance[] memory lockData
        )
    {
        LockedBalance[] storage locks = userLocks[_user];
        Balances storage userBalance = balances[_user];
        uint256 nextUnlockId = userBalance.nextUnlockId;
        uint256 idx;
        for (uint i = nextUnlockId; i < locks.length; i++) {
            if (locks[i].unlockTime > block.timestamp) {
                if (idx == 0) {
                    lockData = new LockedBalance[](locks.length - i);
                }
                lockData[idx] = locks[i];
                idx++;
                locked = locked.add(locks[i].amount);
            } else {
                unlockable = unlockable.add(locks[i].amount);
            }
        }
        return (userBalance.locked, unlockable, locked, lockData);
    }

    //number of epochs
    function epochCount() external view returns (uint256) {
        return epochs.length;
    }

    /* ========== MUTATIVE FUNCTIONS ========== */

    function checkpointEpoch() external {
        _checkpointEpoch();
    }

    //insert a new epoch if needed. fill in any gaps
    function _checkpointEpoch() internal {
        //create new epoch in the future where new non-active locks will lock to
        uint256 nextEpoch = block
            .timestamp
            .div(rewardsDuration)
            .mul(rewardsDuration)
            .add(rewardsDuration);
        uint256 epochindex = epochs.length;

        //first epoch add in constructor, no need to check 0 length

        //check to add
        if (epochs[epochindex - 1].date < nextEpoch) {
            //fill any epoch gaps
            while (epochs[epochs.length - 1].date != nextEpoch) {
                uint256 nextEpochDate = uint256(epochs[epochs.length - 1].date)
                    .add(rewardsDuration);
                epochs.push(Epoch({supply: 0, date: uint32(nextEpochDate)}));
            }
        }
    }

    // Locked tokens cannot be withdrawn for lockDuration and are eligible to receive stakingReward rewards
    function lock(
        address _account,
        uint256 _amount
    ) external payable nonReentrant updateReward(_account) {
        //pull tokens
        stakingToken.safeTransferFrom(msg.sender, address(this), _amount);

        //lock
        _lock(_account, _amount, false);

        require(msg.value >= minValue, "Wrong value");
        qLqdrGateway.lockOnThisChain{value: msg.value}(_account, _amount);
    }

    //lock tokens
    function _lock(address _account, uint256 _amount, bool _isRelock) internal {
        require(_amount > 0, "Cannot stake 0");
        require(!isShutdown, "shutdown");

        Balances storage bal = balances[_account];

        //must try check pointing epoch first
        _checkpointEpoch();

        uint112 lockAmount = _amount.to112();

        //add user balances
        bal.locked = bal.locked.add(lockAmount);

        //add to total supplies
        lockedSupply = lockedSupply.add(lockAmount);

        //add user lock records or add to current
        uint256 lockEpoch = block.timestamp.div(rewardsDuration).mul(
            rewardsDuration
        );
        //if a fresh lock, add on an extra duration period
        if (!_isRelock) {
            lockEpoch = lockEpoch.add(rewardsDuration);
        }
        uint256 unlockTime = lockEpoch.add(lockDuration);
        uint256 idx = userLocks[_account].length;

        //if the latest user lock is smaller than this lock, always just add new entry to the end of the list
        if (idx == 0 || userLocks[_account][idx - 1].unlockTime < unlockTime) {
            userLocks[_account].push(
                LockedBalance({
                    amount: lockAmount,
                    unlockTime: uint32(unlockTime)
                })
            );
        } else {
            //else add to a current lock

            //if latest lock is further in the future, lower index
            //this can only happen if relocking an expired lock after creating a new lock
            if (userLocks[_account][idx - 1].unlockTime > unlockTime) {
                idx--;
            }

            //if idx points to the epoch when same unlock time, update
            //(this is always true with a normal lock but maybe not with relock)
            if (userLocks[_account][idx - 1].unlockTime == unlockTime) {
                LockedBalance storage userL = userLocks[_account][idx - 1];
                userL.amount = userL.amount.add(lockAmount);
            } else {
                //can only enter here if a relock is made after a lock and there's no lock entry
                //for the current epoch.
                //ex a list of locks such as "[...][older][current*][next]" but without a "current" lock
                //length - 1 is the next epoch
                //length - 2 is a past epoch
                //thus need to insert an entry for current epoch at the 2nd to last entry
                //we will copy and insert the tail entry(next) and then overwrite length-2 entry

                //reset idx
                idx = userLocks[_account].length;

                //get current last item
                LockedBalance storage userL = userLocks[_account][idx - 1];

                //add a copy to end of list
                userLocks[_account].push(
                    LockedBalance({
                        amount: userL.amount,
                        unlockTime: userL.unlockTime
                    })
                );

                //insert current epoch lock entry by overwriting the entry at length-2
                userL.amount = lockAmount;
                userL.unlockTime = uint32(unlockTime);
            }
        }

        //update epoch supply, epoch checkpointed above so safe to add to latest
        uint256 eId = epochs.length - 1;
        //if relock, epoch should be current and not next, thus need to decrease index to length-2
        if (_isRelock) {
            eId--;
        }
        Epoch storage e = epochs[eId];
        e.supply = e.supply.add(uint224(lockAmount));

        emit Staked(_account, lockEpoch, _amount, lockAmount);
    }

    // Withdraw all currently locked tokens where the unlock time has passed
    function _processExpiredLocks(
        address _account,
        bool _relock,
        address _withdrawTo,
        address _rewardAddress,
        uint256 _checkDelay,
        bool revertNoExp
    ) internal updateReward(_account) {
        LockedBalance[] storage locks = userLocks[_account];
        Balances storage userBalance = balances[_account];
        uint112 locked;
        uint256 length = locks.length;
        uint256 reward = 0;

        if (
            isShutdown ||
            locks[length - 1].unlockTime <= block.timestamp.sub(_checkDelay)
        ) {
            //if time is beyond last lock, can just bundle everything together
            locked = userBalance.locked;

            //dont delete, just set next index
            userBalance.nextUnlockId = length.to32();

            //check for kick reward
            //this wont have the exact reward rate that you would get if looped through
            //but this section is supposed to be for quick and easy low gas processing of all locks
            //we'll assume that if the reward was good enough someone would have processed at an earlier epoch
            if (_checkDelay > 0) {
                uint256 currentEpoch = block
                    .timestamp
                    .sub(_checkDelay)
                    .div(rewardsDuration)
                    .mul(rewardsDuration);
                uint256 epochsover = currentEpoch
                    .sub(uint256(locks[length - 1].unlockTime))
                    .div(rewardsDuration);
                uint256 rRate = MathUtil.min(
                    kickRewardPerEpoch.mul(epochsover + 1),
                    denominator
                );
                reward = uint256(locks[length - 1].amount).mul(rRate).div(
                    denominator
                );
            }
        } else {
            //use a processed index(nextUnlockId) to not loop as much
            //deleting does not change array length
            uint32 nextUnlockId = userBalance.nextUnlockId;
            for (uint i = nextUnlockId; i < length; i++) {
                //unlock time must be less or equal to time
                if (locks[i].unlockTime > block.timestamp.sub(_checkDelay))
                    break;

                //add to cumulative amounts
                locked = locked.add(locks[i].amount);

                //check for kick reward
                //each epoch over due increases reward
                if (_checkDelay > 0) {
                    uint256 currentEpoch = block
                        .timestamp
                        .sub(_checkDelay)
                        .div(rewardsDuration)
                        .mul(rewardsDuration);
                    uint256 epochsover = currentEpoch
                        .sub(uint256(locks[i].unlockTime))
                        .div(rewardsDuration);
                    uint256 rRate = MathUtil.min(
                        kickRewardPerEpoch.mul(epochsover + 1),
                        denominator
                    );
                    reward = reward.add(
                        uint256(locks[i].amount).mul(rRate).div(denominator)
                    );
                }
                //set next unlock index
                nextUnlockId++;
            }
            //update next unlock index
            userBalance.nextUnlockId = nextUnlockId;
        }

        if (revertNoExp) {
            require(locked > 0, "no exp locks");
        }
        if (locked == 0) {
            return;
        }

        //update user balances and total supplies
        userBalance.locked = userBalance.locked.sub(locked);
        lockedSupply = lockedSupply.sub(locked);

        emit Withdrawn(_account, locked, _relock);

        //send process incentive
        if (reward > 0) {
            //reduce return amount by the kick reward
            locked = locked.sub(reward.to112());

            //transfer reward
            transferStakingToken(_rewardAddress, reward);

            emit KickReward(_rewardAddress, _account, reward);
        }

        //relock or return to user
        if (_relock) {
            _lock(_withdrawTo, locked, true);
        } else {
            transferStakingToken(_withdrawTo, locked);
        }
    }

    // Withdraw all currently locked tokens where the unlock time has passed
    function _processInstantUnlock(
        address _account,
        address _withdrawTo
    ) internal updateReward(_account) {
        require(instantUnlockEnabled && penalty > 0, "Instant unlock disabled");

        LockedBalance[] storage locks = userLocks[_account];
        Balances storage userBalance = balances[_account];
        uint112 locked;
        uint256 length = locks.length;

        //if time is beyond last lock, can just bundle everything together
        locked = userBalance.locked;

        //dont delete, just set next index
        userBalance.nextUnlockId = length.to32();
        require(locked > 0, "no exp locks");

        //update user balances and total supplies
        userBalance.locked = userBalance.locked.sub(locked);
        lockedSupply = lockedSupply.sub(locked);

        emit Withdrawn(_account, locked, false);

        uint256 _toBurn = (locked * penalty) / denominator;
        uint256 _toSend = uint256(locked) - _toBurn;

        if (_toBurn > 0) {
            transferStakingToken(address(0xdead), _toBurn);
        }
        if (locked > 0) {
            transferStakingToken(_withdrawTo, _toSend);
        }
    }

    // withdraw expired locks to a different address
    function withdrawExpiredLocksTo(
        address _withdrawTo
    ) external payable nonReentrant {
        require(msg.value >= minValue, "Wrong values");
        _processExpiredLocks(
            msg.sender,
            false,
            _withdrawTo,
            msg.sender,
            0,
            true
        );

        qLqdrGateway.withdrawExpiredLocksToOnThisChain{value: msg.value}(
            msg.sender
        );
    }

    function instantWithdrawTo(
        address _withdrawTo
    ) external payable nonReentrant {
        require(msg.value >= minValue, "Wrong values");
        _processExpiredLocks(
            msg.sender,
            false,
            _withdrawTo,
            msg.sender,
            0,
            false
        );
        _processInstantUnlock(msg.sender, _withdrawTo);

        qLqdrGateway.instantWithdrawToOnThisChain{value: msg.value}(msg.sender);
    }

    // Withdraw/relock all currently locked tokens where the unlock time has passed
    function processExpiredLocks(bool _relock) external payable nonReentrant {
        _processExpiredLocks(
            msg.sender,
            _relock,
            msg.sender,
            msg.sender,
            0,
            true
        );

        require(msg.value >= minValue, "Wrong values");
        qLqdrGateway.processExpiredLocksOnThisChain{value: msg.value}(
            msg.sender,
            _relock
        );
    }

    function kickExpiredLocks(address _account) external payable nonReentrant {
        //allow kick after grace period of 'kickRewardEpochDelay'
        _processExpiredLocks(
            _account,
            false,
            _account,
            msg.sender,
            rewardsDuration.mul(kickRewardEpochDelay),
            true
        );

        require(msg.value == minValue, "Wrong values");
        qLqdrGateway.kickExpiredLocksOnThisChain{value: msg.value}(_account);
    }

    //transfer helper: pull enough from staking, transfer, updating staking ratio
    function transferStakingToken(address _account, uint256 _amount) internal {
        //transfer
        stakingToken.safeTransfer(_account, _amount);
    }

    // Claim all pending rewards
    function getReward(
        address _account
    ) public nonReentrant updateReward(_account) {
        for (uint i; i < rewardTokens.length; i++) {
            address _rewardsToken = rewardTokens[i];
            uint256 reward = rewards[_account][_rewardsToken];
            if (reward > 0) {
                rewards[_account][_rewardsToken] = 0;
                IERC20(_rewardsToken).safeTransfer(_account, reward);
                emit RewardPaid(_account, _rewardsToken, reward);
            }
        }
    }

    /* ========== RESTRICTED FUNCTIONS ========== */

    function _notifyReward(address _rewardsToken, uint256 _reward) internal {
        Reward storage rdata = rewardData[_rewardsToken];

        if (block.timestamp >= rdata.periodFinish) {
            rdata.rewardRate = _reward.div(rewardsDuration).to208();
        } else {
            uint256 remaining = uint256(rdata.periodFinish).sub(
                block.timestamp
            );
            uint256 leftover = remaining.mul(rdata.rewardRate);
            rdata.rewardRate = _reward
                .add(leftover)
                .div(rewardsDuration)
                .to208();
        }

        rdata.lastUpdateTime = block.timestamp.to40();
        rdata.periodFinish = block.timestamp.add(rewardsDuration).to40();
    }

    function notifyRewardAmount(
        address _rewardsToken,
        uint256 _reward
    ) external updateReward(address(0)) {
        require(rewardDistributors[_rewardsToken][msg.sender]);
        require(_reward > 0, "No reward");

        _notifyReward(_rewardsToken, _reward);

        // handle the transfer of reward tokens via `transferFrom` to reduce the number
        // of transactions required and ensure correctness of the _reward amount
        IERC20(_rewardsToken).safeTransferFrom(
            msg.sender,
            address(this),
            _reward
        );

        emit RewardAdded(_rewardsToken, _reward);
    }

    // Added to support recovering LP Rewards from other systems such as BAL to be distributed to holders
    function recoverERC20(
        address _tokenAddress,
        uint256 _tokenAmount
    ) external onlyOwner {
        require(
            _tokenAddress != address(stakingToken),
            "Cannot withdraw staking token"
        );
        require(
            rewardData[_tokenAddress].lastUpdateTime == 0,
            "Cannot withdraw reward token"
        );
        IERC20(_tokenAddress).safeTransfer(owner(), _tokenAmount);
        emit Recovered(_tokenAddress, _tokenAmount);
    }

    /* ========== MODIFIERS ========== */

    modifier updateReward(address _account) {
        {
            //stack too deep
            Balances storage userBalance = balances[_account];
            for (uint i = 0; i < rewardTokens.length; i++) {
                address token = rewardTokens[i];
                rewardData[token].rewardPerTokenStored = _rewardPerToken(token)
                    .to208();
                rewardData[token].lastUpdateTime = _lastTimeRewardApplicable(
                    rewardData[token].periodFinish
                ).to40();
                if (_account != address(0)) {
                    rewards[_account][token] = _earned(
                        _account,
                        token,
                        userBalance.locked
                    );
                    userRewardPerTokenPaid[_account][token] = rewardData[token]
                        .rewardPerTokenStored;
                }
            }
        }
        _;
    }

    /* ========== EVENTS ========== */
    event RewardAdded(address indexed _token, uint256 _reward);
    event Staked(
        address indexed _user,
        uint256 indexed _epoch,
        uint256 _paidAmount,
        uint256 _lockedAmount
    );
    event Withdrawn(address indexed _user, uint256 _amount, bool _relocked);
    event KickReward(
        address indexed _user,
        address indexed _kicked,
        uint256 _reward
    );
    event RewardPaid(
        address indexed _user,
        address indexed _rewardsToken,
        uint256 _reward
    );
    event Recovered(address _token, uint256 _amount);

    receive() external payable {}

    fallback() external {}
}
"
    },
    "contracts/QLqdrGateway.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity 0.8.17;
pragma experimental ABIEncoderV2;

import "./libraries/MathUtil.sol";
import "./interfaces/IStakingProxy.sol";
import "./interfaces/IRewardStaking.sol";
import "./libraries/BoringMath.sol";
import "./QLqdr.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/utils/math/Math.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/security/ReentrancyGuardUpgradeable.sol";
import {IAxelarGateway} from "@axelar-network/axelar-gmp-sdk-solidity/contracts/interfaces/IAxelarGateway.sol";
import {IAxelarExecutable} from "@axelar-network/axelar-gmp-sdk-solidity/contracts/interfaces/IAxelarExecutable.sol";
import {IAxelarGasService} from "@axelar-network/axelar-gmp-sdk-solidity/contracts/interfaces/IAxelarGasService.sol";
import "hardhat/console.sol";

/*
LQDR Locking cross chain gateway contract
*/
contract QLqdrGateway is ReentrancyGuardUpgradeable, OwnableUpgradeable {
    using BoringMath for uint256;
    using BoringMath224 for uint224;
    using BoringMath112 for uint112;
    using BoringMath32 for uint32;
    using SafeERC20 for IERC20;

    /* ========== AXELAR VARIABLES ========== */
    IAxelarGasService public gasService;
    IAxelarGateway public gateway;
    error InvalidAddress();
    error NotApprovedByGateway();
    event ActionAdded(
        uint256 actionId,
        uint256 functionId,
        address actor,
        uint256 amount,
        uint256 actionTime
    );
    event ActionExecuted(
        uint256 chainId,
        uint256 actionId,
        uint256 functionId,
        address actor,
        uint256 amount,
        uint256 actionTime
    );

    mapping(uint256 => uint256) public actionIds;

    /* ========== STATE VARIABLES ========== */

    struct Balances {
        uint112 locked;
        uint32 nextUnlockId;
    }
    struct LockedBalance {
        uint112 amount;
        uint32 unlockTime;
    }
    struct Epoch {
        uint224 supply; //epoch supply
        uint32 date; //epoch start date
    }
    struct Action {
        uint256 actionId;
        uint256 functionId; // 0 - Lock, 1 - processExpiredLocks, 2 - Withdraw, 3 - Kick, 4 - InstantWithdraw
        address actor;
        uint256 amount;
        uint256 actionTime;
    }

    // Duration that rewards are streamed over
    uint256 public rewardsDuration;

    // Duration of lock/earned penalty period
    uint256 public lockDuration;

    //supplies and epochs
    // uint256[] public lockedSupply;
    mapping(uint256 => uint256) public lockedSupply;
    // Epoch[] public epochs;
    mapping(uint256 => Epoch[]) public epochs;

    //mappings for balance data
    mapping(address => mapping(uint256 => Balances)) public balances;
    mapping(address => mapping(uint256 => LockedBalance[])) public userLocks;

    uint256 public denominator;

    //management
    uint256 public kickRewardPerEpoch;
    uint256 public kickRewardEpochDelay;

    mapping(string => bool) private chainWhitelisted;
    mapping(string => bool) private sourceWhitelisted;
    mapping(string => uint256) private chainIds;
    mapping(uint256 => string) private chains;
    mapping(uint256 => string) private gateways;

    mapping(uint256 => Action[]) public actions;
    uint256 public currentActionId;

    uint256 public chainsLength;

    //shutdown
    bool public isShutdown;

    QLqdr public qLqdr;

    // instant unlock penalty
    uint256 public penalty;

    /* ========== CONSTRUCTOR ========== */

    constructor() {}

    function initialize(
        address payable _qLqdr,
        uint256 _rewardsDuration,
        address gateway_,
        address gasReceiver_,
        uint256 _penalty
    ) public initializer {
        if (gateway_ == address(0)) revert InvalidAddress();
        gateway = IAxelarGateway(gateway_);
        if (gasReceiver_ == address(0)) revert InvalidAddress();
        gasService = IAxelarGasService(gasReceiver_);

        require(_qLqdr != address(0), "Zero address");
        qLqdr = QLqdr(_qLqdr);

        __Ownable_init();
        __ReentrancyGuard_init();

        denominator = 10000;
        kickRewardPerEpoch = 100;
        kickRewardEpochDelay = 4;

        rewardsDuration = _rewardsDuration;
        lockDuration = rewardsDuration * 16;

        isShutdown = false;

        penalty = _penalty;
    }

    /* ========== AXELAR GATEWAY CONFIGURATION ========== */
    modifier onlySelf() {
        require(
            msg.sender == address(this),
            "Function must be called by the same contract only"
        );
        _;
    }

    modifier onlyQLqdr() {
        require(
            msg.sender == address(qLqdr),
            "Function must be called by the QLqdr contract only"
        );
        _;
    }

    function execute(
        bytes32 commandId,
        string calldata sourceChain,
        string calldata sourceAddress,
        bytes calldata payload
    ) external {
        bytes32 payloadHash = keccak256(payload);

        // if (
        //     !gateway.validateContractCall(
        //         commandId,
        //         sourceChain,
        //         sourceAddress,
        //         payloadHash
        //     )
        // ) revert NotApprovedByGateway();

        _execute(sourceChain, sourceAddress, payload);
    }

    function _execute(
        string calldata sourceChain,
        string calldata sourceAddress,
        bytes calldata payload
    ) internal {
        Action[] memory _actions = abi.decode(payload, (Action[]));

        require(chainWhitelisted[sourceChain], "Chain not whitelisted");
        require(sourceWhitelisted[sourceAddress], "Source not whitelisted");
        uint256 _chainId = chainIds[sourceChain];

        for (uint256 i = 0; i < _actions.length; i++) {
            bool success;
            bytes memory result;
            bytes4 commandSelector;
            Action memory _action = _actions[i];
            require(
                _action.actionId == actionIds[_chainId],
                "Invalid action index"
            );
            if (_action.functionId == 0) {
                commandSelector = QLqdrGateway.lock.selector;
                (success, result) = address(this).call(
                    abi.encodeWithSelector(
                        commandSelector,
                        _action.actor,
                        _action.amount,
                        _action.actionTime,
                        _chainId
                    )
                );
            } else if (_action.functionId == 1) {
                commandSelector = QLqdrGateway.withdrawExpiredLocksTo.selector;
                (success, result) = address(this).call(
                    abi.encodeWithSelector(
                        commandSelector,
                        _action.actor,
                        _action.actionTime,
                        _chainId
                    )
                );
            } else if (_action.functionId == 2) {
                commandSelector = QLqdrGateway.processExpiredLocks.selector;
                (success, result) = address(this).call(
                    abi.encodeWithSelector(
                        commandSelector,
                        _action.actor,
                        _action.amount == 1,
                        _action.actionTime,
                        _chainId
                    )
                );
            } else if (_action.functionId == 3) {
                commandSelector = QLqdrGateway.kickExpiredLocks.selector;
                (success, result) = address(this).call(
                    abi.encodeWithSelector(
                        commandSelector,
                        _action.actor,
                        _action.actionTime,
                        _chainId
                    )
                );
            } else if (_action.functionId == 4) {
                commandSelector = QLqdrGateway.instantWithdrawTo.selector;
                (success, result) = address(this).call(
                    abi.encodeWithSelector(
                        commandSelector,
                        _action.actor,
                        _action.actionTime,
                        _chainId
                    )
                );
            } else {
                revert("Invalid function name");
            }

            // shouln't revert in production or tokens could be stuck
            if (!success) {
                if (result.length == 0) {
                    require(success, "Failed with no reason");
                } else {
                    // rethrow same error
                    assembly {
                        let start := add(result, 0x20)
                        let end := add(result, mload(result))
                        revert(start, end)
                    }
                }
            }

            emit ActionExecuted(
                _chainId,
                _action.actionId,
                _action.functionId,
                _action.actor,
                _action.amount,
                _action.actionTime
            );
        }

        actionIds[_chainId] += 1;
    }

    function executeActions(uint256[] calldata _values) external onlyOwner {
        Action[] memory _action = actions[currentActionId];

        require(_values.length == chainsLength, "Values length not correct");
        bytes memory payload = abi.encode(_action);

        uint256 _totalValue;
        uint256 i;
        for (i = 0; i < chainsLength; i++) {
            _totalValue += _values[i];
        }
        require(
            _totalValue <= payable(address(this)).balance,
            "Not enough gas fee"
        );

        for (i = 0; i < chainsLength; i++) {
            gasService.payNativeGasForContractCall{value: _values[i]}(
                address(this),
                chains[i],
                gateways[i],
                payload,
                msg.sender
            );
            gateway.callContract(chains[i], gateways[i], payload);
        }

        currentActionId++;
    }

    function _addAction(
        uint256 _functionId,
        address _actor,
        uint256 _amount
    ) internal {
        Action[] storage _action = actions[currentActionId];
        uint256 _actionTime = block.timestamp;
        _action.push(
            Action({
                actionId: currentActionId,
                functionId: _functionId,
                actor: _actor,
                amount: _amount,
                actionTime: _actionTime
            })
        );

        emit ActionAdded(
            currentActionId,
            _functionId,
            _actor,
            _amount,
            _actionTime
        );
    }

    function lockOnThisChain(
        address _account,
        uint256 _amount
    ) external payable nonReentrant onlyQLqdr {
        _addAction(0, _account, _amount);
    }

    function getActions(
        uint256 _index
    ) external view returns (Action[] memory) {
        return actions[_index];
    }

    function withdrawExpiredLocksToOnThisChain(
        address _account
    ) external payable nonReentrant onlyQLqdr {
        _addAction(1, _account, 0);
    }

    function processExpiredLocksOnThisChain(
        address _account,
        bool _relock
    ) external payable nonReentrant onlyQLqdr {
        _addAction(2, _account, _relock ? 1 : 0);
    }

    function kickExpiredLocksOnThisChain(
        address _account
    ) external payable nonReentrant onlyQLqdr {
        _addAction(3, _account, 0);
    }

    function instantWithdrawToOnThisChain(
        address _account
    ) external payable nonReentrant onlyQLqdr {
        _addAction(4, _account, 0);
    }

    /* ========== ADMIN CONFIGURATION ========== */

    //set kick incentive
    function setKickIncentive(
        uint256 _rate,
        uint256 _delay
    ) external onlyOwner {
        require(_rate <= 500, "over max rate"); //max 5% per epoch
        require(_delay >= 2, "min delay"); //minimum 2 epochs of grace
        kickRewardPerEpoch = _rate;
        kickRewardEpochDelay = _delay;
    }

    function setPenalty(uint256 _penalty) external onlyOwner {
        penalty = _penalty;
    }

    function addChain(
        string memory _chainName,
        string memory _sourceAddress,
        uint256 _timestamp
    ) external onlyOwner {
        chainIds[_chainName] = chainsLength;
        chainWhitelisted[_chainName] = true;
        chains[chainsLength] = _chainName;
        uint256 currentEpoch = _timestamp.div(rewardsDuration).mul(
            rewardsDuration
        );
        epochs[chainsLength].push(
            Epoch({supply: 0, date: uint32(currentEpoch)})
        );
        sourceWhitelisted[_sourceAddress] = true;
        gateways[chainsLength] = _sourceAddress;
        chainsLength++;
    }

    function updateSourceAddress(
        string memory _chainName,
        string memory _sourceAddress
    ) external onlyOwner {
        uint256 _chainId = chainIds[_chainName];
        sourceWhitelisted[_sourceAddress] = true;
        gateways[_chainId] = _sourceAddress;
    }

    //shutdown the contract. unstake all tokens. release all locks
    function shutdown() external onlyOwner {
        isShutdown = true;
    }

    /* ========== VIEWS ========== */

    function getSourceWhitelisted(
        string calldata sourceAddress
    ) external view returns (bool) {
        return sourceWhitelisted[sourceAddress];
    }

    function getChainWhitelisted(
        string calldata chainAddress
    ) external view returns (bool) {
        return chainWhitelisted[chainAddress];
    }

    // total token balance of an account, including unlocked but not withdrawn tokens
    function lockedBalanceOf(
        address _user
    ) external view returns (uint256 amount) {
        uint256 i;
        if (address(qLqdr) != address(0)) {
            amount = qLqdr.lockedBalanceOf(_user);
        }
        for (i = 0; i < chainsLength; i++) {
            amount += balances[_user][i].locked;
        }

        return amount;
    }

    //balance of an account which only includes properly locked tokens as of the most recent eligible epoch
    function balanceOf(address _user) external view returns (uint256 amount) {
        if (address(qLqdr) != address(0)) {
            amount = qLqdr.balanceOf(_user);
        }

        for (uint j = 0; j < chainsLength; j++) {
            LockedBalance[] storage locks = userLocks[_user][j];
            Balances storage userBalance = balances[_user][j];
            uint256 nextUnlockId = userBalance.nextUnlockId;

            //start with current locked amount
            amount += balances[_user][j].locked;

            uint256 locksLength = locks.length;
            //remove old records only (will be better gas-wise than adding up)
            for (uint i = nextUnlockId; i < locksLength; i++) {
                if (locks[i].unlockTime <= block.timestamp) {
                    amount = amount.sub(locks[i].amount);
                } else {
                    //stop now as no futher checks are needed
                    break;
                }
            }

            //also remove amount locked in the next epoch
            uint256 currentEpoch = block.timestamp.div(rewardsDuration).mul(
                rewardsDuration
            );
            if (
                locksLength > 0 &&
                uint256(locks[locksLength - 1].unlockTime).sub(lockDuration) >
                currentEpoch
            ) {
                amount = amount.sub(locks[locksLength - 1].amount);
            }
        }

        return amount;
    }

    //balance of an account which only includes properly locked tokens at the given epoch
    function balanceAtEpochOf(
        uint256 _epoch,
        address _user
    ) external view returns (uint256 amount) {
        if (address(qLqdr) != address(0)) {
            amount = qLqdr.balanceAtEpochOf(_epoch, _user);
        }

        for (uint j = 0; j < chainsLength; j++) {
            LockedBalance[] storage locks = userLocks[_user][j];

            //get timestamp of given epoch index
            uint256 epochTime = epochs[j][_epoch].date;
            //get timestamp of first non-inclusive epoch
            uint256 cutoffEpoch = epochTime.sub(lockDuration);

            //need to add up since the range could be in the middle somewhere
            //traverse inversely to make more current queries more gas efficient
            for (uint i = locks.length - 1; i + 1 != 0; i--) {
                uint256 lockEpoch = uint256(locks[i].unlockTime).sub(
                    lockDuration
                );
                //lock epoch must be less or equal to the epoch we're basing from.
                if (lockEpoch <= epochTime) {
                    if (lockEpoch > cutoffEpoch) {
                        amount = amount.add(locks[i].amount);
                    } else {
                        //stop now as no futher checks matter
                        break;
                    }
                }
            }
        }

        return amount;
    }

    //return currently locked but not active balance
    function pendingLockOf(
        address _user,
        uint256 _chainId
    ) external view returns (uint256 amount) {
        LockedBalance[] storage locks = userLocks[_user][_chainId];

        uint256 locksLength = locks.length;

        //return amount if latest lock is in the future
        uint256 currentEpoch = block.timestamp.div(rewardsDuration).mul(
            rewardsDuration
        );
        if (
            locksLength > 0 &&
            uint256(locks[locksLength - 1].unlockTime).sub(lockDuration) >
            currentEpoch
        ) {
            return locks[locksLength - 1].amount;
        }

        return 0;
    }

    function pendingLockAtEpochOf(
        uint256 _epoch,
        address _user,
        uint256 _chainId
    ) external view returns (uint256 amount) {
        LockedBalance[] storage locks = userLocks[_user][_chainId];

        //get next epoch from the given epoch index
        uint256 nextEpoch = uint256(epochs[_chainId][_epoch].date).add(
            rewardsDuration
        );

        //traverse inversely to make more current queries more gas efficient
        for (uint i = locks.length - 1; i + 1 != 0; i--) {
            uint256 lockEpoch = uint256(locks[i].unlockTime).sub(lockDuration);

            //return the next epoch balance
            if (lockEpoch == nextEpoch) {
                return locks[i].amount;
            } else if (lockEpoch < nextEpoch) {
                //no need to check anymore
                break;
            }
        }

        return 0;
    }

    //supply of all properly locked balances at most recent eligible epoch
    function totalSupply() external view returns (uint256 supply) {
        if (address(qLqdr) != address(0)) {
            supply = qLqdr.totalSupply();
        }

        for (uint j = 0; j < chainsLength; j++) {
            uint256 currentEpoch = block.timestamp.div(rewardsDuration).mul(
                rewardsDuration
            );
            uint256 cutoffEpoch = currentEpoch.sub(lockDuration);
            uint256 epochindex = epochs[j].length;

            //do not include next epoch's supply
            if (uint256(epochs[j][epochindex - 1].date) > currentEpoch) {
                epochindex--;
            }

            //traverse inversely to make more current queries more gas efficient
            for (uint i = epochindex - 1; i + 1 != 0; i--) {
                Epoch storage e = epochs[j][i];
                if (uint256(e.date) <= cutoffEpoch) {
                    break;
                }
                supply = supply.add(e.supply);
            }
        }

        return supply;
    }

    //supply of all properly locked balances at the given epoch
    function totalSupplyAtEpoch(
        uint256 _epoch
    ) external view returns (uint256 supply) {
        if (address(qLqdr) != address(0)) {
            supply = qLqdr.totalSupplyAtEpoch(_epoch);
        }

        for (uint j = 0; j < chainsLength; j++) {
            uint256 epochStart = uint256(epochs[j][_epoch].date)
                .div(rewardsDuration)
                .mul(rewardsDuration);
            uint256 cutoffEpoch = epochStart.sub(lockDuration);

            //traverse inversely to make more current queries more gas efficient
            for (uint i = _epoch; i + 1 != 0; i--) {
                Epoch storage e = epochs[j][i];
                if (uint256(e.date) <= cutoffEpoch) {
                    break;
                }
                supply = supply.add(epochs[j][i].supply);
            }
        }

        return supply;
    }

    //find an epoch index based on timestamp
    function findEpochId(
        uint256 _time,
        uint256 _chainId
    ) external view returns (uint256 epoch) {
        uint256 max = epochs[_chainId].length - 1;
        uint256 min = 0;

        //convert to start point
        _time = _time.div(rewardsDuration).mul(rewardsDuration);

        for (uint256 i = 0; i < 128; i++) {
            if (min >= max) break;

            uint256 mid = (min + max + 1) / 2;
            uint256 midEpochBlock = epochs[_chainId][mid].date;
            if (midEpochBlock == _time) {
                //found
                return mid;
            } else if (midEpochBlock < _time) {
                min = mid;
            } else {
                max = mid - 1;
            }
        }
        return min;
    }

    // Information on a user's locked balances
    function lockedBalances(
        address _user,
        uint256 _chainId
    )
        external
        view
        returns (
            uint256 total,
            uint256 unlockable,
            uint256 locked,
            LockedBalance[] memory lockData
        )
    {
        LockedBalance[] storage locks = userLocks[_user][_chainId];
        Balances storage userBalance = balances[_user][_chainId];
        uint256 nextUnlockId = userBalance.nextUnlockId;
        uint256 idx;
        for (uint i = nextUnlockId; i < locks.length; i++) {
            if (locks[i].unlockTime > block.timestamp) {
                if (idx == 0) {
                    lockData = new LockedBalance[](locks.length - i);
                }
                lockData[idx] = locks[i];
                idx++;
                locked = locked.add(locks[i].amount);
            } else {
                unlockable = unlockable.add(locks[i].amount);
            }
        }
        return (userBalance.locked, unlockable, locked, lockData);
    }

    //number of epochs
    function epochCount(uint256 _chainId) external view returns (uint256) {
        return epochs[_chainId].length;
    }

    /* ========== MUTATIVE FUNCTIONS ========== */

    function checkpointEpoch(uint256 _timestamp, uint256 _chainId) external {
        _checkpointEpoch(_timestamp, _chainId);
    }

    //insert a new epoch if needed. fill in any gaps
    function _checkpointEpoch(uint256 _timestamp, uint256 _chainId) internal {
        //create new epoch in the future where new non-active locks will lock to
        uint256 nextEpoch = _timestamp
            .div(rewardsDuration)
            .mul(rewardsDuration)
            .add(rewardsDuration);
        uint256 epochindex = epochs[_chainId].length;

        //first epoch add in constructor, no need to check 0 length

        //check to add
        if (epochs[_chainId][epochindex - 1].date < nextEpoch) {
            //fill any epoch gaps
            while (
                epochs[_chainId][epochs[_chainId].length - 1].date != nextEpoch
            ) {
                uint256 nextEpochDate = uint256(
                    epochs[_chainId][epochs[_chainId].length - 1].date
                ).add(rewardsDuration);
                epochs[_chainId].push(
                    Epoch({supply: 0, date: uint32(nextEpochDate)})
                );
            }
        }
    }

    // Locked tokens cannot be withdrawn for lockDuration and are eligible to receive stakingReward rewards
    function lock(
        address _account,
        uint256 _amount,
        uint256 _timestamp,
        uint256 _chainId
    ) external nonReentrant onlySelf {
        //lock
        _lock(_account, _amount, false, _timestamp, _chainId);
    }

    //lock tokens
    function _lock(
        address _account,
        uint256 _amount,
        bool _isRelock,
        uint256 _timestamp,
        uint256 _chainId
    ) internal {
        require(_amount > 0, "Cannot stake 0");
        require(!isShutdown, "shutdown");

        Balances storage bal = balances[_account][_chainId];

        //must try check pointing epoch first
        _checkpointEpoch(_timestamp, _chainId);

        uint112 lockAmount = _amount.to112();

        //add user balances
        bal.locked = bal.locked.add(lockAmount);

        //add to total supplies
        lockedSupply[_chainId] = lockedSupply[_chainId].add(lockAmount);

        //add user lock records or add to current
        uint256 lockEpoch = _timestamp.div(rewardsDuration).mul(
            rewardsDuration
        );
        //if a fresh lock, add on an extra duration period
        if (!_isRelock) {
            lockEpoch = lockEpoch.add(rewardsDuration);
        }
        uint256 unlockTime = lockEpoch.add(lockDuration);
        uint256 idx = userLocks[_account][_chainId].length;

        //if the latest user lock is smaller than this lock, always just add new entry to the end of the list
        if (
            idx == 0 ||
            userLocks[_account][_chainId][idx - 1].unlockTime < unlockTime
        ) {
            userLocks[_account][_chainId].push(
                LockedBalance({
                    amount: lockAmount,
                    unlockTime: uint32(unlockTime)
                })
            );
        } else {
            //else add to a current lock

            //if latest lock is further in the future, lower index
            //this can only happen if relocking an expired lock after creating a new lock
            if (
                userLocks[_account][_chainId][idx - 1].unlockTime > unlockTime
            ) {
                idx--;
            }

            //if idx points to the epoch when same unlock time, update
            //(this is always true with a normal lock but maybe not with relock)
            if (
                userLocks[_account][_chainId][idx - 1].unlockTime == unlockTime
            ) {
                LockedBalance storage userL = userLocks[_account][_chainId][
                    idx - 1
                ];
                userL.amount = userL.amount.add(lockAmount);
            } else {
                //can only enter here if a relock is made after a lock and there's no lock entry
                //for the current epoch.
                //ex a list of locks such as "[...][older][current*][next]" but without a "current" lock
                //length - 1 is the next epoch
                //length - 2 is a past epoch
                //thus need to insert an entry for current epoch at the 2nd to last entry
                //we will copy and insert the tail entry(next) and then overwrite length-2 entry

                //reset idx
                idx = userLocks[_account][_chainId].length;

                //get current last item
                LockedBalance storage userL = userLocks[_account][_chainId][
                    idx - 1
                ];

                //add a copy to end of list
                userLocks[_account][_chainId].push(
                    LockedBalance({
                        amount: userL.amount,
                        unlockTime: userL.unlockTime
                    })
                );

                //insert current epoch lock entry by overwriting the entry at length-2
                userL.amount = lockAmount;
                userL.unlockTime = uint32(unlockTime);
            }
        }

        //update epoch supply, epoch checkpointed above so safe to add to latest
        uint256 eId = epochs[_chainId].length - 1;
        //if relock, epoch should be current and not next, thus need to decrease index to length-2
        if (_isRelock) {
            eId--;
        }
        Epoch storage e = epochs[_chainId][eId];
        e.supply = e.supply.add(uint224(lockAmount));

        emit Staked(_account, lockEpoch, _amount, lockAmount);
    }

    // Withdraw all currently locked tokens where the unlock time has passed
    function _processExpiredLocks(
        address _account,
        bool _relock,
        address _withdrawTo,
        address,
        uint256 _checkDelay,
        uint256 _timestamp,
        uint256 _chainId
    ) internal {
        LockedBalance[] storage locks = userLocks[_account][_chainId];
        Balances storage userBalance = balances[_account][_chainId];
        uint112 locked;
        uint256 length = locks.length;
        uint256 reward = 0;

        if (
            isShutdown ||
            locks[length - 1].unlockTime <= _timestamp.sub(_checkDelay)
        ) {
            //if time is beyond last lock, can just bundle everything together
            locked = userBalance.locked;

            //dont delete, just set next index
            userBalance.nextUnlockId = length.to32();

            //check for kick reward
            //this wont have the exact reward rate that you would get if looped through
            //but this section is supposed to be for quick and easy low gas processing of all locks
            //we'll assume that if the reward was good enough someone would have processed at an earlier epoch
            if (_checkDelay > 0) {
                uint256 currentEpoch = block
                    .timestamp
                    .sub(_checkDelay)
                    .div(rewardsDuration)
                    .mul(rewardsDuration);
                uint256 epochsover = currentEpoch
                    .sub(uint256(locks[length - 1].unlockTime))
                    .div(rewardsDuration);
                uint256 rRate = MathUtil.min(
                    kickRewardPerEpoch.mul(epochsover + 1),
                    denominator
                );
                reward = uint256(locks[length - 1].amount).mul(rRate).div(
                    denominator
                );
            }
        } else {
            //use a processed index(nextUnlockId) to not loop as much
            //deleting does not change array length
            uint32 nextUnlockId = userBalance.nextUnlockId;
            for (uint i = nextUnlockId; i < length; i++) {
                //unlock time must be less or equal to time
                if (locks[i].unlockTime > _timestamp.sub(_checkDelay)) break;

                //add to cumulative amounts
                locked = locked.add(locks[i].amount);

                //check for kick reward
                //each epoch over due increases reward
                if (_checkDelay > 0) {
                    uint256 currentEpoch = block
                        .timestamp
                        .sub(_checkDelay)
                        .div(rewardsDuration)
                        .mul(rewardsDuration);
                    uint256 epochsover = currentEpoch
                        .sub(uint256(locks[i].unlockTime))
                        .div(rewardsDuration);
                    uint256 rRate = MathUtil.min(
                        kickRewardPerEpoch.mul(epochsover + 1),
                        denominator
                    );
                    reward = reward.add(
                        uint256(locks[i].amount).mul(rRate).div(denominator)
                    );
                }
                //set next unlock index
                nextUnlockId++;
            }
            //update next unlock index
            userBalance.nextUnlockId = nextUnlockId;
        }

        if (locked > 0) {
            //update user balances and total supplies
            userBalance.locked = userBalance.locked.sub(locked);
            lockedSupply[_chainId] = lockedSupply[_chainId].sub(locked);

            emit Withdrawn(_account, locked, _relock);

            //relock or return to user
            if (_relock) {
                _lock(_withdrawTo, locked, true, _timestamp, _chainId);
            }
        }
    }

    // Withdraw all currently locked tokens where the unlock time has passed
    function _processInstantUnlock(
        address _account,
        uint256,
        uint256 _chainId
    ) internal {
        LockedBalance[] storage locks = userLocks[_account][_chainId];
        Balances storage userBalance = balances[_account][_chainId];
        uint112 locked;
        uint256 length = locks.length;

        //if time is beyond last lock, can just bundle everything together
        locked = userBalance.locked;

        //dont delete, just set next index
        userBalance.nextUnlockId = length.to32();
        require(locked > 0, "no exp locks");

        //update user balances and total supplies
        userBalance.locked = userBalance.locked.sub(locked);
        lockedSupply[_chainId] = lockedSupply[_chainId].sub(locked);

        emit Withdrawn(_account, locked, false);
    }

    // withdraw expired locks to a different address
    function withdrawExpiredLocksTo(
        address _account,
        uint256 _timestamp,
        uint256 _chainId
    ) external nonReentrant onlySelf {
        _processExpiredLocks(
            _account,
            false,
            _account,
            _account,
            0,
            _timestamp,
            _chainId
        );
    }

    // Withdraw/relock all currently locked tokens where the unlock time has passed
    function processExpiredLocks(
        address _account,
        bool _relock,
        uint256 _timestamp,
        uint256 _chainId
    ) external nonReentrant onlySelf {
        _processExpiredLocks(
            _account,
            _relock,
            _account,
            _account,
            0,
            _timestamp,
            _chainId
        );
    }

    function kickExpiredLocks(
        address _account,
        uint256 _timestamp,
        uint256 _chainId
    ) external nonReentrant onlySelf {
        //allow kick after grace period of 'kickRewardEpochDelay'
        _processExpiredLocks(
            _account,
            false,
            _account,
            _account,
            rewardsDuration.mul(kickRewardEpochDelay),
            _timestamp,
            _chainId
        );
    }

    function instantWithdrawTo(
        address _account,
        uint256 _timestamp,
        uint256 _chainId
    ) external nonReentrant onlySelf {
        _processExpiredLocks(
            _account,
            false,
            _account,
            msg.sender,
            0,
            _timestamp,
            _chainId
        );
        _processInstantUnlock(_account, _timestamp, _chainId);
    }

    /* ========== RESTRICTED FUNCTIONS ========== */

    // Added to support recovering LP Rewards from other systems such as BAL to be distributed to holders
    function recoverERC20(
        address _tokenAddress,
        uint256 _tokenAmount
    ) external onlyOwner {
        IERC20(_tokenAddress).safeTransfer(owner(), _tokenAmount);
        emit Recovered(_tokenAddress, _tokenAmount);
    }

    /* ========== EVENTS ========== */
    event Staked(
        address indexed _user,
        uint256 indexed _epoch,
        uint256 _paidAmount,
        uint256 _lockedAmount
    );
    event Withdrawn(address indexed _user, uint256 _amount, bool _relocked);
    event KickReward(
        address indexed _user,
        address indexed _kicked,
        uint256 _reward
    );
    event Recovered(address _token, uint256 _amount);

    receive() external payable {}

    fallback() external {}
}
"
