pragma solidity ^0.8.0;








contract BridgeERC20V2 is Committee, Pausable, ReentrancyGuard {
    using SafeERC20 for IERC20;
    using EnumerableSet for EnumerableSet.UintSet;

    enum FEE_TYPE {
        NO_FEE,
        FIX,
        PERCENTAGE
    }

    enum TOKEN_STRATEGY {
        LOCK,
        BURN
    }

    struct ERC20BridgeInfo {
        IERC20 token;
        address feeReceiver;
        // if fee type is PERCENTAGE it will be divided by 10,000 (50 mean 0.5%) else it mean fix rate
        uint256 fee;
        FEE_TYPE feeType;
        TOKEN_STRATEGY strategy;
        address receiver;
    }

    address public wrapNative;

    mapping(IERC20 => EnumerableSet.UintSet) private _destChains;

    mapping(IERC20 => ERC20BridgeInfo) public bridgeInfoByToken;

    event Bridge(address indexed token, address indexed from, uint256 amount, uint256 indexed destChainID);

    event AddDestChainID(address indexed token, uint256 indexed chainID);
    event RemoveDestChainID(address indexed token, uint256 indexed chainID);

    event SetToken(address indexed token);
    event WrapNativeSet(address indexed sender, address indexed from, address indexed to);
    event DeleteToken(address indexed token);

    constructor(
        address _committee,
        address _wrapNative,
        ERC20BridgeInfo[] memory _tokens,
        uint256[][] memory _destChainIDs
    ) {
        for (uint256 i; i < _tokens.length; i++) {
            _setToken(_tokens[i]);
            for (uint256 j; j < _destChainIDs[i].length; j++) {
                _addDestChainID(_tokens[i].token, _destChainIDs[i][j]);
            }
        }
        committee = _committee;
        wrapNative = _wrapNative;
    }

    ///////////////////////////////// setter /////////////////////////////////////

    function pause() external onlyCommittee {
        _pause();
    }

    function unpause() external onlyCommittee {
        _unpause();
    }

    function addDestChainID(IERC20 _token, uint256 _destChainID) public onlyCommittee {
        _addDestChainID(_token, _destChainID);
    }

    function removeDestChainID(IERC20 _token, uint256 _destChainID) external onlyCommittee {
        _destChains[_token].remove(_destChainID);
        emit RemoveDestChainID(address(_token), _destChainID);
    }

    function getDestChains(IERC20 _token) external view returns (uint256[] memory) {
        uint256 length = _destChains[_token].length();
        uint256[] memory results = new uint256[](length);
        for (uint256 i; i < length; i++) {
            results[i] = _destChains[_token].at(i);
        }
        return results;
    }

    function setToken(ERC20BridgeInfo memory _bridgeInfo) public onlyCommittee {
        _setToken(_bridgeInfo);
    }

    function setWrapNative(address _wrapNative) external onlyCommittee {
        require(_wrapNative != address(0));
        emit WrapNativeSet(msg.sender, wrapNative, _wrapNative);
        wrapNative = _wrapNative;
    }

    function deleteToken(IERC20 _token) external onlyCommittee {
        require(address(_token) != address(0));
        delete bridgeInfoByToken[_token];
        emit DeleteToken(address(_token));
    }

    function _addDestChainID(IERC20 _token, uint256 _destChainID) private {
        _destChains[_token].add(_destChainID);
        emit AddDestChainID(address(_token), _destChainID);
    }

    function _setToken(ERC20BridgeInfo memory _bridgeInfo) private {
        require(address(_bridgeInfo.token) != address(0));
        bridgeInfoByToken[_bridgeInfo.token] = _bridgeInfo;
        emit SetToken(address(_bridgeInfo.token));
    }

    //////////////////////////////////////////////////////////////////////////////

    function bridge(
        IERC20 _token,
        uint256 _amount,
        uint256 _destChainID
    ) external whenNotPaused nonReentrant {
        _bridge(_token, _amount, _destChainID, false);
    }

    function bridge(uint256 _destChainID) external payable whenNotPaused nonReentrant {
        INativeWrap(wrapNative).deposit{ value: msg.value }();
        _bridge(IERC20(wrapNative), msg.value, _destChainID, true);
    }

    function _bridge(
        IERC20 _token,
        uint256 _amount,
        uint256 _destChainID,
        bool _isNative
    ) private {
        require(!_isContract(msg.sender), "IS_CONTRACT");
        require(address(bridgeInfoByToken[_token].token) != address(0), "TOKEN_IS_NOT_SET");
        require(_destChains[_token].contains(_destChainID), "CHAIN_NOT_SUPPORTED");

        ERC20BridgeInfo memory bridgeInfo = bridgeInfoByToken[_token];

        uint256 fee = _computeFeeByFeeType(bridgeInfo.feeType, _amount, bridgeInfo.fee);

        uint256 totalAmount = _amount - fee;

        if (_isNative) {
            if (bridgeInfo.strategy == TOKEN_STRATEGY.LOCK) {
                _token.safeTransfer(bridgeInfo.receiver, totalAmount);
            }
            if (bridgeInfo.feeType != FEE_TYPE.NO_FEE && fee > 0) {
                _token.safeTransfer(bridgeInfo.feeReceiver, fee);
            }
        } else {
            if (bridgeInfo.strategy == TOKEN_STRATEGY.LOCK) {
                _token.safeTransferFrom(msg.sender, bridgeInfo.receiver, totalAmount);
            } else {
                IBurnable(address(_token)).burnFrom(msg.sender, totalAmount);
            }
            if (bridgeInfo.feeType != FEE_TYPE.NO_FEE && fee > 0) {
                _token.safeTransferFrom(msg.sender, bridgeInfo.feeReceiver, fee);
            }
        }

        emit Bridge(address(bridgeInfo.token), msg.sender, totalAmount, _destChainID);
    }

    function _computeFeeByFeeType(
        FEE_TYPE feeType,
        uint256 _amount,
        uint256 _fee
    ) private pure returns (uint256) {
        if (feeType == FEE_TYPE.FIX) {
            return _fee;
        } else if (feeType == FEE_TYPE.PERCENTAGE) {
            return (_amount * _fee) / 10000;
        }
        return 0;
    }

    function computeFeeByFeeType(IERC20 _token, uint256 _amount) public view returns (uint256) {
        return _computeFeeByFeeType(bridgeInfoByToken[_token].feeType, _amount, bridgeInfoByToken[_token].fee);
    }

    function _isContract(address _account) private view returns (bool) {
        uint32 size;
        assembly {
            size := extcodesize(_account)
        }
        return size > 0;
    }
}
