    "/contracts/lib/Token/ERC20NotifyOnReceive.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.8;

import "./ERC20.sol";
import "./IERC20Receiver.sol";
import "./../Address.sol";
import "./IERC165.sol";

abstract contract ERC20NotifyOnReceive is ERC20 {
    using Address for address;

    function _transfer(address sender, address recipient, uint256 amount) internal override virtual {
        super._transfer(sender, recipient, amount);
        _notifyOnReceive(msg.sender, sender, recipient, amount);
    }

    function _mint(address account, uint256 amount) internal override virtual {
        super._mint(account, amount);
        _notifyOnReceive(msg.sender, address(0), account, amount);
    }

    function _notifyOnReceive(address operator, address sender, address recipient, uint256 amount) internal virtual {
        if (recipient.isContract()) {
            if (
                _supportsERC165Interface(recipient, type(IERC165).interfaceId) &&
                !_supportsERC165Interface(recipient, 0xffffffff)
            ) {
                if (_supportsERC165Interface(recipient, type(IERC20Receiver).interfaceId)) {
                    IERC20Receiver receiver = IERC20Receiver(recipient);
                    receiver.onERC20Received(operator, sender, amount);
                }
            }
        }
    }

    function _supportsERC165Interface(address account, bytes4 interfaceId) private view returns (bool) {
        bytes memory encodedParams = abi.encodeWithSelector(IERC165.supportsInterface.selector, interfaceId);
        (bool success, bytes memory result) = account.staticcall{gas: 30000}(encodedParams);
        if (result.length < 32) return false;
        return success && abi.decode(result, (bool));
    }
}
"
