    "contracts/staking/FtoStaking.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity 0.8.16;

import "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/ERC20VotesComp.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/Strings.sol";
import "@openzeppelin/contracts/security/Pausable.sol";

/**
 * @title FtoStaking
 * @notice It lets users stake $TOKEN token and receive $ftoTOKEN.
 * @dev A perpetual staking with a lock period.
 * It locks the user's $TOKEN token and mint ftoTOKEN to them. Users may
 * unstake their $TOKEN before lock period, but they need to pay the
 * penalty amount. The penalty amount will be subtracted from user's staked
 * amount and be sent to the treasury.
 *
 * Each stake will reset the lock period.
 **/
// solhint-disable not-rely-on-time
contract FtoStaking is ERC20VotesComp, Ownable, Pausable, ReentrancyGuard {
    using SafeERC20 for IERC20Metadata;

    /// @dev Max base points
    uint256 public constant MAX_BPS = 100_00;

    /// @dev Max penalty days
    uint256 private constant PENALTY_DAYS_LIMIT = 60;

    /// @dev Max penalty base points
    uint256 private constant PENALTY_BP_LIMIT = 50_00;

    /// @dev Staking token
    IERC20Metadata public stakingToken;

    /// @dev Info about each stake by user address
    mapping(address => Stake) public stakers;

    /// @dev Penalty days value
    uint128 public penaltyDays;

    /// @dev Penalty base points value
    uint128 public penaltyBP;

    /// @dev The address to which the penalty tokens will be transferred
    address public treasury;

    /// @dev Minimum stake amount
    uint256 public minAmount;
    uint256 public maxPerStaker;
    uint256 public maxTotal;

    /// @dev Flag to allow/prevent staking
    bool public stakingAllowed;

    /// @dev Distributor address
    address public distributor;

    struct Stake {
        uint256 amount;
        uint256 timestamp;
    }

    event Staked(
        address indexed staker,
        uint256 amount,
        uint256 amountTotal,
        uint256 totalSupply
    );

    event Unstaked(
        address indexed staker,
        uint256 amount,
        uint256 amountLeft,
        uint256 penaltyAmount,
        uint256 totalSupply
    );

    event PenaltyDaysUpdated(uint128 penaltyDays);
    event PenaltyBPUpdated(uint128 penaltyBP);
    event TreasuryUpdated(address treasury);
    event SetMinAmount(uint256 minAmount);
    event SetDistributor(address distributor);
    event SetStakingAllowed(bool stakingAllowed);
    event SetMaxPerStaker(uint256 amountBefore, uint256 amountAfter);
    event SetMaxTotal(uint256 amountBefore, uint256 amountAfter);

    /**
     * @notice constructor
     * @param _stakingToken Staking token address
     * @param _penaltyDays Penalty days value
     * @param _penaltyBP Penalty base points value
     * @param _treasury The address to which the penalty tokens will be transferred
     * @param _minAmount The minimum amount that can be staked
     * @param _distributor The distributor address
     */
    constructor(
        IERC20Metadata _stakingToken,
        uint128 _penaltyDays,
        uint128 _penaltyBP,
        address _treasury,
        uint256 _minAmount,
        address _distributor,
        uint256 _maxPerStaker,
        uint256 _maxTotal
    )
        ERC20(
            string(abi.encodePacked("FTO Staked ", _stakingToken.name())),
            string(abi.encodePacked("fto", _stakingToken.symbol()))
        )
        ERC20Permit(string(abi.encodePacked("Staked ", _stakingToken.name())))
    {
        require(address(_stakingToken) != address(0), "Invalid staking token");
        require(
            _penaltyDays <= PENALTY_DAYS_LIMIT,
            "Penalty days exceeds limit"
        );
        require(_penaltyBP <= PENALTY_BP_LIMIT, "Penalty BP exceeds limit");
        require(_treasury != address(0), "Treasury is the zero address");

        stakingToken = _stakingToken;
        penaltyDays = _penaltyDays;
        penaltyBP = _penaltyBP;
        treasury = _treasury;
        minAmount = _minAmount;
        distributor = _distributor;
        maxPerStaker = _maxPerStaker;
        maxTotal = _maxTotal;
    }

    //////////
    // Pause
    //////////
    function pause() public onlyOwner {
        _pause();
    }

    function unpause() public onlyOwner {
        _unpause();
    }

    /**
     * @notice Stake staking tokens
     * @param _amount amount to stake
     */
    function stake(uint256 _amount) external whenNotPaused nonReentrant {
        require(stakingAllowed, "Staking not allowed");
        _stake(msg.sender, _amount);
    }

    /**
     * @notice Stake staking tokens for another user by distributor
     * @param _user Another user address
     * @param _amount Amount to stake
     */
    function stakeFor(address _user, uint128 _amount) external nonReentrant {
        require(msg.sender == distributor, "Only distributor can stake");
        _stake(_user, _amount);
    }

    /**
     * @notice Unstake staking tokens
     * @notice If penalty period is not over grab penalty
     * @param _amount Amount to unstake
     */
    function unstake(uint256 _amount) external whenNotPaused nonReentrant {
        Stake storage stakeRef = stakers[msg.sender];

        require(stakeRef.amount >= _amount, "Amount too high");

        _burn(msg.sender, _amount);

        // pays a penalty if unstakes during the penalty period
        uint256 penaltyAmount = _calculatePenalty(stakeRef, _amount);

        if (penaltyAmount > 0)
            stakingToken.safeTransfer(treasury, penaltyAmount);

        stakingToken.safeTransfer(msg.sender, _amount - penaltyAmount);

        stakeRef.amount -= _amount;

        emit Unstaked(
            msg.sender,
            _amount,
            stakeRef.amount,
            penaltyAmount,
            totalSupply()
        );
    }

    // ** ONLY OWNER **

    /**
     * @notice Allow/Prevent staking
     * @param _stakingAllowed true/false to allow/prevent
     */
    function setStakingAllowed(bool _stakingAllowed) external onlyOwner {
        require(_stakingAllowed != stakingAllowed, "Already set");

        stakingAllowed = _stakingAllowed;

        emit SetStakingAllowed(_stakingAllowed);
    }

    /**
     * @notice Set a new penalty days value
     * @param _penaltyDays New penalty days value
     */
    function setPenaltyDays(uint128 _penaltyDays) external onlyOwner {
        require(
            _penaltyDays <= PENALTY_DAYS_LIMIT,
            "Penalty days exceeds limit"
        );
        penaltyDays = _penaltyDays;
        emit PenaltyDaysUpdated(_penaltyDays);
    }

    function setMaxPerStaker(uint256 amount) external onlyOwner {
        emit SetMaxPerStaker(maxPerStaker, amount);
        maxPerStaker = amount;
    }

    function setMaxTotal(uint256 amount) external onlyOwner {
        emit SetMaxTotal(maxTotal, amount);
        maxTotal = amount;
    }

    /**
     * @notice Set a new penalty base points value
     * @param _penaltyBP New penalty base points value
     */
    function setPenaltyBP(uint128 _penaltyBP) external onlyOwner {
        require(_penaltyBP <= PENALTY_BP_LIMIT, "Penalty BP exceeds limit");
        penaltyBP = _penaltyBP;
        emit PenaltyBPUpdated(_penaltyBP);
    }

    /**
     * @notice Set a new penalty treasury
     * @param _treasury New treasury address
     */
    function setTreasury(address _treasury) external onlyOwner {
        require(_treasury != address(0), "Treasury is the zero address");
        treasury = _treasury;
        emit TreasuryUpdated(_treasury);
    }

    /**
     * @notice Set a minimum amount to stake
     * @param _minAmount Minimum stake amount
     */
    function setMinAmount(uint256 _minAmount) external onlyOwner {
        minAmount = _minAmount;
        emit SetMinAmount(_minAmount);
    }

    /**
     * @notice Set a distributor address
     * @param _distributor Distributor address
     */
    function setDistributor(address _distributor) external onlyOwner {
        distributor = _distributor;
        emit SetDistributor(_distributor);
    }

    /**
     * @notice Emergency withdraw tokens from contract
     * @param _tokenAddress The token address to withdraw the balance
     */
    function emergencyWithdrawToken(
        IERC20Metadata _tokenAddress
    ) external onlyOwner {
        _tokenAddress.safeTransfer(
            msg.sender,
            _tokenAddress.balanceOf(address(this))
        );
    }

    // ** INTERNAL **

    /// @dev disable transfers
    function _transfer(
        address /* _from */,
        address /* _to */,
        uint256 /* _amount */
    ) internal pure override {
        revert("NON_TRANSFERABLE");
    }

    /**
     * @notice Creates stake from the array
     * @param _staker the address of the staker
     * @param _amount amount to stake
     */
    function _stake(address _staker, uint256 _amount) private {
        require(_amount >= minAmount, "Invalid amount");

        Stake storage stakeRef = stakers[_staker];

        require(
            stakeRef.amount + _amount <= maxPerStaker,
            "Staker max reached"
        );
        require(totalSupply() + _amount <= maxTotal, "Total max reached");

        stakingToken.safeTransferFrom(msg.sender, address(this), _amount);
        _mint(_staker, _amount);
        _delegate(_staker, _staker);

        stakeRef.amount += _amount;
        stakeRef.timestamp = block.timestamp;

        emit Staked(_staker, _amount, stakeRef.amount, totalSupply());
    }

    /**
     * @notice Calculate the panalty amount if user unstakes before penalty days
     * @param _stakeRef Stake tuple
     */
    function _calculatePenalty(
        Stake memory _stakeRef,
        uint256 _amount
    ) internal view returns (uint256 _penaltyAmount) {
        if (
            _stakeRef.timestamp + penaltyDays * 86400 > uint128(block.timestamp)
        ) {
            _penaltyAmount = (_amount * penaltyBP) / MAX_BPS;
        }
    }
