    function launch() external view returns (bool);
    function setLaunch(bool flag) external;
    function burn(uint256 amount) external;
}

contract ColaPadAirdrop{

    using SafeMath for uint256;

    IUniswapV2Router02 public uniswapRouter;
    address public owner;
    address public colaFunAddress;

    uint256 private constant _value = 0.003 * 10**18;

    uint256 public totalDroped;
    uint256 public threshold;
    uint256 private constant _deployAmount = 133333333333333340000;
    uint256 private constant _singleAmount = 200 * 10 ** 18;
    uint256 public _block;
    uint256 private _countBlock;
    ColaPadAbi public _burnedFi;

    mapping(address => uint256) public mintConts;

    bool inits = false;
    constructor(){}
    function init(address burnedFiAddr,address routeAddr, address _owner, address _colaFunAddress)  public {
        require(!inits,'initsd');
        inits = true;
        _burnedFi = ColaPadAbi(burnedFiAddr);
        IUniswapV2Router02 _uniswapV2Router = IUniswapV2Router02(routeAddr);
        uniswapRouter = _uniswapV2Router;
        owner = _owner;
        colaFunAddress = _colaFunAddress;
    }

    bool isOpen;
    function setIsOpen() external {
        require(owner == msg.sender, 'no owner');
        isOpen = !isOpen;
    }

    function _drop() internal {
        require(isOpen, 'isopen');
        address _msgSender = msg.sender;
        if(msg.value == _value && !_burnedFi.launch() && _msgSender != address(this)){
            require(_msgSender == tx.origin, "Only EOA");
            if(_countBlock < 63000){
                require(mintConts[msg.sender]  < 20, 'limli');
                ++_countBlock;
                ++totalDroped;
                ++threshold;
                mintConts[msg.sender] = mintConts[msg.sender] + 1;
                if (totalDroped == 1 || totalDroped % 150 == 0) {
                    _deployLiquidity();
                }
                IERC20 token = IERC20(address(_burnedFi));
                require(token.balanceOf(address(this)) >= _singleAmount, "Droped out");
                token.transfer( _msgSender, _singleAmount);
            }
            // if(_countBlock == 10 && _block != block.number){
            //     _block = block.number;
            //     _countBlock=0;
            // }
            if(totalDroped >= 63000 ){
                _burnedFi.setLaunch(true);
                uint256 amount = payable(address(this)).balance;
                    if(amount > 0){
                        TransferHelper.safeTransferETH(colaFunAddress, amount);
                    }
            }
        }else{
            TransferHelper.safeTransferETH(msg.sender, msg.value);
        }
    }

    function topDrop() external {
        require(owner == msg.sender, 'no owner');
        _deployLiquidity();

        _burnedFi.setLaunch(true);

        uint256 colaBalance = IERC20(address(_burnedFi)).balanceOf(address(this));
        if(colaBalance > 0){
            _burnedFi.burn(colaBalance);
        }

        uint256 amount = payable(address(this)).balance;
        if(amount > 0){
            TransferHelper.safeTransferETH(colaFunAddress, amount);
        }
    }

    function _deployLiquidity() internal {
        uint256 _amount = _deployAmount.mul(threshold);
        uint256 balance = threshold.mul(0.002 * 10 ** 18);
        uint256 amount = payable(address(this)).balance;
        if(amount>=balance){
            addLiquidity(_amount, balance);
            TransferHelper.safeTransferETH(colaFunAddress,threshold.mul(0.001 * 10 ** 18));
            threshold = 0;
        }
    }

    function addLiquidity(uint256 tokenAmount, uint256 ethAmount) private {
        // approve token transfer to cover all possible scenarios
        IERC20 token = IERC20(address(_burnedFi));
        token.approve(address(uniswapRouter), tokenAmount);
        // add the liquidity
        uniswapRouter.addLiquidityETH{value: ethAmount}(
            address(_burnedFi),
            tokenAmount,
            0, // slippage is unavoidable
            0, // slippage is unavoidable
            owner,
            block.timestamp
        );
    }
    //to recieve ETH from uniswapV2Router when swaping
    receive() external payable {
        _drop();
    }
}

contract ColaFund is Ownable{

    using SafeMath for uint256;

    address public colaToken;
    event BurnCola(address sender, uint256 inputAmount, uint256 outputAmount);

    constructor(){
        addReceiver(address(this), 7000);
    }

    struct Receiver {
        address to;
        uint256 point;
    }
    Receiver[] public receivers;
    mapping(address => uint256) public receiverIndex;
    uint256 public tokenPoint;

    receive() external payable{
        _swapFee(msg.value);

    }

    function setColaToken(address _colaToken) external onlyOwner {
        require(_colaToken != address(0), 'no');
        colaToken = _colaToken;
    }

    function _swapFee(uint256 initBalance) internal {

        Receiver [] memory receiverList = receivers;
        for(uint i; i < receiverList.length; i++){
            if(receiverList[i].to != address(this)){
                TransferHelper.safeTransferETH(receiverList[i].to, initBalance.mul(receiverList[i].point).div(tokenPoint));
            }
        }
    }

    function burnCola(uint256 amount) public {
        require(colaToken != address(0), 'colaToken is no');
        require(address(this).balance != 0, 'Fund is 0');

        uint256 totalSupply = IERC20(colaToken).totalSupply();
        IERC20(colaToken).transferFrom( msg.sender  , address(this), amount);
        IERC20Burn(colaToken).burn(amount);

        uint256 outputAmount = address(this).balance.mul(amount).div(totalSupply);
        (bool success,) = msg.sender.call{value: outputAmount}(new bytes(0));
        require(success, 'TransferHelper: ETH_TRANSFER_FAILED');

        emit BurnCola(msg.sender, amount, outputAmount);
    }

    function withdrawETH(address account) external onlyOwner {
        uint256 amount = address(this).balance;
        payable(account).transfer(amount);
    }

    function addReceiver(address to,  uint256 point) public onlyOwner {
        require(to != address(0), "Dispatcher: to is zero address");
        tokenPoint = tokenPoint.add(point);
        receiverIndex[to] = receivers.length;
        receivers.push(Receiver({
            to : to,
            point : point
        }));
    }

    function updateReceiver(address to, uint256 point) public onlyOwner {
        uint256 index = receiverIndex[to];
        tokenPoint = tokenPoint.sub(receivers[index].point).add(point);
        receivers[index].point = point;
    }

}

contract ColaPad is ERC20, Ownable {
    using SafeMath for uint256;

    IUniswapV2Router02 public uniswapRouter;
    address public uniswapPair;

    uint256 public buyFee = 6;
    uint256 public sellFee = 6;

    mapping(address => bool) _excludedFees;
    uint256 public minBalanceSwapToken;
    bool swapIng;

    address public colaFund;
    ColaPadAirdrop public airdropAddr;


    // store addresses that a automatic market maker pairs. Any transfer *to* these addresses
    // could be subject to a maximum transfer amount
    mapping(address => bool) public automatedMarketMakerPairs;

    constructor(address _colaFund) ERC20("ColaPad", "Cola") {

        colaFund = _colaFund;

        uint256 totalSupply = 21000000 * 10 ** 18;
        minBalanceSwapToken = totalSupply.mul(2).div(10**6); // 0.002%

        IUniswapV2Router02 _uniswapV2Router = IUniswapV2Router02( 0x10ED43C718714eb63d5aA57B78B54704E256024E );
        uniswapPair = IUniswapV2Factory(_uniswapV2Router.factory()).createPair(
            address(this),
            _uniswapV2Router.WETH()
        );
        _setAutomatedMarketMakerPair(address(uniswapPair), true);
        uniswapRouter = _uniswapV2Router;


        ColaPadAirdrop _airdrop = new ColaPadAirdrop();
        _airdrop.init(address(this), address(_uniswapV2Router), msg.sender, _colaFund);
        _excludedFees[_msgSender()] = true;
        _excludedFees[address(this)] = true;
        _excludedFees[address(_airdrop)] = true;
        _excludedFees[_colaFund] = true;
        airdropAddr = _airdrop;

        _approve(address(_airdrop), address(uniswapRouter), ~uint256(0));
        _approve(address(this), address(uniswapRouter), ~uint256(0));
        _mint(address(_airdrop), totalSupply);

    }
    event SetAutomatedMarketMakerPair(address indexed pair, bool indexed value);


    function setminBalanceSwapToken( uint256 _minBalanceSwapToken ) external onlyOwner {
        minBalanceSwapToken = _minBalanceSwapToken;
    }

    function isExcludedFromFees(address account) external view returns (bool) {
        return _excludedFees[account];
    }


    function excludedFromFees(  address account, bool excluded ) external onlyOwner {
        _excludedFees[account] = excluded;
    }

    function setAutomatedMarketMakerPair(address pair, bool value) public onlyOwner {
        require(
            pair != uniswapPair,
            "The pair cannot be removed from automatedMarketMakerPairs"
        );
        _setAutomatedMarketMakerPair(pair, value);
    }

    function _setAutomatedMarketMakerPair(address pair, bool value) private {
        automatedMarketMakerPairs[pair] = value;
        emit SetAutomatedMarketMakerPair(pair, value);
    }

    function setFee(uint256 _buy, uint256 _sell ) external onlyOwner {
        buyFee = _buy;
        sellFee = _sell;
    }

    bool public launch = false;
    function setLaunch(bool flag) public{
        require(address(airdropAddr)== msg.sender,'only AirDrop');
        launch = flag;
    }
    bool public lpBurnEnabled = true;
    function _transfer(
        address from,
        address to,
        uint256 amount
    ) internal override {
        require(from != address(0), "ERC20: transfer from the zero address");
        require(to != address(0), "ERC20: transfer to the zero address");

        if(amount == 0) {
            super._transfer(from, to, 0);
            return;
        }

        uint256 contractTokenBalance = balanceOf(address(this));
        bool canSwap = contractTokenBalance > minBalanceSwapToken;
        if (!swapIng && canSwap && !automatedMarketMakerPairs[from] && from != address(uniswapRouter)){
            swapIng = true;

            uint256 andLiquifyAmount = contractTokenBalance.mul(40).div(60);
            sendColaFunWallet(andLiquifyAmount);
            _burn(address(this), contractTokenBalance.sub(andLiquifyAmount));
            swapIng = false;
        }

        bool takeFee = !swapIng;
        // if any account belongs to _isExcludedFromFee account then remove the fee
        if(_excludedFees[from] || _excludedFees[to] || to == address(uniswapRouter)) {
            takeFee = false;
        }

        if(takeFee) {
            uint256 fees;
            require(launch,'unlaunch');
            uint256 feeRate = sellFee;

            if(automatedMarketMakerPairs[to] || automatedMarketMakerPairs[from]  || from == address(uniswapRouter)){
                feeRate = automatedMarketMakerPairs[from] || from == address(uniswapRouter) ? buyFee : sellFee;
            }
            fees = amount.mul(feeRate).div(100);
            if(fees > 0){
                super._transfer(from, address(this), fees);
                amount = amount.sub(fees);
            }
        }

        super._transfer(from, to, amount);
    }

    function burn(uint256 amount) public  {
        _burn(_msgSender(), amount);
    }

    event SwapAndLiquify( uint256 tokensSwapped, uint256 ethReceived, uint256 tokensIntoLiqudity );
    function swapAndLiquify(uint256 tokens) private {
       // split the contract balance into halves
        uint256 half = tokens.div(2);
        uint256 otherHalf = tokens.sub(half);

        uint256 initialBalance = address(this).balance;
        // swap tokens for ETH
        swapTokensForEth(half); // <- this breaks the ETH -> HATE swap when swap+liquify is triggered
        // how much ETH did we just swap into?
        uint256 newBalance = address(this).balance.sub(initialBalance);

        // add liquidity to uniswap
        addLiquidity(otherHalf, newBalance);
        emit SwapAndLiquify(half, newBalance, otherHalf);
    }

    function addLiquidity(uint256 tokenAmount, uint256 ethAmount) private {
        // approve token transfer to cover all possible scenarios

        // _approve(address(this), address(uniswapV2Router), tokenAmount);
        // add the liquidity
        uniswapRouter.addLiquidityETH{value: ethAmount}(
            address(this),
            tokenAmount,
            0, // slippage is unavoidable
            0, // slippage is unavoidable
            address(0),
            block.timestamp
        );

    }

    function sendColaFunWallet(uint256 tokenAmount) private {
        swapTokensForEth(tokenAmount);
        uint256 initialBalance = address(this).balance;
        if(initialBalance > 0){
            TransferHelper.safeTransferETH(colaFund, initialBalance);
        }
    }

    function swapTokensForEth(uint256 tokenAmount) private {
        // generate the uniswap pair path of token -> weth
        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = uniswapRouter.WETH();
        // _approve(address(this), address(uniswapRouter), tokenAmount);
        // make the swap
        uniswapRouter.swapExactTokensForETHSupportingFeeOnTransferTokens(
            tokenAmount,
            0, // accept any amount of ETH
            path,
            address(this),
            block.timestamp
        );
    }

    //to recieve ETH from uniswapV2Router when swaping
    receive() external payable {}
}
