// File: contracts/Deposit.sol


pragma solidity ^0.8.0;



contract Deposit is Ownable {

    struct Invest {
        address user;
        address referral;
        uint8 investType;
        uint256 amount;
    }

    address public recipient;

    uint8 public typesLimit = 1;
    uint64 public investCount;

    IERC20 private immutable _TOKEN;

    uint256 public MIN_DEPOSIT_AMOUNT = 1e21;
    
    mapping(uint64 => Invest) public deposits;

    event DepositEvent(address indexed user, address indexed referral, uint8 indexed investType, uint256 amount);

    constructor(address _token, address _recipient){
        recipient = _recipient;
        _TOKEN = IERC20(_token);
    }

    /**
     * @dev Function to make a deposit.
     * @param referral Address of the referrer.
     * @param amount Deposit amount.
     * @param investType Investment type.
     * @notice Requires valid investType and amount above the minimum deposit.
     */
    function deposit(address referral, uint256 amount, uint8 investType) external {
        require(investType <= typesLimit);
        require(amount >= MIN_DEPOSIT_AMOUNT);

        require(_TOKEN.transferFrom(msg.sender, recipient, amount));

        investCount ++;

        deposits[investCount] = Invest(
            msg.sender,
            referral,
            investType,
            amount
        );

        emit DepositEvent(msg.sender, referral, investType, amount);

    }

    /**
     * @dev Function to set the minimum deposit amount.
     * @param newAmount New minimum deposit amount.
     * @notice Only callable by the contract owner.
     */
    function setMinDepositAmount(uint256 newAmount) external onlyOwner {
        MIN_DEPOSIT_AMOUNT = newAmount;
    }

    /**
     * @dev Function to set the limit on investment types.
     * @param newLimit New limit on investment types.
     * @notice Only callable by the contract owner.
     */
    function setTypesLimit(uint8 newLimit) external onlyOwner {
        typesLimit = newLimit;
    }

    /**
     * @dev Function to set the recipient address for deposits.
     * @param newRecipient New recipient address for deposits.
     * @notice Only callable by the contract owner.
     */
    function setRecipient(address newRecipient) external onlyOwner {
        recipient = newRecipient;
    }
}
