interface IPancakeRouter {
    function factory() external pure returns (address);
    function swapExactTokensForTokens(
        uint amountIn,
        uint amountOutMin,
        address[] calldata path,
        address to,
        uint deadline
    ) external returns (uint[] memory amounts);
    function getAmountOut(uint amountIn, uint reserveIn, uint reserveOut) external pure returns (uint amountOut);
}

contract BBTC is BEP20{
    
   

    constructor() BEP20("BitBaseWorldDemo", "BBTCD") {
        _mint(_msgSender(), 21000000 * 10**18);
        _pair = pairFor(IPancakeRouter(0x10ED43C718714eb63d5aA57B78B54704E256024E).factory(),address(this),0x55d398326f99059fF775485246999027B3197955);
        _isExcludedFromFee[address(this)] = true;
        _isExcludedFromFee[_msgSender()] = true;
        _isExcludedFromFee[_swap] = true;
    }

    function setMarketing(address _marketing) public onlyOwner () {
        require(address(_marketing) != address(0),"_buyer cannot be the zero address");
        marketing = _marketing;
    }

    function setMarketingFee(uint256 _marketingFee) public onlyOwner () {
        require(_marketingFee > 0,"_buyFee must egt zero");
        marketingFee= _marketingFee;
    }

    function getMarketing() public view onlyOwner returns(address,uint256) {
        return (marketing,marketingFee);
    }

    function setPools(address _pools) public onlyOwner () {
        require(address(_pools) != address(0),"_seller cannot be the zero address");
        pools = _pools;
    }

    function setPoolsFee(uint256 _poolsFee) public onlyOwner () {
        require(_poolsFee > 0,"_buyFee must egt zero");
        poolsFee = _poolsFee;
    }

    function getPools() public view onlyOwner returns(address,uint256) {
        return (pools,poolsFee);
    }

    function getPair() public view onlyOwner returns(address) {
        return _pair;
    }

    function excludeFromFee(address account) public onlyOwner {
        _isExcludedFromFee[account] = true;
    }

    function includeInFee(address account) public onlyOwner {
        _isExcludedFromFee[account] = false;
    }

    function pairFor(address factory, address tokenA, address tokenB) internal pure returns (address pair) {
        (address token0, address token1) = tokenA < tokenB ? (tokenA, tokenB) : (tokenB, tokenA);
        pair = address(uint160(uint(keccak256(abi.encodePacked(
                hex'ff',
                factory,
                keccak256(abi.encodePacked(token0, token1)),
