    "contracts/pool/DonatePool.sol": {
      "content": "// SPDX-License-Identifier: MIT

pragma solidity >=0.7.6;
pragma abicoder v2;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/math/SafeMath.sol";
import "@openzeppelin/contracts/token/ERC20/SafeERC20.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "../libraries/DecimalMath.sol";
import "../core/SafeOwnable.sol";
import "../interfaces/IEMERouter.sol";
import "../interfaces/IEMEFactory.sol";

import "hardhat/console.sol";

contract DonatePool is SafeOwnable, ReentrancyGuard {
    using SafeMath for uint256;
    using SafeERC20 for IERC20;

    uint256 public constant UPGRADE_LOCK_DURATION = 60 * 60 * 24 * 1;

    uint112 public alpha = 10**18; // 1

    uint256 public _totalStakingPower;

    uint256 public constant _superiorRatio = 2 * 10**17; // 0.2

    uint256 public signFee = 20 * 10**18;
    uint256 public stakeMinLimit = 100 * 10**18;
    uint256 public nodeLimit = 20000 * 10**18;

    address public constant hole = 0x000000000000000000000000000000000000dEaD;

    IERC20 public _dragonToken;

    IERC20 public _usdtToken;

    IEMERouter public _swapRouter;

    IEMEFactory private _swapFactory;

    address[] private tokenSupported;

    mapping(address => uint256) private tokenSupportedIndex;

    mapping(address => UserInfo) public userInfo;

    mapping(address => bool) public nodes;

    uint256 public nodeTotal;

    mapping(address => bool) public signAccount;

    mapping(address => address[]) public inviteList;

    address public _dragonTeam;

    mapping(address => LockedToken) public upgradeLockedTokens;

    struct LockedToken {
        uint256 locked;
        uint256 lockTime;
        int256 unlocked;
    }

    struct UserInfo {
        address account;
        uint128 stakingPower;
        uint128 superiorSP;
        address superior;
        uint256 credit;
        uint256 creditDebt;
    }

    event StakeDARGON(address indexed user, uint256 amount, uint256 totalPower);
    event DonateDARGON(address indexed user, uint256 donateAmount);
    event RedeemDARGON(
        address indexed user,
        uint256 amount,
        uint256 totalPower
    );
    event SignPool(address indexed user, uint256 amount);
    event SignExpired(address indexed user);
    event BecomeNode(address indexed user);
    event LoseNode(address indexed user);
    event Withdraw(address indexed user, uint256 amount);
    event BindSuperior(address indexed user, address indexed superior);
    event AddTokenSuppout(address erc20);
    event RemoveTokenSuppout(address erc20);

    constructor(
        IEMERouter swapRouter,
        IERC20 dragonToken,
        IERC20 usdtToken,
        address dragonTeam
    ) {
        _dragonToken = dragonToken;
        _dragonTeam = dragonTeam;
        _usdtToken = usdtToken;
        _swapRouter = swapRouter;
        _swapFactory = IEMEFactory(swapRouter.factory());
    }

    function setNodeLimit(uint256 _nodeLimit) external onlyOwner {
        nodeLimit = _nodeLimit;
    }

    function setSignFee(uint256 _signFee) external onlyOwner {
        signFee = _signFee;
    }

    function addTokenSupport(address token) external onlyOwner {
        require(
            token == address(_usdtToken) ||
                _swapFactory.getPair(token, address(_usdtToken)) != address(0),
            "DonatePool: The usdt trading pair was not found"
        );
        require(
            tokenSupported.length == 0 ||
                tokenSupported[tokenSupportedIndex[token]] != token,
            "DonatePool: Currency already supported"
        );
        tokenSupportedIndex[token] = tokenSupported.length;
        tokenSupported.push(token);

        emit AddTokenSuppout(token);
    }

    function delTokenSupport(address token) external onlyOwner {
        require(
            tokenSupported[tokenSupportedIndex[token]] == token,
            "DonatePool: Not in the support list"
        );

        uint256 delIndex = tokenSupportedIndex[token];
        if (delIndex > tokenSupported.length) {
            tokenSupported[delIndex] = tokenSupported[
                tokenSupported.length - 1
            ];
            tokenSupported.pop();
        }
        delete tokenSupportedIndex[token];

        emit RemoveTokenSuppout(token);
    }

    function signPool(address token, address superiorAddress) external {
        require(!signAccount[msg.sender], "DonatePool: Already signed");
        require(
            tokenSupported.length > 0 &&
                tokenSupported[tokenSupportedIndex[token]] == token,
            "DonatePool: Currency not supported"
        );
        UserInfo storage user = userInfo[msg.sender];
        if (user.account == address(0)) {
            user.account = msg.sender;
        }
        if (user.superior == address(0)) {
            if (superiorAddress == address(0)) {
                superiorAddress = _dragonTeam;
            }
            require(
                superiorAddress == _dragonTeam ||
                    (userInfo[superiorAddress].superior != address(0) &&
                        nodes[superiorAddress]),
                "DonatePool: INVALID_SUPERIOR_ADDRESS"
            );
            user.superior = superiorAddress;
            inviteList[superiorAddress].push(msg.sender);
            emit BindSuperior(msg.sender, superiorAddress);
        }

        signAccount[msg.sender] = true;

        uint256 fee = getSignAmount(token);
        IERC20(token).safeTransferFrom(msg.sender, hole, fee);

        emit SignPool(msg.sender, signFee);
    }

    function nodeAuth() external {
        require(
            isMeetNode(msg.sender),
            "DonatePool: Did not meet the application requirements"
        );
        addNode(msg.sender);
    }

    function addNode(address account) internal {
        nodes[account] = true;
        nodeTotal += 1;
        emit BecomeNode(account);
    }

    function removeNode(address account) internal {
        nodes[account] = false;
        nodeTotal -= 1;
        emit LoseNode(account);
    }

    function isMeetNode(address account) public view returns (bool) {
        UserInfo memory user = userInfo[account];
        uint256 stakingPower = uint256(user.stakingPower).sub(
            DecimalMath.divFloor(user.credit, alpha)
        );
        uint256 amount = DecimalMath.mulFloor(stakingPower, alpha);
        return (amount + 100 >= nodeLimit);
    }

    function stake(uint256 amount) external nonReentrant {
        require(
            amount >= stakeMinLimit,
            "DonatePool: must mint greater than stake limit"
        );
        require(
            signAccount[msg.sender],
            "DonatePool: please sign to open the stake first"
        );
        UserInfo storage user = userInfo[msg.sender];

        _dragonToken.safeTransferFrom(msg.sender, address(this), amount);

        uint256 newStakingPower = DecimalMath.divFloor(amount, alpha);
        _stake(user, newStakingPower);
        emit StakeDARGON(msg.sender, amount, _totalStakingPower);
    }

    function redeem(uint256 value, bool all)
        external
        nonReentrant
        balanceEnough(msg.sender, value)
    {
        UserInfo storage user = userInfo[msg.sender];
        uint256 stakingPower;
        if (nodes[msg.sender]) {
            if (balanceOf(msg.sender) - value < nodeLimit) {
                all = true;
            }
        }
        if (all) {
            stakingPower = uint256(user.stakingPower).sub(
                DecimalMath.divFloor(user.credit, alpha)
            );
            value = DecimalMath.mulFloor(stakingPower, alpha);

            signAccount[msg.sender] = false;
            emit SignExpired(msg.sender);
        } else {
            stakingPower = DecimalMath.divFloor(value, alpha);
            if (
                stakingPower >=
                uint256(user.stakingPower).sub(
                    DecimalMath.divFloor(user.credit, alpha)
                )
            ) {
                signAccount[msg.sender] = false;
                emit SignExpired(msg.sender);
            }
        }

        if (nodes[msg.sender] && balanceOf(msg.sender) - value < nodeLimit) {
            LockedToken storage lt = upgradeLockedTokens[msg.sender];
            uint256 _now = block.timestamp;
            uint256 lock = nodeLimit;
            uint256 transFee;
            if (value < nodeLimit) {
                lock = value;
            } else {
                transFee = value - nodeLimit;
            }
            if (_now < lt.lockTime + UPGRADE_LOCK_DURATION) {
                uint256 amount = (lt.locked * (_now - lt.lockTime)) /
                    UPGRADE_LOCK_DURATION;
                lt.locked = lt.locked - amount + lock;
                lt.unlocked += int256(amount);
            } else {
                lt.unlocked += int256(lt.locked);
                lt.locked = lock;
            }
            lt.lockTime = _now;
            if (transFee != 0) {
                _dragonToken.safeTransfer(msg.sender, transFee);
            }
        } else {
            _dragonToken.safeTransfer(msg.sender, value);
        }
        _redeem(user, stakingPower);

        emit RedeemDARGON(msg.sender, value, _totalStakingPower);
    }

    function withdraw() external {
        LockedToken storage lt = upgradeLockedTokens[msg.sender];
        int256 unlocked = lt.unlocked;
        uint256 _now = block.timestamp;

        if (_now < lt.lockTime + UPGRADE_LOCK_DURATION) {
            unlocked += int256(
                (lt.locked * (_now - lt.lockTime)) / UPGRADE_LOCK_DURATION
            );
        } else {
            unlocked += int256(lt.locked);
        }

        require(unlocked > 0, "no token available");

        lt.unlocked -= unlocked;

        _dragonToken.safeTransfer(msg.sender, uint256(unlocked));

        emit Withdraw(msg.sender, uint256(unlocked));
    }

    function available(address _account) public view returns (uint256) {
        LockedToken memory lt = upgradeLockedTokens[_account];
        int256 unlocked = lt.unlocked;
        uint256 _now = block.timestamp;

        if (_now < lt.lockTime + UPGRADE_LOCK_DURATION) {
            unlocked += int256(
                (lt.locked * (_now - lt.lockTime)) / UPGRADE_LOCK_DURATION
            );
        } else {
            unlocked += int256(lt.locked);
        }
        return uint256(unlocked);
    }

    function donate(uint256 amount) external nonReentrant {
        _dragonToken.safeTransferFrom(msg.sender, address(this), amount);

        alpha = uint112(
            uint256(alpha).add(DecimalMath.divFloor(amount, _totalStakingPower))
        );
        emit DonateDARGON(msg.sender, amount);
    }

    function _stake(UserInfo storage to, uint256 stakingPower) internal {
        require(stakingPower <= uint128(-1), "OVERFLOW");

        if (nodes[to.superior]) {
            UserInfo storage superior = userInfo[to.superior];
            uint256 superiorIncreSP = DecimalMath.mulFloor(
                stakingPower,
                _superiorRatio
            );
            uint256 superiorIncreCredit = DecimalMath.mulFloor(
                superiorIncreSP,
                alpha
            );
            to.superiorSP = uint128(
                uint256(to.superiorSP).add(superiorIncreSP)
            );
            superior.stakingPower = uint128(
                uint256(superior.stakingPower).add(superiorIncreSP)
            );
            superior.credit = uint128(
                uint256(superior.credit).add(superiorIncreCredit)
            );

            _totalStakingPower = _totalStakingPower.add(superiorIncreSP);
        }

        to.stakingPower = uint128(uint256(to.stakingPower).add(stakingPower));

        _totalStakingPower = _totalStakingPower.add(stakingPower);
    }

    function _redeem(UserInfo storage from, uint256 stakingPower) internal {
        from.stakingPower = uint128(
            uint256(from.stakingPower).sub(stakingPower)
        );

        if (nodes[msg.sender] && !isMeetNode(msg.sender)) {
            uint256 userCreditSP = DecimalMath.divFloor(from.credit, alpha);
            if (from.stakingPower > userCreditSP) {
                from.stakingPower = uint128(
                    uint256(from.stakingPower).sub(userCreditSP)
                );
            } else {
                userCreditSP = from.stakingPower;
                from.stakingPower = 0;
            }
            from.creditDebt = from.creditDebt.add(from.credit);
            from.credit = 0;

            _totalStakingPower = _totalStakingPower.sub(userCreditSP);

            removeNode(msg.sender);
        }

        uint256 superiorDecreSP = DecimalMath.mulFloor(
            stakingPower,
            _superiorRatio
        );
        superiorDecreSP = from.superiorSP <= superiorDecreSP
            ? from.superiorSP
            : superiorDecreSP;
        from.superiorSP = uint128(
            uint256(from.superiorSP).sub(superiorDecreSP)
        );
        uint256 superiorDecreCredit = DecimalMath.mulFloor(
            superiorDecreSP,
            alpha
        );

        UserInfo storage superior = userInfo[from.superior];
        if (superiorDecreCredit > superior.creditDebt) {
            uint256 dec = DecimalMath.divFloor(superior.creditDebt, alpha);
            superiorDecreSP = dec >= superiorDecreSP
                ? 0
                : superiorDecreSP.sub(dec);
            superiorDecreCredit = superiorDecreCredit.sub(superior.creditDebt);
            superior.creditDebt = 0;
        } else {
            superior.creditDebt = superior.creditDebt.sub(superiorDecreCredit);
            superiorDecreCredit = 0;
            superiorDecreSP = 0;
        }
        uint256 creditSP = DecimalMath.divFloor(superior.credit, alpha);

        if (superiorDecreSP >= creditSP) {
            superior.credit = 0;
            superior.stakingPower = uint128(
                uint256(superior.stakingPower).sub(creditSP)
            );
            _totalStakingPower = _totalStakingPower.sub(creditSP);
        } else {
            superior.credit = uint128(
                uint256(superior.credit).sub(superiorDecreCredit)
            );
            superior.stakingPower = uint128(
                uint256(superior.stakingPower).sub(superiorDecreSP)
            );
            _totalStakingPower = _totalStakingPower.sub(superiorDecreSP);
        }

        _totalStakingPower = _totalStakingPower.sub(stakingPower);
    }

    function getSignAmount(address token) public view returns (uint256) {
        if (token == address(_usdtToken)) {
            return signFee;
        }

        address[] memory path = new address[](2);
        path[0] = address(_usdtToken);
        path[1] = token;
        uint256[] memory amounts = _swapRouter.getAmountsOut(
            1000000000000000000,
            path
        );
        return (amounts[1] * signFee) / 1000000000000000000;
    }

    modifier balanceEnough(address account, uint256 amount) {
        require(
            balanceOf(account) >= amount,
            "DonatePool: available amount not enough"
        );
        _;
    }

    function balanceOf(address account) public view returns (uint256 amount) {
        UserInfo memory user = userInfo[account];

        if (user.stakingPower < DecimalMath.divFloor(user.credit, alpha)) {
            amount = 0;
        } else {
            uint256 stakingPower = uint256(user.stakingPower).sub(
                DecimalMath.divFloor(user.credit, alpha)
            );
            amount = DecimalMath.mulFloor(stakingPower, alpha);
        }
    }

    function inviteUsers(address account)
        public
        view
        returns (UserInfo[] memory)
    {
        UserInfo[] memory users = new UserInfo[](inviteList[account].length);
        for (uint256 i = 0; i != inviteList[account].length; i++) {
            users[i] = userInfo[inviteList[account][i]];
        }
        return users;
    }

    struct CurrencyInfo {
        address token;
        uint256 decmails;
        string symbol;
        uint256 balance;
        uint256 allowance;
        uint256 signFee;
    }

    function supportedCurrencies(address account)
        public
        view
        returns (CurrencyInfo[] memory)
    {
        CurrencyInfo[] memory currencies = new CurrencyInfo[](
            tokenSupported.length
        );
        for (uint256 i = 0; i != currencies.length; i++) {
            ERC20 token = ERC20(tokenSupported[i]);
            currencies[i].token = tokenSupported[i];
            currencies[i].symbol = token.symbol();
            currencies[i].balance = token.balanceOf(account);
            currencies[i].allowance = token.allowance(account, address(this));
            currencies[i].decmails = token.decimals();
            currencies[i].signFee = getSignAmount(tokenSupported[i]);
        }
        return currencies;
    }
}
"
