    "contracts/token/DragonToken.sol": {
      "content": "// SPDX-License-Identifier: MIT

pragma solidity >=0.7.6;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

import "../interfaces/IEMERouter.sol";
import "../interfaces/IEMEFactory.sol";
import "../core/SafeOwnable.sol";
import "../pool/DonatePool.sol";
import "hardhat/console.sol";

contract DragonToken is IERC20, SafeOwnable {
    string public name;
    string public symbol;
    uint8 public decimals;

    uint256 public override totalSupply;
    uint256 public remainedSupply;

    mapping(address => uint256) public override balanceOf;
    mapping(address => mapping(address => uint256)) public override allowance;

    address public swapV2Pair;

    address public constant hole = 0x000000000000000000000000000000000000dEaD;
    address public operator;

    mapping(address => bool) public _isExcludedFrom;
    DonatePool public _donatePool;

    uint256 public BASE_RATIO = 1 * 10**6;
    uint256 public swapDargonForTokenBurnFee = (3 * BASE_RATIO) / 100;
    uint256 public swapDargonForTokenTeamFee = (1 * BASE_RATIO) / 100;
    uint256 public swapDargonForTokenRewardFee = (6 * BASE_RATIO) / 100;

    uint256 public swapTokenForDargonBurnFee = (1 * BASE_RATIO) / 100;
    uint256 public swapTokenForDargonTeamFee = (1 * BASE_RATIO) / 100;
    uint256 public swapTokenForDargonRewardFee = (3 * BASE_RATIO) / 100;

    constructor(
        string memory _name,
        string memory _symbol,
        uint8 _decimals,
        uint256 _maxSupply,
        address _operator,
        IEMERouter _swapRouter,
        address token
    ) {
        name = _name;
        symbol = _symbol;
        decimals = _decimals;
        remainedSupply = _maxSupply;
        operator = _operator;

        swapV2Pair = IEMEFactory(_swapRouter.factory()).createPair(
            address(this),
            address(token)
        );

        _isExcludedFrom[msg.sender] = true;
    }

    function setSwapDargonForTokenBurnFee(uint256 burnFee) external onlyOwner {
        swapDargonForTokenBurnFee = burnFee;
    }

    function setSwapDargonForTokenTeamFee(uint256 teamFee) external onlyOwner {
        swapDargonForTokenTeamFee = teamFee;
    }

    function setSwapDargonForTokenRewardFee(uint256 rewardFee)
        external
        onlyOwner
    {
        swapDargonForTokenRewardFee = rewardFee;
    }

    function setSwapTokenForDargonBurnFee(uint256 burnFee) external onlyOwner {
        swapTokenForDargonBurnFee = burnFee;
    }

    function setSwapTokenForDargonTeamFee(uint256 teamFee) external onlyOwner {
        swapTokenForDargonTeamFee = teamFee;
    }

    function setSwapTokenForDargonRewardFee(uint256 rewardFee)
        external
        onlyOwner
    {
        swapTokenForDargonRewardFee = rewardFee;
    }

    function setDonatePool(DonatePool donatePool) external onlyOwner {
        _donatePool = donatePool;
    }

    function excludeFrom(address account) external onlyOwner {
        _isExcludedFrom[account] = true;
    }

    function includeInFrom(address account) external onlyOwner {
        _isExcludedFrom[account] = false;
    }

    function setOperator(address _operator) external onlyOwner {
        require(_operator != address(0), "zero address");
        operator = _operator;
    }

    function mint(address to, uint256 amount) external onlyOwner {
        require(to != address(0), "zero address");
        require(remainedSupply >= amount, "mint too much");

        remainedSupply -= amount;
        totalSupply += amount;
        balanceOf[to] += amount;

        emit Transfer(address(0), to, amount);
    }

    function donate() external {
        require(address(_donatePool) != address(0), "pool address is zero");
        uint256 rewardFee = balanceOf[address(this)];
        allowance[address(this)][address(_donatePool)] = rewardFee;
        emit Approval(address(this), address(_donatePool), rewardFee);
        _donatePool.donate(rewardFee);
    }

    function _transfer(
        address from,
        address to,
        uint256 amount
    ) private CheckBurnTotal {
        require(to != address(0), "zero address");
        require(balanceOf[from] >= amount, "balance not enough");
        balanceOf[from] -= amount;
        uint256 transFee;
        uint256 burnFee;
        uint256 teamFee;
        uint256 rewardFee;
        if (from == address(swapV2Pair) && !_isExcludedFrom[to]) {
            (
                burnFee,
                teamFee,
                rewardFee,
                transFee
            ) = getSwapTokenForDargonValue(amount);
        } else if (to == address(swapV2Pair) && !_isExcludedFrom[from]) {
            (
                burnFee,
                teamFee,
                rewardFee,
                transFee
            ) = getSwapDargonForTokenValue(amount);
        } else {
            balanceOf[to] += amount;
            emit Transfer(from, to, amount);
            return;
        }
        if (teamFee > 0) {
            balanceOf[operator] += teamFee;
            emit Transfer(from, operator, teamFee);
        }
        if (burnFee > 0) {
            balanceOf[hole] += burnFee;
            emit Transfer(from, hole, burnFee);
        }
        if (rewardFee > 0) {
            balanceOf[address(this)] += rewardFee;
            emit Transfer(from, address(this), rewardFee);

            if (address(_donatePool) != address(0)) {
                allowance[address(this)][address(_donatePool)] = rewardFee;
                emit Approval(address(this), address(_donatePool), rewardFee);
                _donatePool.donate(rewardFee);
            }
        }

        balanceOf[to] += transFee;
        emit Transfer(from, to, transFee);
    }

    function getSwapTokenForDargonValue(uint256 amount)
        private
        view
        returns (
            uint256,
            uint256,
            uint256,
            uint256
        )
    {
        uint256 burnFee = (amount * swapTokenForDargonBurnFee) / BASE_RATIO;
        uint256 teamFee = (amount * swapTokenForDargonTeamFee) / BASE_RATIO;
        uint256 rewardFee = (amount * swapTokenForDargonRewardFee) / BASE_RATIO;
        uint256 transferFee = amount - burnFee - teamFee - rewardFee;
        return (burnFee, teamFee, rewardFee, transferFee);
    }

    function getSwapDargonForTokenValue(uint256 amount)
        private
        view
        returns (
            uint256,
            uint256,
            uint256,
            uint256
        )
    {
        uint256 burnFee = (amount * swapDargonForTokenBurnFee) / BASE_RATIO;
        uint256 teamFee = (amount * swapDargonForTokenTeamFee) / BASE_RATIO;
        uint256 rewardFee = (amount * swapDargonForTokenRewardFee) / BASE_RATIO;
        uint256 transferFee = amount - burnFee - teamFee - rewardFee;
        return (burnFee, teamFee, rewardFee, transferFee);
    }

    function transfer(address to, uint256 amount)
        external
        override
        returns (bool)
    {
        _transfer(msg.sender, to, amount);
        return true;
    }

    function transferFrom(
        address from,
        address to,
        uint256 amount
    ) external override returns (bool) {
        require(allowance[from][msg.sender] >= amount, "allowance not enough");

        allowance[from][msg.sender] -= amount;
        _transfer(from, to, amount);

        return true;
    }

    function approve(address spender, uint256 amount)
        external
        override
        returns (bool)
    {
        require(spender != address(0), "zero address");

        allowance[msg.sender][spender] = amount;
        emit Approval(msg.sender, spender, amount);

        return true;
    }

    modifier CheckBurnTotal() {
        if (totalSupply - balanceOf[hole] <= 2100000 * (10**decimals)) {
            swapDargonForTokenBurnFee = 0;
            swapTokenForDargonBurnFee = 0;
        }
        _;
    }
}
"
    },
