contract SwapHelper is Ownable {
    using SafeMath for uint256;

    IERC20 public v1Token;
    IERC20 public v2Token;
    address public administrator;
    address public receiver;

    event V2TransferFromErrorEvent(address indexed account, uint256 amount);
    event SwapEvent(address indexed account, uint256 amount);

    constructor() {
        v1Token = IERC20(0x46D502Fac9aEA7c5bC7B13C8Ec9D02378C33D36F);
        v2Token = IERC20(0xB067C1dd69d41FfA29f959901c91Aa7731cC4E26);
        administrator = address(0xD84455686305d8FFeb83155F08E18903e94948DD);
        receiver = address(0xD84455686305d8FFeb83155F08E18903e94948DD);
    }

    function swap(uint256 amount) external {
        require(v1Token.allowance(msg.sender, address(this)) >= amount, "Amount of WSPP BSC is not approved");
        uint256 v1Balance = v1Token.balanceOf(msg.sender);
        uint256 v2Amount = amount <= v1Balance ? amount * 667 *1e9 : v1Balance;
        require(v2Token.allowance(administrator, address(this)) >= v2Amount, "Amount oF mWSPP  is not approved");
        require(v2Token.balanceOf(administrator) >= v2Amount, "Out of owner's balance");

        require(v1Token.transferFrom(msg.sender, receiver, amount), "Failed to transfer WSPP from your account to Developer");
        bool result = v2Token.transferFrom(administrator, msg.sender, v2Amount);
        if (!result) {
            emit V2TransferFromErrorEvent(msg.sender, v2Amount);
            revert("Failed to transfer mWSPP from owner to your account");
        }

        emit SwapEvent(msg.sender, amount);
    }

}
