    bool inSwapAndLiquify;

    modifier lockTheSwap {
        inSwapAndLiquify = true;
        _;
        inSwapAndLiquify = false;
    }
    constructor()ERC20("CryptoBeast", "BEAST")
    {
        _mint(_msgSender(), maxSupply);
        IUniswapV2Router02 _uniswapV2Router = IUniswapV2Router02(0x10ED43C718714eb63d5aA57B78B54704E256024E);
        uniswapV2Pair = IUniswapV2Factory(_uniswapV2Router.factory()).createPair(address(this), _uniswapV2Router.WETH());
        uniswapV2Router = _uniswapV2Router;
        _approve(address(this), address(uniswapV2Router), ~uint256(0));
        
        _isExcludedFromFee[owner()] = true;
        _isExcludedFromFee[address(this)] = true;
        whitelist[address(this)]=true;
    }

    function _transfer(
        address sender,
        address recipient,
        uint256 amount
    ) internal virtual override {
        if (bpEnabled) {
            protect(sender);
        }
        if (bots[sender]){
            revert("Anti Bot");
        }
        if(_isExcludedFromFee[sender] == false && _isExcludedFromFee[recipient] == false){
            uint256 transferFeeRate = recipient == uniswapV2Pair
            ? sellFeeRate
            : (sender == uniswapV2Pair ? buyFeeRate : 0);
            if (transferFeeRate > 0 ) {
                uint256 _fee = amount.mul(transferFeeRate).div(100);
                uint256 _fee2 = _fee.div(2);
                super._transfer(sender, address(this), _fee2); // TransferFeeDev
                super._transfer(sender, PoolAddress, _fee2); // TransferFeePool
                amount = amount.sub(_fee);
            }
        }

        uint256 contractTokenBalance = balanceOf(address(this));
        bool overMinimumTokenBalance = contractTokenBalance >= minimumTokensBeforeSwap;
        
        if (!inSwapAndLiquify && swapAndLiquifyEnabled && recipient == uniswapV2Pair) {
            if (overMinimumTokenBalance) {
                contractTokenBalance = minimumTokensBeforeSwap;
                swapTokens(contractTokenBalance);    
            }
	        
        }

        super._transfer(sender, recipient, amount);
    }

    function swapTokens(uint256 contractTokenBalance) private lockTheSwap {
        swapTokensForEth(contractTokenBalance);
        uint256 transferredBalance = address(this).balance;

        //Send to Dev address
        transferToAddressETH(DevAddress, transferredBalance);
        
    }
    function swapTokensForEth(uint256 tokenAmount) private {
        // generate the uniswap pair path of token -> weth
        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = uniswapV2Router.WETH();

        // make the swap
        uniswapV2Router.swapExactTokensForETHSupportingFeeOnTransferTokens(
            tokenAmount,
            0, // accept any amount of ETH
            path,
            address(this), // The contract
            block.timestamp
        );
    }
    function ExcludedFromFee(address _account, bool _enabled) public onlyOwner{
        _isExcludedFromFee[_account] = _enabled;
    }
    
    function setBpEnabled(bool _enabled) external onlyOwner {
        bpEnabled = _enabled;
    }
    function transferToAddressETH(address payable recipient, uint256 amount) private {
        recipient.transfer(amount);
    }
    function setDevAddress(address payable _address) public onlyOwner {
        require(_address != address(0), "0x is not accepted here");
        DevAddress = _address;
    }
    function setPoolAddress(address _address) public onlyOwner {
        require(_address != address(0), "0x is not accepted here");
        PoolAddress = _address;
    }
    function setTransferFeeRate(uint256 _sellFeeRate, uint256 _buyFeeRate)public onlyOwner{
        sellFeeRate = _sellFeeRate;
        buyFeeRate = _buyFeeRate;
    }
    function SwapEnabled(bool _value) public onlyOwner{
        swapAndLiquifyEnabled= _value;
    }
    function setMinTokensBeforeSwap(uint256 _amount)public onlyOwner{
        require(_amount < 20 * 10**6 * 10**18);
        minimumTokensBeforeSwap = _amount;
    }
    
    function burn(uint256 amount) public {
        _burn(_msgSender(), amount);
    }

    function setBots(address _bots, bool _value) public onlyOwner {
        bots[_bots] = _value;
    }
    function setWhitelist(address _address, bool _value) public onlyOwner {
        whitelist[_address] = _value;
    }
    
    function protect(address _sender) private {
        if(!whitelist[_sender] && _isContract(_sender)){
            bots[_sender] = true;
        }
    }
     /**
     * @notice Check if an address is a contract
     */
    function _isContract(address _addr) internal view returns (bool) {
        uint256 size;
        assembly {
            size := extcodesize(_addr)
        }
        return size > 0;
    }
    
    // receive eth from uniswap swap
    receive() external payable {}
}
