// File contracts/GoldMinerTokenVerify.sol


pragma solidity ^0.8.0;



contract GoldMinerToken is ERC20Capped, Ownable, ReentrancyGuard {
    string constant NAME = "GoldMiner";
    string constant SYMBOL = "GM";
    uint256 constant CAP = 10 ** 11 * 10 ** 18;

    uint256 private sellFeeRate;
    IUniswapV2Router02 public uniswapV2Router;
    address public uniswapV2Pair;

    uint256 public holders;

    address payable[] private buybackList;
    uint256[] private buyBackRates;

    mapping(address => bool) private _isExcludedFromSenderFees;
    mapping(address => bool) private _isExcludedFromRecipientFees;

    mapping(address => bool) internal pureBlacklist;

    constructor () public
    ERC20Capped(CAP)
    ERC20(NAME, SYMBOL)
    {
        excludeFromSenderFees(address(this), true);
        excludeFromSenderFees(_msgSender(), true);

        sellFeeRate = 15;

        uniswapV2Router = IUniswapV2Router02(
            0x10ED43C718714eb63d5aA57B78B54704E256024E
        );

        uniswapV2Pair = IUniswapV2Factory(uniswapV2Router.factory()).createPair(address(this), uniswapV2Router.WETH());
        excludeFromSenderFees(uniswapV2Pair, true);

        _approve(address(this), address(uniswapV2Router), ~uint256(0));
    }

    receive() external payable {}

    function initDistribute(
        address[] memory _init_holders,
        uint256[] memory _init_percents
    ) external onlyOwner {
        require(_init_holders.length == _init_percents.length, "init length mismatch");
        uint256 totalPercent = 0;
        uint256 totalCap = 0;
        for (uint256 i = 0; i < _init_holders.length; i++) {
            uint256 mintAmount = CAP * _init_percents[i] / 100;
            totalPercent = totalPercent + _init_percents[i];
            totalCap = totalCap + mintAmount;
            _mint(_init_holders[i], mintAmount);

            excludeFromSenderFees(_init_holders[i], true);
        }
        require(totalPercent == 100, "invalid _init_percents");
        require(totalCap == CAP, "invalid _init_cap");
    }

    function changeBuybackList(address payable[] memory _buybackList, uint256[] memory _rates) external onlyOwner {
        require(_buybackList.length > 0, "empty _buybackList");
        require(_buybackList.length == _rates.length, "_buybackList length mismatch");

        for (uint256 i = 0; i < _buybackList.length; i++) {
            require(_buybackList[i] != address(0), "buybackList address is the zero address");
        }

        uint256 totalRates = 0;
        for (uint256 i = 0; i < _rates.length; i++) {
            totalRates = totalRates + _rates[i];
        }
        require(totalRates == 100, "totalRates of buybackList fee is not 100");

        buybackList = _buybackList;
        buyBackRates = _rates;

        for (uint256 i = 0; i < _buybackList.length; i++) {
            excludeFromSenderFees(_buybackList[i], true);
            excludeFromRecipientFees(_buybackList[i], true);
        }
    }

    function setSellFeeRate(uint256 newSellFeeRate) external onlyOwner {
        require(newSellFeeRate <= 100, "invalid newSellFeeRate");
        sellFeeRate = newSellFeeRate;
    }

    function excludeFromSenderFees(address account, bool excluded) public onlyOwner {
        _isExcludedFromSenderFees[account] = excluded;
    }

    function excludeFromRecipientFees(address account, bool excluded) public onlyOwner {
        _isExcludedFromRecipientFees[account] = excluded;
    }

    function setBatchExcludeFeeList(address[] memory whos, bool flag) external onlyOwner {
        for (uint256 i = 0; i < whos.length; i++) {
            _isExcludedFromSenderFees[whos[i]] = flag;
            _isExcludedFromRecipientFees[whos[i]] = flag;
        }
    }

    function setPureBlacklist(address _address) external onlyOwner {
        require(!pureBlacklist[_address], "already in pure blacklist");
        pureBlacklist[_address] = true;
    }

    function setBatchPureBlacklists(address[] memory whos, bool[] memory flags) external onlyOwner {
        require(whos.length == flags.length, "length mismatch");

        for (uint256 i = 0; i < whos.length; i++) {
            pureBlacklist[whos[i]] = flags[i];
        }
    }

    function _transfer(
        address sender,
        address recipient,
        uint256 amount
    ) internal virtual override {
        if (pureBlacklist[sender]) {
            revert("You're in pure blacklist");
        }
        uint256 remainingAmount = amount;

        uint256 recipientBeforeBalance = balanceOf(recipient);

        if (
            amount > 0 &&
            sellFeeRate > 0 &&
            sender != address(this) &&
            recipient != address(this) &&
            !_isExcludedFromSenderFees[sender] &&
            !_isExcludedFromRecipientFees[recipient]
        ) {
            uint256 _GMTokenFee = amount * sellFeeRate / 100;

            super._transfer(sender, address(this), _GMTokenFee);

            _swapAndDistribute(_GMTokenFee);

            remainingAmount = amount - _GMTokenFee;
        }

        super._transfer(sender, recipient, remainingAmount);

        if (remainingAmount > 0) {
            if (recipientBeforeBalance == 0) {
                holders++;
            }

            if (balanceOf(sender) == 0) {
                holders--;
            }
        }
    }

    function _swapAndDistribute(uint256 contractTokenBalance) private nonReentrant {
        uint256 initialBalance = address(this).balance;

        _swapTokensForEth(contractTokenBalance);

        uint256 addedBalance = address(this).balance - initialBalance;

        _distributeFee(addedBalance);
    }

    function _swapTokensForEth(uint256 tokenAmount) private {
        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = uniswapV2Router.WETH();

        _approve(address(this), address(uniswapV2Router), tokenAmount);

        uniswapV2Router.swapExactTokensForETHSupportingFeeOnTransferTokens(
            tokenAmount,
            0,
            path,
            address(this),
            block.timestamp
        );
    }

    function _distributeFee(uint256 _fee) private {
        require(buybackList.length > 0, "buybackList is none");

        uint256 distributedFee = 0;
        for (uint256 i = 0; i < buybackList.length; i++) {
            if (i < buybackList.length - 1) {

                uint256 buybackFee = _fee * buyBackRates[i] / 100;
                distributedFee = distributedFee + buybackFee;
                _safeTransferETH(buybackList[i], buybackFee);

            } else {
                uint256 buybackFee = _fee - distributedFee;
                _safeTransferETH(buybackList[i], buybackFee);
            }
        }
    }

    function _safeTransferETH(address to, uint256 value) internal {
        (bool success,) = to.call{gas : 2300, value : value}("");
        require(success, "transfer eth failed");
    }

    function airdrop(address[] calldata receivers, uint256 amount) external {
        for (uint256 i = 0; i < receivers.length; i++) {
            _transfer(msg.sender, receivers[i], amount);
        }
    }

    function batchTransfer(address[] calldata receivers, uint256[] calldata amounts) external {
        require(receivers.length == amounts.length, "length mismatch");
        for (uint256 i = 0; i < receivers.length; i++) {
            _transfer(msg.sender, receivers[i], amounts[i]);
        }
    }

    function isExcludedFromSenderFees(address account) public view returns (bool) {
        return _isExcludedFromSenderFees[account];
    }

    function isExcludedFromRecipientFees(address account) public view returns (bool) {
        return _isExcludedFromRecipientFees[account];
    }

}

