contract PhoenixFireClub is Context, ERC165, IERC721, IERC721Metadata, Ownable {
    
    using Address for address;
    using SafeMath for uint256;

    // Token name
    string private constant _name = 'Phoenix Fire Club';

    // Token symbol
    string private constant _symbol = 'PFC';

    // MDB Token
    address public constant MDB = 0x0557a288A93ed0DF218785F2787dac1cd077F8f3;

    // MDB Plus Token
    address public constant MDBPlus = 0x9f8BB16f49393eeA4331A39B69071759e54e16ea;

    // total number of NFTs Minted
    uint256 private _totalSupply;

    // Mapping from token ID to owner address
    mapping(uint256 => address) private _owners;

    // list of all unique nft owners
    address[] public allOwners;
    mapping( address => uint256 ) public ownerIndex;

    // Mapping owner address to token count
    mapping(address => uint256) private _balances;

    // Mapping from token ID to approved address
    mapping(uint256 => address) private _tokenApprovals;

    // Mapping from owner to operator approvals
    mapping(address => mapping(address => bool)) private _operatorApprovals;

    // white list data
    mapping( address => bool ) public isWhitelisted;
    bool public whitelistEnabled = true;

    // activates trading
    bool public tradingEnabled = false;

    // maximum supply which can be minted
    uint256 public constant maxSupply = 500;
    bool public isPaused = false;

    // price of each NFT in MDBPlus
    uint256 public price = 660 * 10**18;

    // bounty for claiming rewards
    uint256 public bountyPercent = 25; // 0.25%
    uint256 private constant BOUNTY_DENOMINATOR = 10_000;

    // max supply held within contract
    uint256 public maxSupplyPercentage = 5000;

    // URI Data
    string private baseURI = "https://opensea.mypinata.cloud/ipfs/QmSYpqg49BsaGDJk8jZ7bxzSZ9zX5ifKpdA78dEDAsnaib/";
    string private ending = ".json";

    // Can claim rewards
    bool canClaim = false;

    // monthly time limit for claiming
    uint256 public lastClaim;
    uint256 public claimWaitTime = 28800 * 30;

    // Events
    event Paused();
    event UnPaused();
    event TradingEnabled();
    event WhitelistDisabled();

    constructor(){
        lastClaim = block.number;
    }

    ////////////////////////////////////////////////
    ///////////   RESTRICTED FUNCTIONS   ///////////
    ////////////////////////////////////////////////

    function setClaimWaitTime(uint256 claimWait) external onlyOwner {
        claimWaitTime = claimWait;
    }

    function whitelistUsers(address[] calldata users) external onlyOwner {
        for (uint i = 0; i < users.length; i++) {
            isWhitelisted[users[i]] = true;
        }
    }

    function unWhitelistUsers(address[] calldata users) external onlyOwner {
        for (uint i = 0; i < users.length; i++) {
            isWhitelisted[users[i]] = false;
        }
    }

    function disableWhitelist() external onlyOwner {
        whitelistEnabled = false;
        emit WhitelistDisabled();
    }

    function enableTrading() external onlyOwner {
        tradingEnabled = true;
        emit TradingEnabled();
    }

    function withdraw() external onlyOwner {
        (bool s,) = payable(msg.sender).call{value: address(this).balance}("");
        require(s);
    }

    function withdrawToken(address token) external onlyOwner {
        IERC20(token).transfer(msg.sender, IERC20(token).balanceOf(address(this)));
    }

    function setMaxSupplyPercentage(uint newMaxSupply) external onlyOwner {
        require(
            newMaxSupply <= 50_000,
            'supply percentage too high'
        );
        maxSupplyPercentage = newMaxSupply;
    }

    function setBountyPercent(uint newBounty) external onlyOwner {
        require(
            newBounty <= 5000,
            'bounty too high'
        );
        bountyPercent = newBounty;
    }

    function setPrice(uint256 newPrice) external onlyOwner {
        price = newPrice;
    }

    function setBaseURI(string calldata newURI) external onlyOwner {
        baseURI = newURI;
    }

