    function pendingRewards() public view returns (uint256) {

        uint bal = MDBInContract();
        uint expectedBalance = ( IERC20(MDB).totalSupply() * maxSupplyPercentage ) / 100_000;

        if (bal < expectedBalance) {
            return 0;
        }

        return bal - expectedBalance;
    }

    function pendingForUser(address user) external view returns (uint256) {
        return ( pendingRewards() * _balances[user] ) / _totalSupply;
    }

    function MDBInContract() public view returns (uint256) {
        return IERC20(MDB).balanceOf(address(this));
    }

    function currentBounty() external view returns (uint256) {
        return ( pendingRewards() * bountyPercent ) / BOUNTY_DENOMINATOR;
    }

    function fetchTokenIDsForUser(address user) external view returns (uint256[] memory) {
        uint balance = _balances[user];
        uint256[] memory arr = new uint256[](balance);
        if (balance <= 0) {
            return arr;
        }
        uint count = 0;
        for (uint i = 0; i < _totalSupply;) {
            if (_owners[i] == user) {
                arr[count] = i;
                count++;
            }
            unchecked { ++i; }
        }
        return arr;
    }

