contract StrategyCommonChefLP is StratManager, MaxiFeeManager {
    using SafeERC20 for IERC20;
    using SafeMath for uint256;

    // Tokens used
    address constant public lp = address(0x6eF1dE5cB0ce29EdaFb361E5B86948A2aaFcF31C); // ZCR-BNB
    address constant public pinksale = address(0x407993575c91ce7643a4d4cCACc9A98c36eE1BBE);
    address constant public wbnb = address(0xbb4CdB9CBd36B01bD1cBaEBF2De08d9173bc095c);
    // address constant public wbnb = address(0xae13d989daC2f0dEbFf460aC112a837C89BAa7cd);
    address constant public lockaddress = address(0x6f287eCBBbCE7042187d388db663b036fc67b5B0);


    address public want;
    address public output;

    // Third party contracts
    address public rewardPool;

    // Routes
    // address[] public outputToWantRoute;
    address[] public outputToBnbRoute;
    /**
     * @dev Event that is fired each time someone harvests the strat.
     */
    event StratHarvest(address indexed harvester);
    event LockLiquidity(uint256 id);

    constructor(
        address _want,
        address _output,
        address _rewardPool,
        address _vault,
        address _unirouter,
        address _keeper,
        address _beefyFeeRecipient

    ) StratManager(_keeper, _unirouter, _vault, _beefyFeeRecipient) public {
        want = _want;
        output = _output;
        rewardPool = _rewardPool;

        // outputToWantRoute = [output, eth, want];
        // outputToWantRoute = [output, want];
        outputToBnbRoute = [output, wbnb];

        _giveAllowances();
    }

    // puts the funds to work
    function deposit() public whenNotPaused {
        uint256 wantBal = IERC20(want).balanceOf(address(this));

        if (wantBal > 0) {
            IRewardPool(rewardPool).stake(wantBal);
        }
    }


    function withdraw(uint256 _amount) external {
        require(msg.sender == vault, "!vault");

        uint256 wantBal = IERC20(want).balanceOf(address(this));

        if (wantBal < _amount) {
            IRewardPool(rewardPool).withdraw(_amount.sub(wantBal));
            wantBal = IERC20(want).balanceOf(address(this));
        }

        if (wantBal > _amount) {
            wantBal = _amount;
        }

        if (tx.origin == owner() || paused()) {
            IERC20(want).safeTransfer(vault, wantBal);
        } else {
            uint256 withdrawalFeeAmount = wantBal.mul(withdrawalFee).div(WITHDRAWAL_MAX);
            IERC20(want).safeTransfer(vault, wantBal.sub(withdrawalFeeAmount));
        }
    }


    // compounds earnings and charges performance fee
    function harvest() external whenNotPaused onlyEOA {
        IRewardPool(rewardPool).getReward();
        addLiquidity();
        deposit();
        emit StratHarvest(msg.sender);
    }

    function addLiquidity() internal {

        // FEE TO PAY COMPOUND EXEC
        uint256 toBNBfee = IERC20(output).balanceOf(address(this)).mul(statFee).div(MAX_STAT_FEE);
        IPancakeRouter01(unirouter).swapExactTokensForETH(toBNBfee, 0, outputToBnbRoute, beefyFeeRecipient, now);

        // GENERATE LIQUIDITY
        uint256 toBNB = IERC20(output).balanceOf(address(this)).mul(5000).div(10000);
        IPancakeRouter01(unirouter).swapExactTokensForETH(toBNB, 0, outputToBnbRoute, address(this), now);
        uint256 _liquidity = IERC20(output).balanceOf(address(this));

        // ADD TO LIQUIDITY
        IPancakeRouter01(unirouter).addLiquidityETH{value: address(this).balance}(
            output,
            _liquidity,
            0, // slippage is unavoidable
            0, // slippage is unavoidable
            address(this),
            block.timestamp + 360
        );

        // FEE LOCK
        uint256 toLock = IERC20(want).balanceOf(address(this)).mul(liquidityFee).div(MAX_CALL_FEE);
        IERC20(want).transfer(lockaddress, toLock);
    }

    // calculate the total underlaying 'want' held by the strat.
    function balanceOf() public view returns (uint256) {
        return balanceOfWant().add(balanceOfPool());
    }

    // it calculates how much 'want' this contract holds.
    function balanceOfWant() public view returns (uint256) {
        return IERC20(want).balanceOf(address(this));
    }

    // it calculates how much 'want' the strategy has working in the farm.
    function balanceOfPool() public view returns (uint256) {
        return IRewardPool(rewardPool).balanceOf(address(this));
    }

    // called as part of strat migration. Sends all the available funds back to the vault.
    function retireStrat() external {
        require(msg.sender == vault, "!vault");

        IRewardPool(rewardPool).withdraw(balanceOfPool());

        uint256 wantBal = IERC20(want).balanceOf(address(this));
        IERC20(want).transfer(vault, wantBal);
    }

    // pauses deposits and withdraws all funds from third party systems.
    function panic() public onlyManager {
        pause();
        IRewardPool(rewardPool).withdraw(balanceOfPool());
    }

    function pause() public onlyManager {
        _pause();

        _removeAllowances();
    }

    function unpause() external onlyManager {
        _unpause();

        _giveAllowances();

        deposit();
    }

    function _giveAllowances() internal {
        IERC20(want).safeApprove(rewardPool, uint256(-1));
        IERC20(output).safeApprove(rewardPool, uint256(-1));
        IERC20(wbnb).safeApprove(rewardPool, uint256(-1));

        IERC20(want).safeApprove(unirouter, uint256(-1));
        IERC20(output).safeApprove(unirouter, uint256(-1));
        IERC20(wbnb).safeApprove(unirouter, uint256(-1));

        IERC20(want).safeApprove(pinksale, uint256(-1));
        IERC20(output).safeApprove(pinksale, uint256(-1));
        IERC20(wbnb).safeApprove(pinksale, uint256(-1));
    }

    function _removeAllowances() internal {
        IERC20(want).safeApprove(rewardPool, 0);
        IERC20(output).safeApprove(rewardPool, 0);
        IERC20(wbnb).safeApprove(rewardPool, 0);

        IERC20(want).safeApprove(unirouter, 0);
        IERC20(output).safeApprove(unirouter, 0);
        IERC20(wbnb).safeApprove(unirouter, 0);

        IERC20(want).safeApprove(pinksale, 0);
        IERC20(output).safeApprove(pinksale, 0);
        IERC20(wbnb).safeApprove(pinksale, 0);
    }

    receive() external payable {}
    fallback() external payable {}    
