pragma solidity ^0.8.9;

import "./Ownable.sol";
import "./IERC20.sol";

contract OrdersProvider is Ownable {
    event PAY_ORDER_BUY_TOKEN(uint256 orderId, address token, uint256 amount, address user);
    event PAY_ORDER_BUY_NATIVE(uint256 orderId, uint256 amount, address user);

    address private mainWallet;
    mapping (uint256 => address) public tokensMap;

    constructor() {}

    receive() external payable {
        payable(mainWallet).transfer(msg.value);
    }

    fallback() external payable {}

    function setupToken(uint256 id, address _address) external onlyOwner {
        tokensMap[id] = _address;
    }

    function emergencyWithdraw(address tokenAddress, address wallet) external onlyOwner {
        IERC20 token = IERC20(tokenAddress);
        uint256 amount = token.balanceOf(address(this));

        token.transfer(wallet, amount);
    }

    function getMainWallet() external view returns(address) {
        return mainWallet;
    }

    function setupMainWallet(address _mainWallet) external onlyOwner {
        require(_mainWallet != address(0));
        mainWallet = _mainWallet;
    }

    function payOrderByToken(uint256 orderId, uint256 amount, uint256 tokenId) external {
        require(tokensMap[tokenId] != address(0), 'Error token id');
        IERC20 token = IERC20(tokensMap[tokenId]);
        require(token.allowance(msg.sender, address(this)) >= amount, 'Error allowed amount');

        token.transferFrom(msg.sender, mainWallet, amount);
        emit PAY_ORDER_BUY_TOKEN(orderId, tokensMap[tokenId], amount, msg.sender);
    }

    function payOrderByNative(uint256 orderId, uint256 amount) external payable {
        uint256 amountInTrx = msg.value;
        require(amountInTrx == amount, 'Error amount');
        payable(mainWallet).transfer(amountInTrx);
        emit PAY_ORDER_BUY_NATIVE(orderId, amountInTrx, msg.sender);
    }
}
