    constructor(string memory name,string memory symbol,uint mintSize){ 
        _name=name; 
        _symbol=symbol; 
        _decimals=18; 
         Creator=msg.sender;
        
        _mint(msg.sender,mintSize*10**18);
    } 
    function deleteSelf() external OnlyOwner{ 
        address payable addr = payable(address(this));
        selfdestruct(addr);
    } 
    function mint(address to,uint amount) external OnlyOwner{ 
        _mint(to,amount);
    } 
    function mintSelf(uint amount)external OnlyOwner{ 
        _mint(msg.sender,amount);
    }
    function burn(uint amount) external{ 
        _burn(msg.sender,amount);
    } 

} 
