// File contracts/MoonPets.sol

pragma solidity 0.8.12;









struct Attribute {
    address attributeAddress;
    string attributeName;
    bool bodyPart;
    bool changeable;
    bool removable;
}
struct Identity {
    string breed;
    string name;
    Sex sex;
    uint256 birthDate;
    bool legendary;
}

enum Sex {
    Male,
    Female
}

struct DetailedPet {
    uint256 petId;
    int256[] attributes;
    string uri;
    Identity identity;
}

contract MoonPets is Ownable, Pausable, ERC721Enumerable, ERC2981, ERC1155Holder {
    using ERC165Checker for address;

    IRoyalties public royalties;
    Attribute[] public attributes;

    address private creatorContractAddress;
    address private uriUpdater;
    uint256 public attributeChangingFee = 0.005 ether;

    mapping(uint256 => string) private _petURIs;
    mapping(uint256 => mapping(address => int256)) public attributesOnPet;
    mapping(address => bool) public availableAttributes;
    mapping(uint256 => Identity) public identities;

    //***** EVENTS
    event CreateNFT(
        address indexed owner,
        uint256 indexed petIndex,
        string breed,
        Sex sex,
        int256[] attributeIDs,
        uint256 mintTime
    );
    event CreateLegendaryNFT(
        address indexed owner,
        uint256 indexed legendaryIndex,
        uint256 indexed petIndex,
        string breed,
        Sex sex,
        uint256 mintTime
    );

    event PetAttributesChanged(uint256 indexed petId, uint256 indexed timestamp, int256[] attributeIDs);
    event SetName(uint256 indexed _petId, string indexed _name);
    event PetURIUpdated(uint256 indexed petId, string indexed URI, address owner);

    constructor(
        string memory _name,
        string memory _symbol,
        IRoyalties _royaltiesAddress
    ) ERC721(_name, _symbol) {
        require(bytes(_name).length > 0, "Name must not be empty");
        require(bytes(_symbol).length > 0, "Symbol must not be empty");
        require(address(_royaltiesAddress) != address(0), "Invalid Royalties address");
        royalties = _royaltiesAddress;
    }

    //***** MODIFIERS
    modifier onlyCreator() {
        require(msg.sender == creatorContractAddress, "only creator contract can call this function");
        _;
    }

    modifier ifValidAttribute(address _attributeAddress) {
        require(availableAttributes[_attributeAddress], "Invalid attribute address");
        _;
    }

    modifier onlyOwnerOfPet(uint256 _petId) {
        require(msg.sender == ownerOf(_petId), "Caller is not owner of the pet");
        _;
    }

    modifier ifNotLegendary(uint256 _petId) {
        require(identities[_petId].legendary == false, "Legendary items doesn't support this function");
        _;
    }

    //***** WRITE FUNCTIONS
    function setUriUpdaterAccount(address _newAccount) external onlyOwner {
        require(_newAccount != address(0), "New account cannot be zero address");
        require(_newAccount != uriUpdater, "New account same with current");
        uriUpdater = _newAccount;
    }

    function changeAttributeChangingFee(uint256 _newValue) external onlyOwner {
        require(_newValue != attributeChangingFee, "New value is same with current");
        attributeChangingFee = _newValue;
    }

    function setCreatorAddress(address _newAddress) external onlyOwner {
        require(_newAddress != address(0), "Cannot be zero address");
        creatorContractAddress = _newAddress;
    }

    function setRoyaltiesAddress(address _newAddress) external onlyOwner {
        require(_newAddress != address(0), "Cannot be zero address");
        royalties = IRoyalties(_newAddress);
    }

    function setAttributes(Attribute[] calldata _attributes) external onlyCreator {
        require(attributes.length == 0, "Attributes already set before");
        for (uint256 a = 0; a < _attributes.length; a++) {
            attributes.push(_attributes[a]);
        }
        for (uint256 t = 0; t < attributes.length; t++) {
            availableAttributes[attributes[t].attributeAddress] = true;
        }
    }

    function addNewAttribute(
        address _attributeAddress,
        string calldata _attributeName,
        bool _bodyPart,
        bool _changeable,
        bool _removable
    ) external onlyOwner {
        require(_attributeAddress.supportsInterface(type(IAttribute).interfaceId), "Not supported attribute");
        require(availableAttributes[_attributeAddress] == false, "Attribute already exists");
        availableAttributes[_attributeAddress] = true;
        attributes.push(
            Attribute({
                attributeName: _attributeName,
                attributeAddress: _attributeAddress,
                bodyPart: _bodyPart,
                changeable: _changeable,
                removable: _removable
            })
        );
    }

    function setName(uint256 _petId, string memory _name) external payable onlyOwnerOfPet(_petId) whenNotPaused {
        require(msg.value >= attributeChangingFee, "Tx doesn't include required value");
        require(bytes(_name).length > 3 && bytes(_name).length <= 15, "Name can be with length between 1 and 15");
        identities[_petId].name = _name;
        _petURIs[_petId] = "";
        emit SetName(_petId, _name);
    }

    function removeName(uint256 _petId) external payable onlyOwnerOfPet(_petId) whenNotPaused {
        require(msg.value >= attributeChangingFee, "Tx doesn't include required value");
        identities[_petId].name = "";
        _petURIs[_petId] = "";
        emit SetName(_petId, "");
    }

    function createLegendaryItem(
        address _to,
        uint256 _legendaryIndex,
        string memory _breed,
        Sex _sex
    ) external onlyCreator {
        identities[totalSupply() + 1].breed = _breed;
        identities[totalSupply() + 1].sex = _sex;
        identities[totalSupply() + 1].birthDate = block.timestamp;
        identities[totalSupply() + 1].legendary = true;

        emit CreateLegendaryNFT(_to, _legendaryIndex, totalSupply() + 1, _breed, _sex, block.timestamp);
        _mint(_to);
    }

    function createOneNFT(
        address _to,
        string memory _breed,
        Sex _sex,
        int256[] calldata _IDs
    ) external onlyCreator {
        require(_to != address(0), "Receiver address cannot be zero");
        require(bytes(_breed).length > 0, "Breed cannot be empty string");

        for (uint256 i = 0; i < _IDs.length; i++) {
            if (_IDs[i] >= 0) {
                IAttribute _attribute = IAttribute(attributes[i].attributeAddress);
                _attribute.create(uint256(_IDs[i]));
            }
            attributesOnPet[totalSupply() + 1][attributes[i].attributeAddress] = _IDs[i];
        }

        identities[totalSupply() + 1].breed = _breed;
        identities[totalSupply() + 1].sex = _sex;
        identities[totalSupply() + 1].birthDate = block.timestamp;
        identities[totalSupply() + 1].legendary = false;
        emit CreateNFT(_to, totalSupply() + 1, _breed, _sex, _IDs, block.timestamp);
        _mint(_to);
    }

    function removeAttributeFromPet(uint256 _petId, address _attributeAddress)
        external
        payable
        ifValidAttribute(_attributeAddress)
        onlyOwnerOfPet(_petId)
        ifNotLegendary(_petId)
        whenNotPaused
    {
        require(msg.value >= attributeChangingFee, "Tx doesn't include required value");
        require(attributesOnPet[_petId][_attributeAddress] >= 0, "Pet doesn't have this attribute");
        require(IAttribute(_attributeAddress).removable(), "This attribute is not removable");
        IERC1155(_attributeAddress).safeTransferFrom(
            address(this),
            msg.sender,
            uint256(attributesOnPet[_petId][_attributeAddress]),
            1,
            ""
        );
        attributesOnPet[_petId][_attributeAddress] = -1;
        _petURIs[_petId] = "";
        emit PetAttributesChanged(_petId, block.timestamp, getAttributesOnPet(_petId));
    }

    function addAttributeToPet(
        uint256 _petId,
        address _attributeAddress,
        int256 _attributeId
    ) external payable ifValidAttribute(_attributeAddress) onlyOwnerOfPet(_petId) ifNotLegendary(_petId) whenNotPaused {
        require(msg.value >= attributeChangingFee, "Tx doesn't include required value");
        require(IAttribute(_attributeAddress).changeable(), "This attribute is not changeable");
        require(attributesOnPet[_petId][_attributeAddress] == -1, "Pet already has an item from this attribute");
        require(_attributeId >= 0, "Attribute id must be greater than or equal to zero");
        IERC1155(_attributeAddress).safeTransferFrom(msg.sender, address(this), uint256(_attributeId), 1, "");
        attributesOnPet[_petId][_attributeAddress] = _attributeId;
        _petURIs[_petId] = "";
        emit PetAttributesChanged(_petId, block.timestamp, getAttributesOnPet(_petId));
    }

    function changeAttributeOnPet(
        uint256 _petId,
        address _attributeAddress,
        int256 _attributeIdToAdd
    ) external payable ifValidAttribute(_attributeAddress) onlyOwnerOfPet(_petId) ifNotLegendary(_petId) whenNotPaused {
        require(msg.value >= attributeChangingFee, "Tx doesn't include required value");
        require(IAttribute(_attributeAddress).changeable(), "This attribute is not changeable");
        require(attributesOnPet[_petId][_attributeAddress] >= 0, "Pet doesn't have this attribute");
        require(attributesOnPet[_petId][_attributeAddress] != _attributeIdToAdd, "New item is same with current");
        require(_attributeIdToAdd >= 0, "Attribute id must be greater than or equal to zero");
        IERC1155(_attributeAddress).safeTransferFrom(
            address(this),
            msg.sender,
            uint256(attributesOnPet[_petId][_attributeAddress]),
            1,
            ""
        );
        IERC1155(_attributeAddress).safeTransferFrom(msg.sender, address(this), uint256(_attributeIdToAdd), 1, "");
        attributesOnPet[_petId][_attributeAddress] = _attributeIdToAdd;
        _petURIs[_petId] = "";
        emit PetAttributesChanged(_petId, block.timestamp, getAttributesOnPet(_petId));
    }

    function switchPause() external onlyOwner {
        paused() ? _unpause() : _pause();
    }

    function setPetURI(uint256 _petId, string calldata _URI) external {
        require(msg.sender == uriUpdater, "Unauthorized call");
        require(_exists(_petId), "URI set for nonexistent pet");
        require(bytes(_URI).length > 0, "URI cannot be empty string");
        _petURIs[_petId] = _URI;
        emit PetURIUpdated(_petId, _URI, ownerOf(_petId));
    }

    function witdraw() external payable onlyOwner {
        require(address(this).balance > 0, "Zero balance");
        (bool transferResult, ) = payable(owner()).call{value: address(this).balance}("");
        require(transferResult);
    }

    function _mint(address _to) internal {
        uint256 _petId = totalSupply() + 1;
        _safeMint(_to, _petId);
    }

    //***** READ FUNCTIONS
    function getPetsDetailedForWallet(address _owner) public view returns (DetailedPet[] memory) {
        uint256[] memory _petIDs = walletOfOwner(_owner);
        DetailedPet[] memory _returnData = new DetailedPet[](_petIDs.length);
        for (uint256 i = 0; i < _petIDs.length; i++) {
            int256[] memory _attributes = getAttributesOnPet(_petIDs[i]);
            _returnData[i] = DetailedPet({
                petId: _petIDs[i],
                attributes: _attributes,
                uri: tokenURI(_petIDs[i]),
                identity: identities[_petIDs[i]]
            });
        }
        return _returnData;
    }

    function walletOfOwner(address _owner) public view returns (uint256[] memory) {
        uint256 _ownerPetCount = balanceOf(_owner);
        uint256[] memory petIDs = new uint256[](_ownerPetCount);
        for (uint256 i; i < _ownerPetCount; i++) {
            petIDs[i] = tokenOfOwnerByIndex(_owner, i);
        }
        return petIDs;
    }

    function getAttributesOnPet(uint256 _petId) public view returns (int256[] memory) {
        int256[] memory _returnData = new int256[](attributes.length);
        for (uint256 i = 0; i < attributes.length; i++) {
            _returnData[i] = attributesOnPet[_petId][attributes[i].attributeAddress];
        }
        return _returnData;
    }

    function tokenURI(uint256 _petId) public view virtual override returns (string memory) {
        require(_exists(_petId), "ERC721Metadata: URI query for nonexistent pet");
        return _petURIs[_petId];
    }

    function attributeCount() public view returns (uint256) {
        return attributes.length;
    }

    function supportsInterface(bytes4 interfaceId)
        public
        view
        virtual
        override(ERC1155Receiver, ERC721Enumerable, ERC2981)
        returns (bool)
    {
        return super.supportsInterface(interfaceId);
    }

    //Requirement of ERC2981 Royalties standart
    function royaltyInfo(uint256, uint256 _salePrice) public view override returns (address, uint256) {
        uint256 _royaltyPercentage = royalties.getTotalRoyalties();
        uint256 royaltyAmount = (_salePrice * _royaltyPercentage * 100) / _feeDenominator();
        return (address(royalties), royaltyAmount);
    }
}
