// SPDX-License-Identifier: MIT
pragma solidity 0.8.0;

contract Ownable {
    address public owner;

    event OwnershipTransferred(address indexed previousOwner, address indexed newOwner);

    modifier onlyOwner() {
        require(msg.sender == owner, "Not the owner");
        _;
    }

    constructor() {
        owner = msg.sender;
        emit OwnershipTransferred(address(0), owner);
    }

    function transferOwnership(address newOwner) external onlyOwner {
        require(newOwner != address(0), "Invalid address");
        emit OwnershipTransferred(owner, newOwner);
        owner = newOwner;
    }
}

contract TomorrowlandToken is Ownable {
    string public name = "Tomorrowland";
    string public symbol = "TML";
    uint8 public decimals = 18;

    uint256 public totalSupply = 1000000000 * (10**uint256(decimals)); // 8 billion tokens
    uint256 public maxSupply = 8000000000 * (10**uint256(decimals)); // 1 billion tokens
    uint256 public initialPrice = 10; // $0.10 per token in cents

    mapping(address => uint256) public balanceOf;

    event Transfer(address indexed from, address indexed to, uint256 value);
    event Burn(address indexed from, uint256 value);

    constructor() {
        balanceOf[owner] = totalSupply;
        emit Transfer(address(0), owner, totalSupply);
    }

    function transfer(address to, uint256 value) external returns (bool) {
        require(to != address(0), "Invalid address");
        require(value <= balanceOf[msg.sender], "Insufficient balance");

        balanceOf[msg.sender] -= value;
        balanceOf[to] += value;

        emit Transfer(msg.sender, to, value);
        return true;
    }

    function mint(uint256 amount) external onlyOwner {
        require(totalSupply + amount <= maxSupply, "Exceeds total supply");

        balanceOf[owner] += amount;
        totalSupply += amount;

        emit Transfer(address(0), owner, amount);
    }

    function burn(uint256 amount) external onlyOwner {
        require(amount <= balanceOf[owner], "Insufficient balance for burning");

        balanceOf[owner] -= amount;
        totalSupply -= amount;

        emit Burn(owner, amount);
    }

    function getCost(uint256 amount) external view returns (uint256) {
        return amount * initialPrice;
    }
}
