    "contracts/router/PoopRouter.sol": {
      "content": "// SPDX-License-Identifier: MIT

pragma solidity 0.8.16;

import "@openzeppelin/contracts/utils/cryptography/MerkleProof.sol";
import '@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol';
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/Ownable2Step.sol";
import '@openzeppelin/contracts/token/ERC20/IERC20.sol';
import '../interfaces/IUniswapV2Router02.sol';
import '../interfaces/IUniswapV2Factory.sol';
import '../interfaces/IWETH.sol';
import '../interfaces/IPoop.sol';

contract PoopRouter is ReentrancyGuard, Ownable2Step {
    using SafeERC20 for IERC20;

    uint immutable MIN_AMOUNT;
    IWETH public immutable WETH;
    IPoop public immutable POOP;
    mapping(IUniswapV2Router02 => IUniswapV2Factory) public supportedRouters;
    uint MIN_BUY_AMOUNT;
    bytes32 public ROOT;
    bool public ONLY_WHITELIST;
    address public VAULT;

    constructor(IWETH weth, IPoop poop, uint minAmount, uint minBuyAmount, IUniswapV2Router02 supportedRouter, bytes32 root, address vault, address owner) {
        WETH = weth;
        POOP = poop;
        MIN_AMOUNT = minAmount;
        MIN_BUY_AMOUNT = minBuyAmount;
        supportedRouters[supportedRouter] = IUniswapV2Factory(supportedRouter.factory());
        ROOT = root;
        ONLY_WHITELIST = (root != bytes32(0));
        require(vault != address(0), "illegal vault address");
        VAULT = vault;
        transferOwnership(owner);
    }

    function swapExactTokens(IUniswapV2Router02 router, address[] calldata path, uint amount, uint minAmount, address referral) external payable {
        require(!ONLY_WHITELIST, "only whitelist");
        _swapExactTokens(router, path, amount, minAmount, referral);
    }

    function swapExactTokensForWhitelist(IUniswapV2Router02 router, address[] calldata path, uint amount, uint minAmount, uint maxAmount, bytes32[] calldata proof, address referral) external payable {
        require(MerkleProof.verifyCalldata(
            proof, ROOT, keccak256(bytes.concat(keccak256(abi.encode(msg.sender, maxAmount))))
        ), "verify failed");
        _swapExactTokens(router, path, amount, minAmount, referral);
    }

    function setRoot(bytes32 root) external onlyOwner {
        ROOT = root;
    }

    function setMinBuyAmount(uint amount) external onlyOwner {
        MIN_BUY_AMOUNT = amount;
    }

    function addSupportedRouter(IUniswapV2Router02 router) external onlyOwner {
        supportedRouters[router] = IUniswapV2Factory(router.factory());
    }

    function delSupportedRouter(IUniswapV2Router02 router) external onlyOwner {
        delete supportedRouters[router];
    }

    function setOnlyWhitelist(bool flag) external onlyOwner {
        ONLY_WHITELIST = flag;
    }

    function setVault(address vault) external onlyOwner {
        require(vault != address(0), "illegal vault");
        VAULT = vault;
    }

    function _swapExactTokens(IUniswapV2Router02 router, address[] calldata path, uint amount, uint minAmount, address referral) internal nonReentrant {
        IUniswapV2Factory factory = IUniswapV2Factory(router.factory());
        require(address(factory) != address(0), "router not support");

        require(path.length >= 2 && path[path.length - 1] == address(WETH), "illegal path");
        require(minAmount >= MIN_AMOUNT, "illegal amount");

        IERC20 token = IERC20(path[0]);
        token.safeTransferFrom(msg.sender, address(this), amount);

        uint balance = token.balanceOf(address(this));
        token.safeApprove(address(router), balance);
        try router.swapExactTokensForETHSupportingFeeOnTransferTokens(balance, minAmount, path, address(this), block.timestamp) {
            //we defined minAmount for swap, so there would be values;
            WETH.withdraw(WETH.balanceOf(address(this)));
        } catch {
            //do nothing
        }
        token.safeApprove(address(router), 0);

        balance = token.balanceOf(address(this));
        if (balance > 0) {
            token.safeTransfer(VAULT, balance);
        }

        require(address(this).balance >= MIN_BUY_AMOUNT, "illegal buy amount");
        POOP.buy{value: address(this).balance}(msg.sender, referral);
    }

    receive() external payable {}

    fallback() external payable {}

}
"
    },
    "contracts/router/WhitelistRouter.sol": {
      "content": "// SPDX-License-Identifier: MIT

pragma solidity 0.8.16;

import "@openzeppelin/contracts/utils/cryptography/MerkleProof.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/Ownable2Step.sol";
import '../interfaces/IPoop.sol';

contract WhitelistRouter is ReentrancyGuard, Ownable2Step {
    IPoop public immutable POOP;
    bytes32 public ROOT;
    mapping(address => uint) public userAmounts;
    uint public MIN_BUY_AMOUNT;

    constructor(IPoop poop, bytes32 root, uint minBuyAmount, address owner) {
        POOP = poop;
        ROOT = root;
        MIN_BUY_AMOUNT = minBuyAmount;
        transferOwnership(owner);
    }

    function buy(uint maxAmount, bytes32[] calldata proof, address referral) external payable nonReentrant {
        require(userAmounts[msg.sender] + msg.value <= maxAmount, "execute amount");
        require(msg.value >= MIN_BUY_AMOUNT, "illegal amount, failed");

        require(MerkleProof.verifyCalldata(
            proof, ROOT, keccak256(bytes.concat(keccak256(abi.encode(msg.sender, maxAmount))))
        ), "verify failed");

        POOP.buy{value: msg.value}(msg.sender, referral);
        userAmounts[msg.sender] += msg.value;
    }

    function setRoot(bytes32 root) external onlyOwner {
        ROOT = root;
    }

    function setMinBuyAmount(uint minBuyAmount) external onlyOwner {
        MIN_BUY_AMOUNT = minBuyAmount;
    }
}
