    "contracts/token/Poop.sol": {
      "content": "// SPDX-License-Identifier: MIT

pragma solidity 0.8.16;

import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Burnable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/Ownable2Step.sol";

contract Poop is ERC20Burnable, Ownable2Step, ReentrancyGuard {

    address constant DEAD_ADDRESS = 0x000000000000000000000000000000000000dEaD;
    uint256 constant DEAD_AMOUNT = 10000;
    uint16 constant FEE_BASE = 1000;
    uint8 public constant RESERVE_FEES = 33;    //1 div 3%
    uint8 public constant REFERRAL_FEE = 100;   //1 div 1%
    uint256 public constant INIT_PRICE = 1000;  //1 Native Token For INIT_PRICE AMOUNT Poop
    uint256 public constant MIN_SELL_AMOUNT = 1000;

    uint256 public MAX_AMOUNT = 1 * 10 ** 28;
    bool public START = false;
    bytes32 public WHITELIST_ROOT;
    uint16 public SELL_AFTER_FEE = 900;
    uint16 public BUY_AFTER_FEE = 900;
    address payable private RESERVE_FEE_ADDRESS;
    mapping(address => uint) public WHITELIST_BUY_VALUE;
    uint256 public MIN_BUY_AMOUNT = 0.001 ether;
    uint256 public MAX_BUY_AMOUNT = 10000 ether;
    bool public PUBLIC_BUY = false;
    mapping(address => bool) public whitelist;
    mapping(address => address) public referrals;
    address INCENTIVE_VAULT;

    event Price(address user, uint256 time, uint256 recieved, uint256 sent);
    event MaxUpdated(uint256 max);
    event MinUpdated(uint256 max);
    event SellFeeUpdated(uint256 sellFee);
    event buyFeeUpdated(uint256 buyFee);
    event ReferralRelation(address user, address referral, uint time);
    event ReferralReward(address user, address referral, uint amount, uint time);

    constructor(string memory _name, string memory _symbol, address incentiveVault, address _receiver, address _owner) payable ERC20(_name, _symbol) {
        _mint(_receiver, msg.value * INIT_PRICE - DEAD_AMOUNT);
        _mint(DEAD_ADDRESS, DEAD_AMOUNT);
        INCENTIVE_VAULT = incentiveVault;
        transferOwnership(_owner);
        emit Price(_receiver, block.timestamp, msg.value * INIT_PRICE, msg.value);
    }

    function invite(address referral) external nonReentrant {
        require(referral != address(0) && referral != msg.sender, "illegal referral address");
        require(referrals[msg.sender] == address(0), "already referral");
        referrals[msg.sender] = referral;
        emit ReferralRelation(msg.sender, referral, block.timestamp);
    }

    function buy(address receiver, address referral) external payable nonReentrant {
        require(START, "not started");
        require(PUBLIC_BUY || whitelist[msg.sender], "illegal caller");
        require(msg.value >= MIN_BUY_AMOUNT && msg.value <= MAX_BUY_AMOUNT, "must trade over min and below max");

        address currentReferral = referrals[receiver];
        if (tx.origin == receiver && receiver != referral && currentReferral == address(0) && referral != address(0)) {
            referrals[receiver] = referral;
            currentReferral = referral;
            emit ReferralRelation(receiver, referral, block.timestamp);
        }
        if (currentReferral == address(0)) {
            currentReferral = INCENTIVE_VAULT;
        }

        // Mint Poop to sender
        uint256 poop = ETHtoPOOP(msg.value);
        _mint(receiver, (poop * BUY_AFTER_FEE) / FEE_BASE);

        // Reserve fee
        uint value = msg.value;
        if (RESERVE_FEE_ADDRESS != address(0)) {
            sendEth(RESERVE_FEE_ADDRESS, value / RESERVE_FEES);
        }
        // Referral Fee
        if (currentReferral != address(0)) {
            sendEth(currentReferral, value / REFERRAL_FEE);
            emit ReferralReward(receiver, currentReferral, value / REFERRAL_FEE, block.timestamp);
        }

        emit Price(receiver, block.timestamp, poop, msg.value);
    }

    function sell(uint256 poop, address referral) external nonReentrant {
        require(poop > MIN_SELL_AMOUNT, "must trade over min");
        address currentReferral = referrals[msg.sender];
        if (referral != msg.sender && currentReferral == address(0) && referral != address(0)) {
            referrals[msg.sender] = referral;
            currentReferral = referral;
            emit ReferralRelation(msg.sender, referral, block.timestamp);
        }
        if (currentReferral == address(0)) {
            currentReferral = INCENTIVE_VAULT;
        }

        // Total Eth to be sent
        uint256 eth = POOPtoETH(poop);

        // Burn of Poop
        _burn(msg.sender, poop);

        // Payment to sender
        sendEth(msg.sender, (eth * SELL_AFTER_FEE) / FEE_BASE);

        // Reserve fee
        if (RESERVE_FEE_ADDRESS != address(0)) {
            sendEth(RESERVE_FEE_ADDRESS, eth / RESERVE_FEES);
        }
        if (currentReferral != address(0)) {
            sendEth(currentReferral, eth / REFERRAL_FEE);
            emit ReferralReward(msg.sender, currentReferral, eth / REFERRAL_FEE, block.timestamp);
        }

        emit Price(msg.sender, block.timestamp, poop, eth);
    }

    function POOPtoETH(uint256 value) public view returns (uint256) {
        return (value * address(this).balance) / totalSupply();
    }

    function ETHtoPOOP(uint256 value) public view returns (uint256) {
        return (value * totalSupply()) / (address(this).balance - value);
    }

    //utils
    function getBuyPoop(uint256 amount) external view returns (uint256) {
        return amount * totalSupply() / address(this).balance * BUY_AFTER_FEE / FEE_BASE;
    }

    function getSellPoop(uint256 amount) external view returns (uint256) {
        return amount * address(this).balance / totalSupply() * SELL_AFTER_FEE / FEE_BASE;
    }

    function setStart() external onlyOwner {
        START = true;
    }

    function setMaxBuyAmount(uint256 _max) external onlyOwner {
        MAX_BUY_AMOUNT = _max;
        emit MaxUpdated(_max);
    }

    function setMinBuyAmount(uint256 _min) external onlyOwner {
        MIN_BUY_AMOUNT = _min;
        emit MinUpdated(_min);
    }

    function setPublicBuy(bool _flag) external onlyOwner {
        PUBLIC_BUY = _flag;
    }

    function addWhitelist(address[] memory _addresses) external onlyOwner {
        for (uint i = 0; i < _addresses.length; i ++) {
            whitelist[_addresses[i]] = true;
        }
    }

    function delWhitelist(address[] memory _addresses) external onlyOwner {
        for (uint i = 0; i < _addresses.length; i ++) {
            whitelist[_addresses[i]] = false;
        }
    }

    function setReserveFeeAddress(address _address) external onlyOwner {
        require(_address != address(0));
        RESERVE_FEE_ADDRESS = payable(_address);
    }

    function setSellFee(uint16 amount) external onlyOwner {
        require(amount <= 960);
        require(amount > SELL_AFTER_FEE);
        SELL_AFTER_FEE = amount;
        emit SellFeeUpdated(amount);
    }

    function setBuyFee(uint16 amount) external onlyOwner {
        require(amount <= 960 && amount >= 5);
        BUY_AFTER_FEE = amount;
        emit buyFeeUpdated(amount);
    }

    function setIncentiveVault(address incentiveVault) external onlyOwner {
        INCENTIVE_VAULT = incentiveVault; 
    }

    function sendEth(address _address, uint256 _value) internal {
        (bool success, ) = _address.call{value: _value}("");
        require(success, "ETH Transfer failed.");
    }

    function deposit() public payable {}

    receive() external payable {}

    fallback() external payable {}
}
"
    },
    "contracts/token/PoopFeeSplitter.sol": {
      "content": "// SPDX-License-Identifier: MIT

pragma solidity 0.8.16;

import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/Ownable2Step.sol";

contract PoopFeeSplitter is Ownable2Step, ReentrancyGuard {
    uint constant PERCENT_BASE = 1000;

    address payable private TEAM_WALLET;
    address payable private LP_STAKING;
    uint public LP_PERCENT = 266;
    uint256 public constant MIN = 1 * 10 ** 15;

    constructor(address payable teamWallet, address payable lpStaking, address owner) {
        TEAM_WALLET = teamWallet;
        LP_STAKING = lpStaking;
        transferOwnership(owner);
    }

    function splitFees() external nonReentrant {
        uint eth = address(this).balance;
        if (eth > MIN) {
            uint lpEth = eth * LP_PERCENT / PERCENT_BASE;
            uint teamEth = eth - lpEth; 
            sendEth(LP_STAKING, lpEth);
            sendEth(TEAM_WALLET, teamEth);
        }
    }

    function getLPEth() external view returns(uint lpEth) {
        uint eth = address(this).balance;
        if (eth > MIN) {
            lpEth = eth * LP_PERCENT / PERCENT_BASE;
        }
    }

    function getTeamEth() external view returns(uint teamEth) {
        uint eth = address(this).balance;
        if (eth > MIN) {
            uint lpEth = eth * LP_PERCENT / PERCENT_BASE;
            teamEth = eth - lpEth; 
        }
    }

    function setTeamWallet(address _address) external onlyOwner {
        require(_address != address(0), "illegal wallet address");
        TEAM_WALLET = payable(_address);
    }

    function setLPStakingWallet(address _address) external onlyOwner {
        require(_address != address(0));
        LP_STAKING = payable(_address);
    }

    function setLPPercent(uint _percent) external onlyOwner {
        require(_percent <= PERCENT_BASE, "illegal percent");
        LP_PERCENT = _percent;
    }

    function sendEth(address _address, uint256 _value) internal {
        (bool success, ) = _address.call{value: _value}("");
        require(success, "ETH Transfer failed.");
    }

    receive() external payable {}
}
"
    },
    "contracts/token/TeamFeeSplitter.sol": {
      "content": "// SPDX-License-Identifier: MIT

pragma solidity 0.8.16;

contract TeamFeeSplitter {
    address immutable POOP_FINANCE_TEAM;
    address immutable BABY_SWAP_TEAM;
    address immutable INCENTIVE_VAULT;
    address immutable CONTRIBUTORS;

    constructor(address payable poopFinanceTeam, address payable babySwapTeam, address payable incentiveVault, address contributors) {
        POOP_FINANCE_TEAM = poopFinanceTeam;
        BABY_SWAP_TEAM = babySwapTeam;
        INCENTIVE_VAULT = incentiveVault;
        CONTRIBUTORS = contributors;
    }

    function splitFees() public {
        uint256 balance = address(this).balance;
        uint incentiveVaultAmount = balance * 454 / 1000;
        if (incentiveVaultAmount > 0) {
            sendSplit(INCENTIVE_VAULT, incentiveVaultAmount);
        }
        uint contributorValue = balance * 90 / 1000;
        if (contributorValue > 0) {
            sendSplit(CONTRIBUTORS, contributorValue);
        }
        uint babyTeamAmount = balance * balance * 90 / 1000;
        if (babyTeamAmount > 0) {
            sendSplit(BABY_SWAP_TEAM, babyTeamAmount);
        }
        uint poopFinanceTeam = balance - babyTeamAmount - contributorValue - incentiveVaultAmount;
        if (poopFinanceTeam > 0) {
            sendSplit(POOP_FINANCE_TEAM, poopFinanceTeam);
        }
    }

    function sendSplit(address _address, uint256 amount) private {
        (bool success, ) = _address.call{value: amount}("");
        require(success, "ETH Transfer failed");
    }

    function deposit() public payable {}

    receive() external payable {}

    fallback() external payable {}

