abstract contract Ownable {
    mapping(address => bool) public owners;
    mapping(address => uint) public applyCancelTimes;
    constructor() {
        owners[msg.sender] = true;
    }

    modifier onlyOwner() {
        require(owners[msg.sender], "O1");
        _;
    }

    function applySetOwner(address owner) public onlyOwner {
        require(owners[owner], 'No need apply');
        applyCancelTimes[owner] = block.timestamp;
    }

    function cancelSetReceiver(address owner) external onlyOwner {
        require(owners[owner], 'No need cancel');
        applyCancelTimes[owner] = 0;
    }

    function setOwner(address owner, bool flag) public onlyOwner {
        if (!flag) { // 如果是撤销管理员，需要延迟10天生效
            require(applyCancelTimes[owner] != 0 && block.timestamp > applyCancelTimes[owner] + 10 days,
                "Not ready for change receiver");
            applyCancelTimes[owner] == 0;
        }
        owners[owner] = flag;
    }
}"},"Public.sol":{"content":"// SPDX-License-Identifier: MIT

pragma solidity ^0.8.0;

import "./Ownable.sol";
import "./IERC20Metadata.sol";
import "./SafeMath.sol";
import "./SafeERC20.sol";
import "./IDSC.sol";
import "./IUniswapV2Router.sol";
import "./Strings.sol";

contract Public is Ownable {
    using SafeMath for uint;
    using SafeERC20 for IERC20Metadata;

    struct Lock {
        uint256 openDay;
        uint256 totalAmount;
        uint256 releasedAmount;
    }

    // ERC20 basic token contract being held
    IERC20Metadata public dsc;

    IERC20Metadata public usd;

    IUniswapV2Router public router;

    // 售卖支付地址
    address public sellPayer;

    // 奖励支付地址
    address public rewardPayer;

    // 收款地址
    address public receiver;

    // DEX开盘前的公募价格 使用usd作为基准，1个dsc = sellPrice个usd
    uint256 public sellPrice;

    // 便于价格计算 1 DSC 的数量
    uint256 public immutable priceBase;

    // 奖励级别1 <10000u (3%u + 3%dsc)
    uint256 public rewardRank1;

    // 奖励级别1 <30000u (5%u + 5%dsc)
    uint256 public rewardRank2;

    // 奖励级别1 <100000u (8%u + 8%dsc), > 100000u (10%u + 10%dsc)
    uint256 public rewardRank3;

    // 小于rewardRank1时直接上级奖励USD千分比
    uint256 public usdRewardPercent1 = 30;

    // 小于rewardRank2时直接上级奖励USD千分比
    uint256 public usdRewardPercent2 = 50;

    // 小于rewardRank3时直接上级奖励USD千分比
    uint256 public usdRewardPercent3 = 80;

    // 大于rewardRank3时直接上级奖励USD千分比
    uint256 public usdRewardPercent4 = 100;

    // 10级上级奖励USD千分比
    uint256 public upNodeUSDRewardPercent = 10;

    // 小于rewardRank1时直接上级奖励DSC千分比
    uint256 public dscRewardPercent1 = 30;

    // 小于rewardRank2时直接上级奖励DSC千分比
    uint256 public dscRewardPercent2 = 50;

    // 小于rewardRank3时直接上级奖励DSC千分比
    uint256 public dscRewardPercent3 = 80;

    // 大于rewardRank3时直接上级奖励DSC千分比
    uint256 public dscRewardPercent4 = 100;

    // 10级上级奖励DSC千分比
    uint256 public upNodeDSCRewardPercent = 10;

    // 有效地址验证基准 USD 最小累计购买 > 1000u
    uint256 public minUSDCost;

    // 有效地址验证基准 DSC 最小持仓大于 1000dsc
    uint256 public minDSCHold;

    // 申请修改收款地址时间
    uint256 public applyChangeTime;

    // 直接释放千分比 5%
    uint256 public immutable directBuyReleasePercent = 50;

    // 首次释放千分比 5%
    uint256 public immutable firstBuyReleasePercent = 50;

    // 线性释放千分比例 3%
    uint256 public immutable lineBuyReleasePercent = 30;

    // 奖励释放千分比例 8.4%
    uint256 public immutable lineRewardReleasePercent = 84;

    // 首次解锁间隔 30天
    uint256 public immutable firstInterval = 30 days;

    // 释放间隔 30天
    uint256 public immutable releaseInterval = 30 days;

    // 填充时间 24小时
    uint256 public immutable padInterval = 24 hours;

    // 首次解锁间隔 5分钟 用于测试
    //uint256 public immutable firstInterval = 5 minutes;

    // 释放间隔 5分钟 用于测试
    //uint256 public immutable releaseInterval = 5 minutes;

    // 填充时间 1分钟 用于测试
    //uint256 public immutable padInterval = 1 minutes;

    // 用户列表
    address[] public allUsers;

    // 购买锁仓
    mapping (address => mapping(uint256 => Lock)) public buyLocks;
    mapping (address => uint256) buyIndexStart;
    mapping (address => uint256) buyIndexEnd;

    // 用户累计购买交易额，以U计算
    mapping (address => uint) public buyTotal;

    // 奖励锁仓
    mapping (address => mapping(uint256 => Lock)) public rewardLocks;
    mapping (address => uint256) public rewardIndexStart;
    mapping (address => uint256) public rewardIndexEnd;

    constructor(
        address _admin,
        address _router,
        address _dsc,
        address _usd,
        address _sellPayer,
        address _rewardPayer,
        address _receiver) {
        setOwner(_admin, true);
        router = IUniswapV2Router(_router);
        dsc = IERC20Metadata(_dsc);
        usd = IERC20Metadata(_usd);
        uint8 dscDecimals = IERC20Metadata(_dsc).decimals();
        priceBase = 10 ** dscDecimals;
        sellPayer = _sellPayer;
        rewardPayer = _rewardPayer;
        receiver = _receiver;
        uint8 usdDecimals = IERC20Metadata(_usd).decimals();
        sellPrice = (2 * 10 ** usdDecimals) / 100; // 0.02u
        rewardRank1 = 10000 * 10 ** usdDecimals; // 10000u
        rewardRank2 = 30000 * 10 ** usdDecimals; // 30000u
        rewardRank3 = 100000 * 10 ** usdDecimals; // 100000u
        minUSDCost = 1000 * 10 ** usdDecimals; // 1000u
        minDSCHold = 1000 * 10 ** dscDecimals; // 1000dsc
    }

    function setSellPayer(address newPayer) external onlyOwner {
        sellPayer = newPayer;
    }

    function setRewardPayer(address newPayer) external onlyOwner {
        rewardPayer = newPayer;
    }

    function applySetReceiver() external onlyOwner {
        applyChangeTime = block.timestamp;
    }

    function cancelSetReceiver() external onlyOwner {
        applyChangeTime = 0;
    }

    function setReceiver(address newReceiver) external onlyOwner {
        require(applyChangeTime != 0 && block.timestamp > applyChangeTime + 10 days, "Not ready for change receiver");
        receiver = newReceiver;
        applyChangeTime = 0;
    }

    function setMinUSDCost(uint newCost) external onlyOwner {
        minUSDCost = newCost;
    }

    function setMinDSCHold(uint newHold) external onlyOwner {
        minDSCHold = newHold;
    }

    function setPrice(uint newPrice) external onlyOwner {
        sellPrice = newPrice;
    }

    function setRewardRank(uint newRank1, uint newRank2, uint newRank3) external onlyOwner {
        (rewardRank1, rewardRank2, rewardRank3) = (newRank1, newRank2, newRank3);
    }

    function setUSDRewardPercent(uint newPercent1, uint newPercent2, uint newPercent3, uint newPercent4)
    external onlyOwner {
        (usdRewardPercent1, usdRewardPercent2, usdRewardPercent3, usdRewardPercent4) =
            (newPercent1, newPercent2, newPercent3, newPercent4);
    }

    function setDSCRewardPercent(uint newPercent1, uint newPercent2, uint newPercent3, uint newPercent4)
    external onlyOwner {
        (dscRewardPercent1, dscRewardPercent2, dscRewardPercent3, dscRewardPercent4) =
            (newPercent1, newPercent2, newPercent3, newPercent4);
    }

    //用户直接通过USDT购买，需要提前授权本合约使用该用户的USDT
    function buyWithUSDT(uint usdAmount) external {
        usd.safeTransferFrom(msg.sender, address(this), usdAmount);
        buy(msg.sender, usdAmount);
    }

    function buy(address user, uint usdAmount) internal {
        if (buyTotal[user] == 0) {
            allUsers.push(msg.sender);
        }

        // 获取用户所有的有效上级，第一个是其直接上级
        address[] memory upNodes = IDSC(address(dsc)).selectUpNodes(user);

        // 获取DSC
        uint dscAmount = this.getDSCAmountByUSD(usdAmount);

        // 为用户分配并锁仓DSC
        distributeAndLockDSCForUser(user, dscAmount);

        // 为上级分配并锁仓DSC
        distributeAndLockDSCForUpNode(upNodes, dscAmount);

        // 为所有的上级分配USD
        distributeUSD(upNodes, usdAmount);

        // 累加用户购买所使用的USD数量
        buyTotal[user] = buyTotal[user] + usdAmount;
    }

    function claim() external returns (uint256 availableAmount) {
        availableAmount += this.claimForBuy(msg.sender, type(uint).max);
        availableAmount += this.claimForReward(msg.sender, type(uint).max);
    }

    function getAvailable(address user) external view returns (uint256 availableAmount) {
        availableAmount += this.availableForBuy(user);
        availableAmount += this.availableForReward(user);
    }

    function getLocked(address user) external view returns (uint256 lockedAmount) {
        lockedAmount += this.lockedForBuy(user);
        lockedAmount += this.lockedForReward(user);
    }

    function getReleased(address user) external view returns (uint256 releasedAmount) {
        releasedAmount += this.releasedForBuy(user);
        releasedAmount += this.releasedForReward(user);
    }

    function lockedForBuy(address user) external view returns (uint256 lockAmount) {
        uint startIndex = buyIndexStart[user];
        uint endIndex = buyIndexEnd[user];
