    function claimForBuy(address user, uint size) external returns (uint256 availableAmount) {
        uint startIndex = buyIndexStart[user];
        uint nextIndex = startIndex;
        uint end = size > (buyIndexEnd[user] - startIndex) ? buyIndexEnd[user] : startIndex + size;
        for (uint i=startIndex; i < end; i++) {
            Lock memory lock = buyLocks[user][i];

            // 没到释放时间，后面所有的都没到时间，不用继续检查
            if (block.timestamp < lock.openDay + firstInterval) break;

            // 初次释放数量，在第一次线性释放之前可以claim的数量
            uint releaseDate = lock.openDay + firstInterval + releaseInterval;
            uint firstAmount = lock.totalAmount * firstBuyReleasePercent / 1000;
            if (block.timestamp < releaseDate) {
                availableAmount += firstAmount;
                buyLocks[user][i].releasedAmount += firstAmount;
                continue;
            }

            // 线性释放数量
            uint releasedTimes = ((block.timestamp - releaseDate) / releaseInterval) + 1;
            uint releasedPercent = directBuyReleasePercent + firstBuyReleasePercent + releasedTimes * lineBuyReleasePercent;
            releasedPercent = releasedPercent > 1000 ? 1000 : releasedPercent;
            uint releaseAmount = ((lock.totalAmount * releasedPercent) / 1000) - lock.releasedAmount;
            if (releasedPercent == 1000) {
                delete buyLocks[user][i];
                nextIndex = i+1;
            } else if(releaseAmount > 0) {
                buyLocks[user][i].releasedAmount += releaseAmount;
            }

            availableAmount += releaseAmount;
        }

        if (startIndex != nextIndex) buyIndexStart[user] = nextIndex;
        dsc.transfer(user, availableAmount);
    }

