// File: H2H.sol


pragma solidity ^0.8.0;      

contract EventFunding  is Ownable {
     IERC20 public token;
    uint256 public contractTotalFund;
    
    // store the funds contributed by each participant for a specific eventID
    mapping(uint256 => mapping(address => uint256)) public eventFunds;

    //store fund for each userId
    mapping(uint256=> uint256) public eventTotalFund;
        
    event ParticipantAdded(address indexed participant, uint256 indexed eventID, uint256 amount);
     event FundsWithdrawn(address indexed participant, uint256  indexed eventID, uint256 amount);

       constructor(address _tokenAddress) {
        token = IERC20(_tokenAddress);
    }
       
    // Function to add a participant and their funds for a specific eventID
    function addParticipant(uint256 eventID, uint256 amount)   external  {
        require(eventID != 0, "Invalid eventID");
        
        eventFunds[eventID][msg.sender]+= amount;
        contractTotalFund += amount;

        eventTotalFund[eventID] += amount;
        
        token.transferFrom(msg.sender,address(this), amount);
        emit ParticipantAdded(msg.sender, eventID, amount); 
    }
    
     // Function for participants to withdraw their funds for a specific eventID
    function sendAmount(uint256 eventID, address[] memory users, uint256[] memory amount , uint256 adminAmount) external onlyOwner{
        require(eventID !=0, "Invalid eventID");
        require(users.length == amount.length,"length mismatch");
        uint256 totalAmount ;

        for(uint256 i=0;i<users.length;i++){
          totalAmount += amount[i];
        }

        require(totalAmount <=   eventTotalFund[eventID],"Exceed amount" );
    
        eventTotalFund[eventID] -= totalAmount;
        require(eventTotalFund[eventID]>= adminAmount,"Insufficient amount in contract for Admin");
        
        for(uint256 i=0; i< users.length;i++){
         eventFunds[eventID][users[i]] =0;
         token.transfer(users[i],amount[i]);
          emit FundsWithdrawn(users[i], eventID, amount[i]);
        }  
          token.transfer(owner(),eventTotalFund[eventID]);
          eventTotalFund[eventID]=0;
    }
    
     // Emergency function to withdraw all funds from the contract (only available to the contract owner)
       function emergencyWithdraw() external onlyOwner {
        require(contractTotalFund > 0, "No funds available for emergency withdrawal."); 
        require(token.transfer(owner(), contractTotalFund), "Token transfer failed");
        contractTotalFund = 0;
    }
}
