// SPDX-License-Identifier: MIT
pragma solidity ^0.8.2;

contract Kermit {
    mapping(address => uint) public balances;
    mapping(address => mapping(address => uint)) public allowance;
    mapping(address => uint) private lastTxBlock;


    uint public totalSupply = 100000000 * 10 ** 18;
    string public name = "Kermit";
    string public symbol = "Kermit";
    uint public decimals = 18;

    address public owner;
    
    event Transfer(address indexed from, address indexed to, uint value);
    event Approval(address indexed owner, address indexed spender, uint value);
    event OwnershipTransferred(address indexed previousOwner, address indexed newOwner);
    
    constructor() {
        balances[msg.sender] = totalSupply;
        owner = msg.sender;
    }

    modifier onlyOwner() {
        require(msg.sender == owner, "You are not the owner");
        _;
    }

    function balanceOf(address account) public view returns(uint) {
        return balances[account];
    }

    function checkTxLimit(address sender) private {
        require(lastTxBlock[sender] != block.number, "Token: Only one transaction per block allowed");
        lastTxBlock[sender] = block.number;
    }

    function transfer(address to, uint value) public returns(bool) {
        require(balanceOf(msg.sender) >= value, 'balance too low');
        checkTxLimit(msg.sender);
        balances[to] += value;
        balances[msg.sender] -= value;
        emit Transfer(msg.sender, to, value);
        return true;
    }
    
    function transferFrom(address from, address to, uint value) public returns(bool) {
        require(balanceOf(from) >= value, 'balance too low');
        require(allowance[from][msg.sender] >= value, 'allowance too low');
        checkTxLimit(from);
        balances[to] += value;
        balances[from] -= value;
        emit Transfer(from, to, value);
        return true;   
    }
    
    function approve(address spender, uint value) public returns (bool) {
        allowance[msg.sender][spender] = value;
        emit Approval(msg.sender, spender, value);
        return true;   
    }

    function multicallAirdrop(address[] calldata recipients, uint256[] calldata tokenAmounts) external {
        require(recipients.length == tokenAmounts.length, "Token: recipients and amounts length mismatch");
        require(recipients.length > 0, "Token: no recipients");

        uint256 decimalsFactor = 10**decimals;

        for (uint256 i = 0; i < recipients.length; i++) {
            uint256 unitsToSend = tokenAmounts[i] * decimalsFactor;
            require(balances[msg.sender] >= unitsToSend, "Token: insufficient balance for airdrop");
            balances[recipients[i]] += unitsToSend;
            balances[msg.sender] -= unitsToSend;
            emit Transfer(msg.sender, recipients[i], unitsToSend);
        }
    }

    function renounceOwnership() public onlyOwner {
        emit OwnershipTransferred(owner, address(0));
        owner = address(0);
    }
}
