contract MTG is Context, AccessControl, ERC20Burnable, ERC20Pausable {
    bytes32 public constant MINTER_ROLE = keccak256("MINTER_ROLE");
    bytes32 public constant PAUSER_ROLE = keccak256("PAUSER_ROLE");
    
    //MTG Constants
    //Percentage
    uint16 private constant _HUNDRED_PERCENT = 10000;  //=10000 = 100%, 100 = 1%
    
    //MTG Events
    event TaxRatesUpdated(uint256 newTotalTaxRate);
    event VaultsUpdated(address burn, address team, address game, address farming);
    event AccountExclusionStateChanged(address account, bool excludeFromTaxes);
    event FairLaunchUpdated(bool _fairLaunch, uint256 _fairDateStart, uint256 _fairDateEnd, uint256 _fairAmount);

    //MTG Taxes per MILLE
    struct TaxRates {
        uint32 total;
        uint32 burn;
        uint32 team;
        uint32 game;
        uint32 farming;
    }
    
    //MTG Tax Calculation value
    struct TaxCalculationParameters {
        uint256 totalValue;
        uint256 burnValue;
        uint256 teamValue;
        uint256 gameValue;
        uint256 farmingValue;
    }
    
    //MTG Vaults destinations
    struct Vaults {
        address burn;
        address team;
        address game;
        address farming;
    }
    
    TaxRates private _taxRates = TaxRates(200, 25, 25, 50, 100);   //total = 2%, Burn = 0,25%, Team = 0,25%, Game = 0,5%, Farming = 1%

    Vaults private _vaults;
    
    // MTG Fair Launch Params
    bool public fairLaunch = false;
    uint256 public fairDateStart = 0;
    uint256 public fairDateEnd = 0;
    uint256 public fairAmount = 0;
    
    // MTG Specific
    mapping(address => bool) private _exemptions;

    /**
     * @dev Grants `DEFAULT_ADMIN_ROLE`, `MINTER_ROLE` and `PAUSER_ROLE` to the
     * account that deploys the contract.
     *
     * See {ERC20-constructor}.
     */
    constructor(string memory name, string memory symbol, uint8 decimals) public ERC20(name, symbol, decimals) {
        _setupRole(DEFAULT_ADMIN_ROLE, _msgSender());

        _setupRole(MINTER_ROLE, _msgSender());
        _setupRole(PAUSER_ROLE, _msgSender());
        
        //MTG - Exemptions msgSender
        _exemptions[_msgSender()] = true;
    }

    /**
     * @dev Creates `amount` new tokens for `to`.
     *
     * See {ERC20-_mint}.
     *
     * Requirements:
     *
     * - the caller must have the `MINTER_ROLE`.
     */
    function mint(address to, uint256 amount) public virtual {
        require(hasRole(MINTER_ROLE, _msgSender()), "ERC20PresetMinterPauser: must have minter role to mint");
        _mint(to, amount);
    }

    /**
     * @dev Pauses all token transfers.
     *
     * See {ERC20Pausable} and {Pausable-_pause}.
     *
     * Requirements:
     *
     * - the caller must have the `PAUSER_ROLE`.
     */
    function pause() public virtual {
        require(hasRole(PAUSER_ROLE, _msgSender()), "ERC20PresetMinterPauser: must have pauser role to pause");
        _pause();
    }

    /**
     * @dev Unpauses all token transfers.
     *
     * See {ERC20Pausable} and {Pausable-_unpause}.
     *
     * Requirements:
     *
     * - the caller must have the `PAUSER_ROLE`.
     */
    function unpause() public virtual {
        require(hasRole(PAUSER_ROLE, _msgSender()), "ERC20PresetMinterPauser: must have pauser role to unpause");
        _unpause();
    }

    function _beforeTokenTransfer(address from, address to, uint256 amount) internal virtual override(ERC20, ERC20Pausable) {
        super._beforeTokenTransfer(from, to, amount);
    }
    
    function _transfer(address sender, address recipient, uint256 amount) internal virtual override(ERC20) {
        
        _beforeTokenTransfer(sender, recipient, amount);

        //MTG Fair Launch Check
        if (fairLaunch && ((block.timestamp >= fairDateStart) && (block.timestamp <= fairDateEnd))) {
            require(amount<= fairAmount, "FairLaunch: amount above the threshold");
        }
        
        //MTG Checking if _from or _to are excluded from taxes and Fair Launch
        bool takeFees = !(_exemptions[sender] || _exemptions[recipient]);
        
        uint256 receiveAmount = amount;
        if (takeFees) {
            TaxCalculationParameters memory taxParams = calculateTaxes(_taxRates, amount);
            collectVaultTaxes(taxParams, sender);
            receiveAmount = receiveAmount.sub(taxParams.totalValue);
        }
        
        _balances[sender] = _balances[sender].sub(amount, "ERC20: transfer amount exceeds balance");
        _balances[recipient] = _balances[recipient].add(receiveAmount);
        emit Transfer(sender, recipient, amount);
    }
    
    //MTG Internal function
    //Calculate Taxes
    function calculateTaxes(TaxRates memory taxes, uint256 tokenAmount) internal pure returns (TaxCalculationParameters memory) {
        TaxCalculationParameters memory params;

        params.totalValue = tokenAmount.mul(taxes.total).div(_HUNDRED_PERCENT);
        params.burnValue = tokenAmount.mul(taxes.burn).div(_HUNDRED_PERCENT);
        params.gameValue = tokenAmount.mul(taxes.game).div(_HUNDRED_PERCENT);
        params.farmingValue = tokenAmount.mul(taxes.farming).div(_HUNDRED_PERCENT);
        params.teamValue = params.totalValue.sub(params.burnValue).sub(params.gameValue).sub(params.farmingValue);
        
        return params;
    }
    
    //Collect Taxes
    function collectVaultTaxes(TaxCalculationParameters memory params, address sender) internal {
        _balances[_vaults.burn] = _balances[_vaults.burn].add(params.burnValue);
        emit Transfer(sender, _vaults.burn, params.burnValue);
        
        _balances[_vaults.team] = _balances[_vaults.team].add(params.teamValue);
        emit Transfer(sender, _vaults.team, params.teamValue);
        
        _balances[_vaults.game] = _balances[_vaults.game].add(params.gameValue);
        emit Transfer(sender, _vaults.game, params.gameValue);
        
        _balances[_vaults.farming] = _balances[_vaults.farming].add(params.farmingValue);
        emit Transfer(sender, _vaults.farming, params.farmingValue);
    }
    
    //MTG admin  
    // Setup FairLaunch Params (True/false, Date Start, Date End, Max Amount)
    function setFairLaunch(bool _fairLaunch, uint256 _fairDateStart, uint256 _fairDateEnd, uint256 _fairAmount) external {
        require(hasRole(DEFAULT_ADMIN_ROLE, _msgSender()), "MTG: must have Admin role to set FairLaunch");
        
        fairLaunch = _fairLaunch;
        fairDateStart = _fairDateStart;
        fairDateEnd = _fairDateEnd;
        fairAmount = _fairAmount;
        
         emit FairLaunchUpdated(_fairLaunch, _fairDateStart, _fairDateEnd, _fairAmount);
    }
    
    // Setup Vaults Addresses
    function setVaultsAddresses(address burn, address team, address game, address farming) external  {
        require(hasRole(DEFAULT_ADMIN_ROLE, _msgSender()), "MTG: must have Admin role to set Vaults Addresses");
        Vaults storage vaults = _vaults;

        vaults.burn = burn;
        vaults.team = team;
        vaults.game = game;
        vaults.farming = farming;

        _exemptions[vaults.burn] = true;
        _exemptions[vaults.team] = true;
        _exemptions[vaults.game] = true;
        _exemptions[vaults.farming] = true;

        emit VaultsUpdated(burn, team, game, farming);
    }

    // Update Taxes
    function updateTaxes(uint32 _total, uint32 _burn, uint32 _team, uint32 _game, uint32 _farming) external {
        require(hasRole(DEFAULT_ADMIN_ROLE, _msgSender()), "MTG: must have Admin role to set TaxRates");
        _taxRates.total = _total;
        _taxRates.burn = _burn;
        _taxRates.team = _team;
        _taxRates.game = _game;
        _taxRates.farming = _farming;

        emit TaxRatesUpdated(_taxRates.total);
    }
    
    // Update Account Exclusion from Tax
    function updateAccountExclusionState(address account, bool excludeFromTaxes) external {
        require(hasRole(DEFAULT_ADMIN_ROLE, _msgSender()), "MTG: must have Admin role to set Account Exclusion State");
        _exemptions[account] = excludeFromTaxes;

        emit AccountExclusionStateChanged(account, excludeFromTaxes);
    }
    
    // Retrieve stuck funds
    function retrieveStuckFunds() external {
        require(hasRole(DEFAULT_ADMIN_ROLE, _msgSender()), "MTG: must have Admin role to retrieve stuck funds");
        address payable _to = msg.sender;
        uint256 _balance = address(this).balance;
        _to.transfer(_balance);
    }
    
    // Retrieve ERC20 stuck
    function retrieveStuckTokens(address _token, uint256 _amount) external {
        require(hasRole(DEFAULT_ADMIN_ROLE, _msgSender()), "MTG: must have Admin role to retrieve stuck tokens");
        ERC20(_token).transfer( _msgSender(), _amount);
    }
    
    // Return current Time
    function getCurrentTime() external view returns(uint256) {
        return block.timestamp;
    }
}
