{"CREToken.sol":{"content":"// SPDX-License-Identifier: GPL-3.0-or-later
pragma solidity >=0.6.0;
import "./IBEP20.sol";
import "./SafeMath.sol";

 
contract CREToken is IBEP20
{
    using SafeMath for uint256;
    address _owner;
    address _minerpool;
    address _miner;
    address _team;
    uint256 _maxSupply= 68000000 * 1e8;

     string constant  _name = 'CRuisE';
     string constant _symbol = 'CRE';
     uint8 immutable _decimals = 8;
 
    address _pancakeAddress;
    uint256 _totalsupply;
    uint256 _totalUserBalance;
    uint256 _onecut;
 
    mapping(address => mapping (address => uint256)) private _allowances;
    mapping(address=>bool) _isExcluded;
    mapping(address=>bool) _minter;
    mapping(address=>uint256) _balances;
    mapping(address=>uint256) _pending;
    mapping(address=>uint256) _cutted;
    address _feeowner;

    uint256 _onebuylimit;
    uint256 _onebymax;


  
    constructor(address feeowner,address team)
    {
        _owner = msg.sender;
        _mint(_owner,_maxSupply);
        _isExcluded[_owner]=true;
        _feeowner=feeowner;
        _team =team;
        _onebuylimit= 2200* 1e8;
        _onebymax= 5000* 1e8;
        _minerpool=_owner;
    }

    function setMinerpool(address miner,address minerpool) public
    {
         require(msg.sender==_owner);
         _miner=miner;
         _minerpool=minerpool;
    }

    function setPancakeAddress(address pancakeAddress) public
    {
        require(msg.sender==_owner);
        _pancakeAddress=pancakeAddress;
    }

    function setOneLimit(uint256 amount) public
    {
        require(msg.sender==_owner);
        _onebuylimit=amount;
    }

    function setOneMax(uint256 amount) public
    {
        require(msg.sender==_owner);
        _onebymax=amount;
    }

    function setTeam(address feeowner,address team) public
    {
         require(msg.sender==_owner);
         _feeowner=feeowner;
        _team =team;
    }


    function name() public  pure returns (string memory) {
        return _name;
    }

    function symbol() public  pure returns (string memory) {
        return _symbol;
    }

    function decimals() public pure returns (uint8) {
        return _decimals;
    }

    function totalSupply() public view override returns (uint256) {
        return _totalsupply;
    }

    function AddExcluded(address account) public 
    {
        require(msg.sender== _owner);
        _isExcluded[account] =true;
    }

    function totalUserBalance() public view returns (uint256)
    {
        return _totalUserBalance;
    }

    function _approve(address owner, address spender, uint256 amount) private {
        require(owner != address(0), "ERC20: approve from the zero address");
        require(spender != address(0), "ERC20: approve to the zero address");

        _allowances[owner][spender] = amount;
        emit Approval(owner, spender, amount);
    }

    function balanceOf(address account) public view override returns (uint256) {
        return _balances[account];
    }
	
	function addMinter(address account) public
	{
		require(msg.sender== _owner);
		_minter[account]=true;
	}

    function takeOutErrorTransfer(address tokenaddress) public
    {
        require(msg.sender==_owner);
        IBEP20(tokenaddress).transfer(_owner, IBEP20(tokenaddress).balanceOf(address(this)));
    }

    function mint(address account,uint256 amount) public 
    {
        require(_minter[msg.sender]==true,"Must be minter");
        _mint(account,amount);
    }

    function _mint(address account, uint256 amount) private {
        require(account != address(0), 'BEP20: mint to the zero address');
        require(totalSupply().add(amount) <=_maxSupply,"MAX SUPPLY OVER");
        _totalsupply=_totalsupply.add(amount);
        UserBalancechanged(account,amount,true);
        emit Transfer(address(0), account, amount);
    }


    function allowance(address owner, address spender) public view override returns (uint256) {
        return _allowances[owner][spender];
    }

    function isExcluded(address account) public view returns (bool) {
        return _isExcluded[account];
    }

    function approve(address spender, uint256 amount) public override returns (bool) {
        _approve(msg.sender, spender, amount);
        return true;
    }

    function transferFrom(address sender, address recipient, uint256 amount) public override returns (bool) {
        _approve(sender, msg.sender, _allowances[sender][msg.sender].sub(amount, "ERC20: transfer amount exceeds allowance"));
        _transfer(sender, recipient, amount);
        return true;
    }

   function transfer(address recipient, uint256 amount) public override returns (bool) {
        _transfer(msg.sender, recipient, amount);
        return true;
    }

   function increaseAllowance(address spender, uint256 addedValue) public  returns (bool) {
        _approve(msg.sender, spender, _allowances[msg.sender][spender].add(addedValue));
        return true;
    }

    function decreaseAllowance(address spender, uint256 subtractedValue) public  returns (bool) {
        _approve(msg.sender, spender, _allowances[msg.sender][spender].sub(subtractedValue, "ERC20: decreased allowance below zero"));
        return true;
    }

    function burnFrom(address sender, uint256 amount) public override  returns (bool)
    {
         _approve(sender, msg.sender, _allowances[sender][msg.sender].sub(amount, "ERC20: transfer amount exceeds allowance"));
        _burn(sender,amount);
        return true;
    }

    function burn(uint256 amount) public override returns (bool)
    {
        _burn(msg.sender,amount);
        return true;
    }
 
    function _burn(address sender,uint256 tAmount) private
    {
         require(sender != address(0), "ERC20: transfer from the zero address");
        require(tAmount > 0, "Transfer amount must be greater than zero");
        if(_totalsupply.sub(_balances[address(0)]) >1000000 * 1e8)
        {
            UserBalancechanged(sender,tAmount,false);
            _balances[address(0)] = _balances[address(0)].add(tAmount);
            emit Transfer(sender, address(0), tAmount);
        }
    }

   function isContract(address account) public view returns (bool) {
        bytes32 codehash;
        bytes32 accountHash = 0xc5d2460186f7233c927e7db2dcc703c0e500b653ca82273b7bfad8045d85a470;
        // solhint-disable-next-line no-inline-assembly
        assembly {
            codehash := extcodehash(account)
        }
        return (codehash != accountHash && codehash != 0x0);
    }

    function getPendingCut(address user) public view returns (uint256)
    {
         if(_onecut==0)
            return 0;
         uint256 total=0;
         if(_onecut > _cutted[user])
            total=_balances[user].mul(_onecut.sub(_cutted[user])).div(1e20);
        return _pending[user].add(total);
    }

    function UserCreditGeted(address user) public
    {
        require(msg.sender==_miner);
        _pending[user]=0;
        _cutted[user] = _onecut;
    }

    function UserBalancechanged(address user,uint256 amount,bool add) private
    {
        if(add)
        {
            _balances[user] =_balances[user].add(amount);
            if(isContract(user))
                 return;
             if(_balances[user] > amount)
                _pending[user]= getPendingCut(user);
             _totalUserBalance= _totalUserBalance.add(amount);
             _cutted[user] = _onecut;
        }
        else
        {
            _balances[user] =_balances[user].sub(amount);
            if(isContract(user))
                 return;
             _pending[user]= getPendingCut(user);
             _totalUserBalance= _totalUserBalance.sub(amount);
             _cutted[user] = _onecut;
        }

    }


    function _transfer(address sender, address recipient, uint256 amount) private {
        require(sender != address(0), "ERC20: transfer from the zero address");
        require(recipient != address(0), "ERC20: transfer to the zero address");
  
        uint256 toamount = amount;
        uint256 addshare=0;
        
        if(_totalsupply.sub(_balances[address(0)]) >1000000 * 1e8)
        {
            if(recipient == _pancakeAddress  || sender==_pancakeAddress)
            {
                if(!isExcluded(sender) && !isExcluded(recipient))
                {
                    if(sender==_pancakeAddress)
                    {
                        if(amount> _onebuylimit)
                            revert("Max buycount to account is 2000");
                    
                        if(amount.mul(9).div(10).add(_balances[recipient]) > _onebymax)
                            revert("Max balance 5000");
                    }
                     
                    uint256 onepercent = amount.mul(1).div(100);
                    if(onepercent > 0)
                    {
                        uint256 p = onepercent.mul(10);
                        addshare=onepercent.mul(3);
                        _balances[address(0)]=_balances[address(0)].add(addshare);
                        emit Transfer(sender, address(0), addshare);
                        
                        _balances[_minerpool]= _balances[_minerpool].add(onepercent);
                        emit Transfer(sender, _minerpool, onepercent);
                        toamount = amount.sub(p);
                        UserBalancechanged(_team,onepercent.mul(2),true);
                        emit Transfer(sender, _team, onepercent.mul(2));
                        UserBalancechanged(_feeowner,onepercent.mul(4),true);
                        emit Transfer(sender, _feeowner, onepercent.mul(4));
                    }
                }
            }
        }
        
        UserBalancechanged(sender,amount,false);
        UserBalancechanged(recipient,toamount,true);

        if(addshare > 0)
        {
            _onecut = _onecut.add(addshare.mul(1e20).div(_totalUserBalance));
        }
        emit Transfer(sender, recipient, toamount);
    }
    
}"},"IBEP20.sol":{"content":"// SPDX-License-Identifier: GPL-3.0-or-later

