    "contracts/BLKSwap.sol": {
      "content": "//SPDX-License-Identifier: MIT

import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import {IERC20Metadata as IERC20M} from "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@chainlink/contracts/src/v0.8/interfaces/AggregatorV3Interface.sol";

pragma solidity ^0.8.19;

contract BLKSwap is Ownable {
    using SafeERC20 for IERC20;

    struct OracleData {
        // struct to store oracle data, packing data to save gas: 20 + 1 + 1 = 22/32 bytes
        uint8 tokenDecimals;
        uint8 oracleDecimals;
        address oracleAddress;
    }

    struct EurUsdOracleData {
        address oracleAddress;
        uint8 oracleDecimals;
    }

    mapping(address => OracleData) public tokenOracles;
    EurUsdOracleData public eurUsdOracle;

    event SwapIntoBLK(
        address indexed account,
        address tokenIn,
        uint256 depositedAmount,
        uint256 price,
        uint256 decimals
    );
    event AddedToTokenOracles(address indexed account, address oracleAddress);
    event RemovedFromTokenOracles(
        address indexed account,
        address oracleAddress
    );
    error PriceLessThanZero(address oracle);
    error ZeroAddress();
    error ZeroAmount();
    error NotEnoughBalance(address account);
    error tokenNotSupported(address token);

    constructor(address _ethOracle, address _eurUsdOracle) {
        tokenOracles[address(0)] = OracleData(
            18,
            AggregatorV3Interface(_ethOracle).decimals(),
            _ethOracle
        );
        eurUsdOracle = EurUsdOracleData(
            _eurUsdOracle,
            AggregatorV3Interface(_eurUsdOracle).decimals()
        );
    }

    function getTokenOracle(address _token) public view returns (address) {
        return tokenOracles[_token].oracleAddress;
    }

    function getAllTokenData(
        address _token
    ) public view returns (OracleData memory) {
        return tokenOracles[_token];
    }

    function addOrChangeTokenOracle(
        address _newToken,
        address _newTokenOracle
    ) external onlyOwner {
        tokenOracles[_newToken] = OracleData(
            IERC20M(_newToken).decimals(),
            AggregatorV3Interface(_newTokenOracle).decimals(),
            _newTokenOracle
        );
        emit AddedToTokenOracles(_newToken, _newTokenOracle);
    }

    function removeTokenOracle(address _toBeRemoved) external onlyOwner {
        delete tokenOracles[_toBeRemoved];
        emit RemovedFromTokenOracles(_toBeRemoved, address(0));
    }

    function getPriceFromOracle(address _token) public view returns (uint256) {
        OracleData memory data = tokenOracles[_token];
        if (data.oracleAddress == address(0)) revert tokenNotSupported(_token);
        (, int256 latestPrice, , , ) = AggregatorV3Interface(data.oracleAddress)
            .latestRoundData();
        //require(latestPrice > 0, "BLKSwap: price must be greater than 0");
        if (latestPrice < 0) revert PriceLessThanZero(data.oracleAddress);
        //uint priceInUsd = uint256(latestPrice) *
        //    10 ** (data.tokenDecimals - data.oracleDecimals); // latestPriceConverted = latestPrice * 10^(tokenDecimals - oracleDecimals)
        (, int eurUsdPrice, , , ) = 
            AggregatorV3Interface(eurUsdOracle.oracleAddress).latestRoundData()
        ;
        if (eurUsdPrice < 0) revert PriceLessThanZero(eurUsdOracle.oracleAddress);
        uint priceInUsd = uint256(latestPrice) * 10 ** (18 - data.oracleDecimals);
        uint eurUsdPriceTo18Decimals = uint(eurUsdPrice) *
            10 ** (18 - eurUsdOracle.oracleDecimals);
        return (priceInUsd * 10 ** 18 / eurUsdPriceTo18Decimals);
    }

    function estimateTokenIn(
        address _token,
        uint256 _amount
    ) external view returns (uint256) {
        uint256 decimalsToken = tokenOracles[_token].tokenDecimals;
        return (_amount * getPriceFromOracle(_token)) / (10 ** decimalsToken);
    }

    function estimateEthIn(uint256 _amount) external view returns (uint256) {
        return (_amount * getPriceFromOracle(address(0))) / 10 ** 18;
    }

    function swapTokenIn(address _token, uint256 _amount) external {
        //require(_amount > 0, "BLKSwap: amount must be greater than 0");
        if (_amount == 0) revert ZeroAmount();
        emit SwapIntoBLK(
            msg.sender,
            _token,
            _amount,
            getPriceFromOracle(_token),
            tokenOracles[_token].tokenDecimals
        );
        IERC20(_token).safeTransferFrom(msg.sender, address(this), _amount);
    }

    function swapEthIn() external payable {
        //require(msg.value > 0, "BLKSwap: amount must be greater than 0");
        if (msg.value == 0) revert ZeroAmount();
        OracleData memory ethOracle = getAllTokenData(address(0));
        emit SwapIntoBLK(
            msg.sender,
            address(0),
            msg.value,
            getPriceFromOracle(address(0)),
            ethOracle.tokenDecimals
        );
    }

    function withdrawEth() external onlyOwner {
        Address.sendValue(payable(msg.sender), address(this).balance);
    }

    function withdrawToken(address _token) external onlyOwner {
        IERC20(_token).safeTransfer(
            msg.sender,
            IERC20(_token).balanceOf(address(this))
        );
    }
}
