    "contracts/SoulNFT.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@cryptolink/contracts/bridge/BridgeClient.sol";
import "@openzeppelin/contracts/utils/Base64.sol";
import "@openzeppelin/contracts/utils/Counters.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/token/ERC721/extensions/ERC721Enumerable.sol";
import "@openzeppelin/contracts/utils/Strings.sol";
import "./libraries/UniswapV2.sol";
import "./interfaces/ISoulStake.sol";
import "./interfaces/ISoulToken.sol";
import "./interfaces/ISoulNFT.sol";
import "./interfaces/ISoulAccountant.sol";
import "./interfaces/IWETH.sol";

/*
 * The Soul Collection
 * https://thesoulcol.com
 * Twitter @TheSoulCol
 */
contract SoulNFT is ERC721Enumerable, BridgeClient {
    struct SoulData {
        string  name;
        uint    image;
        uint    birthdate;
        uint    mintdate;
        address signer;
        uint    bridges;
    }

    using Counters for Counters.Counter;
    Counters.Counter private SOUL_COUNTER;

    IUniswapV2Pair   private immutable SOULPOOL;
    ISoulToken       private immutable TSOUL;
    IUniswapV2Router private immutable ROUTER;
    ISoulAccountant  private immutable ACCOUNTANT;
    ISoulStake       private immutable SOULSTAKE;
    IERC20           private immutable STABLE;
    IWETH            private immutable WETH;

    uint private constant PRICE        = 30 ether; // 30 USDC total mint price
    uint private constant REWARD       =  5 ether; //  5 USDC as a referal reward
    uint private constant STAKE_REWARD =  5 ether; //  5 USDC to stSOUL holders

    mapping(uint => SoulData) private SOUL_DATA;

    string public IMG_BASE = "https://cdn.thesoulcol.com/nft/";
    string public constant SOUL_CONTRACT =
        "The owner of this NFT hereby assumes full ownership of the soul originally belonging to the undersigned. "
        "The minter of this NFT, as owner of their own soul, and on behalf of themselves, in sound mind and body, "
        "shall relinquish the ownership of their mortal soul for eternity, and forever relinquish said soul to the  "
        "owner of this NFT as long as this NFT exists. I, the undersigned, as attested to by signature of my personal "
        "crypto wallet, willingly agree to fully surrender my soul unconditionally, and understand that my soul shall "
        "forever be attached to this NFT, and thereby the owner of this NFT, as long as this NFT exists.";

    event MetadataUpdate(uint256 soulId);
    event SoulMinted(address wallet, uint soulId);
    event SoulSent(address wallet, uint destChainId, uint soulId);
    event SoulReceived(address wallet, uint sourceChainId, uint soulId);

    constructor(ISoulToken _tsoul, ISoulStake _soulstake) ERC721("The Soul Collection", "SOULNFT") {
        require(address(_tsoul)     != address(0), "invalid tsoul address");
        require(address(_soulstake) != address(0), "invalid soulstake address");

        SOULSTAKE  = _soulstake;
        TSOUL      = _tsoul;
        ACCOUNTANT = ISoulAccountant(_tsoul.ACCOUNTANT());
        SOULPOOL   = IUniswapV2Pair(_tsoul.SOULPOOL());
        require(address(ACCOUNTANT) != address(0), "invalid accountant address");
        require(address(SOULPOOL)   != address(0), "invalid tsoul lp pool address");

        WETH   = IWETH(ACCOUNTANT.WETH());
        STABLE = IERC20(ACCOUNTANT.STABLE());
        ROUTER = IUniswapV2Router(ACCOUNTANT.ROUTER());
        require(address(WETH)   != address(0), "invalid weth address");
        require(address(STABLE) != address(0), "invalid stable address");
        require(address(ROUTER) != address(0), "invalid router address");
    }

    /** PUBLIC */
    function buy(address _token, uint _amount, string calldata _name, uint _birthdate, uint _image, address _ref) external payable returns (uint _soulId) {
        address _me = address(this);

        // convert token to STABLE
        if(_token == address(0)) {
            IWETH(address(WETH)).deposit{value: msg.value}();
            UniswapV2.swap(address(ROUTER), address(WETH), address(STABLE), _amount, _me, PRICE);
        } else if(_token != address(STABLE)) {
            SafeERC20.safeTransferFrom(IERC20(_token), msg.sender, _me, _amount);
            UniswapV2.swap(address(ROUTER), _token, address(STABLE), _amount, _me, PRICE);
        } else {
            SafeERC20.safeTransferFrom(STABLE, msg.sender, _me, PRICE);
        }

        // swap STABLE for TSOUL and send rewards to STSOUL holders
        uint _rewardAmount = UniswapV2.swap(address(ROUTER), address(STABLE), address(TSOUL), STAKE_REWARD, _me, 0);

        TSOUL.approve(address(SOULSTAKE), _rewardAmount);
        SOULSTAKE.addReward(_rewardAmount);

        // send referal reward if set
        if(_ref != address(0)) {
            SafeERC20.safeTransfer(STABLE, _ref, REWARD);
        }

        // send remaining balance to ACCOUNTANT
        SafeERC20.safeTransfer(STABLE, address(ACCOUNTANT), STABLE.balanceOf(_me));

        // mint/send NFT
        _soulId = _mintNewSoul(msg.sender, _name, _birthdate, _image, msg.sender);
        emit SoulMinted(msg.sender, _soulId);
    }

    /** BRIDGE SENDER */
    function bridge(address _token, uint _amount, address _to, uint _chainId, uint _soulId) public payable onlyActiveBridge(_chainId) returns (uint _txId) {
        require(_ownerOf(_soulId) == msg.sender, "you do not own this soul");
        address _me = address(this);

        // convert token to stable, making sure that we get at least the required bridge price
        if(_token == address(0)) {
            IWETH(address(WETH)).deposit{value: msg.value}();
            UniswapV2.swap(address(ROUTER), address(WETH), address(STABLE), _amount, _me, CHAINS[_chainId].price);
        } else if(_token != address(STABLE)) {
            SafeERC20.safeTransferFrom(IERC20(_token), msg.sender, _me, _amount);
            UniswapV2.swap(address(ROUTER), _token, address(STABLE), _amount, _me, CHAINS[_chainId].price);
        } else {
            SafeERC20.safeTransferFrom(STABLE, msg.sender, _me, CHAINS[_chainId].price);
        }

        // burn this soul!
        _burn(_soulId);
        emit MetadataUpdate(_soulId);

        // swap for bridge token
        UniswapV2.swap(address(ROUTER), address(STABLE), address(PAPER), STABLE.balanceOf(_me), _me, 0);

        // send bridge request
        _txId = _sendMessage(_chainId, address(ACCOUNTANT), abi.encode(_to, _soulId, SOUL_DATA[_soulId]));

        // swap rest for stable and send to ACCOUNTANT
        UniswapV2.swap(address(ROUTER), address(PAPER), address(STABLE), PAPER.balanceOf(_me), address(ACCOUNTANT), 0);

        emit SoulSent(msg.sender, _chainId, _soulId);
    }

    /** BRIDGE RECEIVER */
    function messageProcess(uint, uint _sourceChainId, address _sender, address, uint256, bytes calldata _data) external override onlySelfBridge(_sender, _sourceChainId) {
        // process data package from source chain
        (address _to, uint _soulId, SoulData memory _soulData) = abi.decode(_data, (address, uint, SoulData));

        // bring this soul back!
        _safeMint(_to, _soulId);
        _soulData.bridges++;
        SOUL_DATA[_soulId] = _soulData;
        emit MetadataUpdate(_soulId);

        emit SoulReceived(_to, _sourceChainId, _soulId);
    }

    /** INTERNAL */
    function _mintNewSoul(address _to, string calldata _name, uint _birthdate, uint _image, address _signer) private returns (uint _soulId) {
        //require(_image > 0 && _image < 11, "invalid image"); // valid image numbers are 1-10

        uint _currId = SOUL_COUNTER.current();
        require(_currId <= 9999999, "souls at max capacity");

        _soulId = (block.chainid * 10**7) + _currId;
        _safeMint(_to, _soulId);
        emit MetadataUpdate(_soulId);

        SOUL_DATA[_soulId].name      = _name;
        SOUL_DATA[_soulId].image     = _image;
        SOUL_DATA[_soulId].birthdate = _birthdate;
        SOUL_DATA[_soulId].mintdate  = block.timestamp;
        SOUL_DATA[_soulId].signer    = _signer;

        SOUL_COUNTER.increment();
    }

    /** VIEWS */
    function soulsOfOwner(address _owner) external view returns (uint[] memory, string[] memory) {
        uint _soulCount = balanceOf(_owner);
        uint[] memory _soulIds = new uint[](_soulCount);
        string[] memory _soulURIs = new string[](_soulCount);

        for (uint x = 0; x < _soulCount; x++) {
            _soulIds[x] = tokenOfOwnerByIndex(_owner, x);
            _soulURIs[x] = tokenURI(_soulIds[x]);
        }

        return (_soulIds, _soulURIs);
    }

    function tokenData(uint _soulId) public view returns (SoulData memory _soulData) {
        return SOUL_DATA[_soulId];
    }

    function tokenURI(uint _soulId) public view override returns (string memory _uri) {
        SoulData memory _soulData = SOUL_DATA[_soulId];
        require(_soulData.mintdate != 0, "Soul does not exist");

        // @note toHexString will lose the upper-lower case mix for the checksum, every front-end lib will be able to restore the checksum
        // @note uint160 is automatically padded to uint256 even without the additional typecasting
        _uri = string(abi.encodePacked("data:application/json;base64,", 
            Base64.encode(bytes(string(abi.encodePacked('{',
                '"description":"', SOUL_CONTRACT, '",',
                '"external_url":"https://thesoulcol.com/",',
                '"image":"', IMG_BASE, Strings.toString(_soulData.image), '.png",',
                '"name":"Soul of ', _soulData.name, '",',
                '"attributes":[',
                    '{"display_type":"date","trait_type": "Birthdate", "value": ', Strings.toString(_soulData.birthdate),'},',
                    '{"display_type":"date","trait_type": "Signed On", "value": ', Strings.toString(_soulData.mintdate), '},',
                    '{"trait_type":"Signer","value":"', Strings.toHexString(_soulData.signer), '"},',
                    '{"display_type":"number","trait_type": "Jumps", "value": ', Strings.toString(_soulData.bridges), '},',
                    '{"trait_type":"Contract","value":"', SOUL_CONTRACT, '"}',
                ']',
            '}'))))
        ));
    }
}"
    }
  },
  "settings": {
    "optimizer": {
      "enabled": true,
      "runs": 2
    },
    "viaIR": true,
    "outputSelection": {
      "*": {
        "*": [
          "evm.bytecode",
          "evm.deployedBytecode",
          "devdoc",
          "userdoc",
          "metadata",
          "abi"
        ]
      }
    },
    "metadata": {
      "useLiteralContent": true
    },
    "libraries": {}
  }
}}
