pragma solidity ^0.8.9;









contract Mars is ERC20, Ownable {
    IUniswapV2Router02 public uniswapV2Router;
    IUniswapV2Pair public uniswapV2Pair;
    TokenDistributor public tokenDistributor;
    DividendDistributor public sellDividendDistributor;
    uint256 public constant SELL_DIVIDEND_USER_NUMBER = 30;
    ICompound public compound;

    address public usdt;
    address public constant DESTROY_ADDRESS = address(0x000000000000000000000000000000000000dEaD);
    address public nodeAddress;
    address public addLiquidityAddress;
    address public marketAddress;
    address public fleetVaultAddress;
    address public marketFeeReceiver;

    uint256 private constant SELL_BURN_FEE = 1;
    uint256 private constant SELL_ADD_TO_LIQUIDITY_FEE = 1;
    uint256 private constant SELL_MARKET_FEE = 1;
    uint256 private constant SELL_FUND_FEE = 2;

    uint256 public numTokensSellToAddToLiquidity = 100 * 10 ** super.decimals();
    uint256 public dividendStartTime;
    uint256 private _openSwapTime;

    bool public inSwap;
    bool public inDividend;

    mapping(address => bool) public isExcludedFromFee;
    mapping(uint256 => bool) public dividendMap;

    modifier lockTheSwap() {
        require(!inSwap, "MARS: inSwap locked");
        inSwap = true;
        _;
        inSwap = false;
    }

    constructor(
        string memory name_,
        string memory symbol_,
        address compoundAddress_,
        address nodeAddress_,
        address addLiquidityAddress_,
        address marketAddress_,
        address fleetVaultAddress_,
        address marketFeeReceiver_,
        address router_,
        address usdt_,
        uint256 openSwapTime_
    ) ERC20(name_, symbol_) {
        nodeAddress = nodeAddress_;
        addLiquidityAddress = addLiquidityAddress_;
        marketAddress = marketAddress_;
        fleetVaultAddress = fleetVaultAddress_;
        marketFeeReceiver = marketFeeReceiver_;
        usdt = usdt_;
        _openSwapTime = openSwapTime_;
        dividendStartTime = block.timestamp;

        IUniswapV2Router02 _uniswapV2Router = IUniswapV2Router02(router_);
        uniswapV2Pair = IUniswapV2Pair(IUniswapV2Factory(_uniswapV2Router.factory()).createPair(address(this), usdt));
        uniswapV2Router = _uniswapV2Router;

        compound = ICompound(compoundAddress_);

        _mint(nodeAddress, 1000_000 * 10 ** decimals());
        _mint(addLiquidityAddress, 1000_000 * 10 ** decimals());
        _mint(marketAddress, 3000_000 * 10 ** decimals());
        _mint(fleetVaultAddress, 5000_000 * 10 ** decimals());

        isExcludedFromFee[address(this)] = true;

        sellDividendDistributor = new DividendDistributor();
        tokenDistributor = new TokenDistributor(usdt);
    }

    function setNumTokensSellToAddToLiquidity(uint256 amount) public onlyOwner {
        numTokensSellToAddToLiquidity = amount;
    }

    function setCompound(address compound_) public onlyOwner {
        compound = ICompound(compound_);
    }

    function _transfer(address from, address to, uint256 amount) internal override {
        if (from != address(uniswapV2Pair)) {
            if (balanceOf(address(this)) >= numTokensSellToAddToLiquidity && !inSwap) {
                swapAndERC20Liquify(numTokensSellToAddToLiquidity);
            }
        }

        uint256 dividendIntervalDays = getDividendIntervalDays();
        if (dividendIntervalDays > 0 && !dividendMap[dividendIntervalDays] && !inSwap) {
            _takeSellDividend();
            dividendMap[dividendIntervalDays] = true;
        }

        bool buy = false;
        bool sell = false;
        bool takeFee = false;
        if (from == address(uniswapV2Pair) && !_isRemoveLiquidity()) {
            buy = true;
            require(block.timestamp >= _openSwapTime || isExcludedFromFee[to], "MARS: Can not swap");
        } else if (to == address(uniswapV2Pair) && !_isAddLiquidity()) {
            sell = true;
            require(block.timestamp >= _openSwapTime || isExcludedFromFee[from], "MARS: Can not swap");
            if (!isExcludedFromFee[from]) {
                takeFee = true;
            }
        }

        if (takeFee) {
            if (sell) {
                if (amount == balanceOf(from)) {
                    amount = (amount * 99) / 100;
                }
                _tokenSell(from, to, amount, SELL_BURN_FEE);
            }
        } else {
            super._transfer(from, to, amount);
        }
    }

    function _tokenSell(address from, address to, uint256 amount, uint256 sellBurnFee) private {
        uint256 burnAmount = (amount * sellBurnFee) / 100;
        uint256 addLiquifyAmount = (amount * SELL_ADD_TO_LIQUIDITY_FEE) / 100;
        uint256 marketAmount = (amount * SELL_MARKET_FEE) / 100;
        uint256 fundAmount = (amount * SELL_FUND_FEE) / 100;

        super._transfer(from, DESTROY_ADDRESS, burnAmount);
        super._transfer(from, address(this), addLiquifyAmount);
        super._transfer(from, marketFeeReceiver, marketAmount);
        super._transfer(from, address(sellDividendDistributor), fundAmount);
        super._transfer(from, to, amount - burnAmount - marketAmount - addLiquifyAmount - fundAmount);
    }

    function swapAndERC20Liquify(uint256 contractTokenBalance) private lockTheSwap {
        uint256 swapLpAmount = contractTokenBalance / 2;
        uint256 addLpAmount = contractTokenBalance - swapLpAmount;
        swapTokensForAnyERC20Token(swapLpAmount);
        IERC20 USDT = IERC20(usdt);
        uint256 tokenBalance = USDT.balanceOf(address(tokenDistributor));
        if (tokenBalance > 0) {
            USDT.transferFrom(address(tokenDistributor), address(this), tokenBalance);
            addLiquidityERC20(addLpAmount, tokenBalance);
        }
    }

    function swapTokensForAnyERC20Token(uint256 tokenAmount) private {
        if (tokenAmount > 0) {
            address[] memory path = new address[](2);
            path[0] = address(this);
            path[1] = usdt;

            _approve(address(this), address(uniswapV2Router), totalSupply());

            uniswapV2Router.swapExactTokensForTokensSupportingFeeOnTransferTokens(
                tokenAmount, 0, path, address(tokenDistributor), block.timestamp
            );
        }
    }

    function addLiquidityERC20(uint256 tokenAmount, uint256 usdtAmount) private {
        _approve(address(this), address(uniswapV2Router), tokenAmount);
        IERC20(usdt).approve(address(uniswapV2Router), usdtAmount);

        uniswapV2Router.addLiquidity(
            address(this), usdt, tokenAmount, usdtAmount, 0, 0, DESTROY_ADDRESS, block.timestamp
        );
    }

    function setDividendStartTime(uint256 _startTime) public onlyOwner {
        dividendStartTime = _startTime;
    }

    function _isAddLiquidity() internal view returns (bool isAdd) {
        IUniswapV2Pair mainPair = uniswapV2Pair;
        (uint256 r0, uint256 r1,) = mainPair.getReserves();

        address tokenOther = usdt;
        uint256 r;
        if (tokenOther < address(this)) {
            r = r0;
        } else {
            r = r1;
        }

        uint256 bal = IERC20(tokenOther).balanceOf(address(uniswapV2Pair));
        isAdd = bal > r;
    }

    function _isRemoveLiquidity() internal view returns (bool isRemove) {
        IUniswapV2Pair mainPair = IUniswapV2Pair(uniswapV2Pair);
        (uint256 r0, uint256 r1,) = mainPair.getReserves();

        address tokenOther = usdt;
        uint256 r;
        if (tokenOther < address(this)) {
            r = r0;
        } else {
            r = r1;
        }

        uint256 bal = IERC20(tokenOther).balanceOf(address(mainPair));
        isRemove = r >= bal;
    }

    function excludedFromFee(address addr) public onlyOwner returns (bool) {
        isExcludedFromFee[addr] = true;
        return true;
    }

    function includedFromFee(address addr) public onlyOwner returns (bool) {
        isExcludedFromFee[addr] = false;
        return true;
    }

    function withdrawERC20(address tokenAdress, address to) public onlyOwner returns (bool) {
        IERC20 token = IERC20(tokenAdress);
        return token.transfer(to, token.balanceOf(address(this)));
    }

    function totalSupply() public view override returns (uint256) {
        return super.totalSupply() - super.balanceOf(DESTROY_ADDRESS);
    }

    function getDividendIntervalDays() public view returns (uint256) {
        return (block.timestamp - dividendStartTime) / 1 days;
    }

    function _takeSellDividend() private lockTheSwap {
        address[] memory users = compound.getLPTopUsers(SELL_DIVIDEND_USER_NUMBER);
        uint256 userNumber;
        for (uint256 i = 0; i < users.length; i++) {
            if (users[i] != address(0)) {
                userNumber++;
            }
        }
        if (userNumber == 0) {
            return;
        }
        uint256 tokenBalance = balanceOf(address(sellDividendDistributor));
        if (tokenBalance > 0) {
            sellDividendDistributor.withdraw(address(this), tokenBalance);
            uint256 dividendAmount = tokenBalance / userNumber;
            address user;
            for (uint256 i = 0; i < users.length; i++) {
                user = users[i];
                if (user != address(0)) {
                    super._transfer(address(this), user, dividendAmount);
                } else {
                    break;
                }
            }
        }
    }

    function setOpenSwapTime(uint256 time) public onlyOwner {
        _openSwapTime = time;
    }
}
