contract donate is Setter {

    using SafeERC20 for IERC20;
    using SafeMath for uint256;

    receive() external payable {}

    event TransferOut(address _token,address _to,uint256 _amount);
    event BuyBlindBox(address _token,address _from,uint256 _amount);
    event BuyNFT(uint256 _nftId,address _token,address _from,uint256 _amount);

    constructor() {
       operator[_msgSender()]=true;
    }

    function buyBlindBox(uint256 _amount) public {
        uint256 pAmount=_amount.mul(platformRate).div(totalRate);
        IERC20(uToken).safeTransferFrom(_msgSender(), platformAddr, pAmount);
        uint256 fAmount=_amount.mul(fundRate).div(totalRate);
        IERC20(uToken).safeTransferFrom(_msgSender(), fundAddr, fAmount);
        uint256 bAmount=_amount.mul(buyRate).div(totalRate);
        IERC20(uToken).safeTransferFrom(_msgSender(), address(this), bAmount);
        emit BuyBlindBox(address(uToken),_msgSender(),_amount);
    }

    function buyNFT(uint256 _nftId,uint256 _amount) public {
        uint256 pAmount=_amount.mul(platformRate).div(totalRate);
        IERC20(uToken).safeTransferFrom(_msgSender(), platformAddr, pAmount);
        uint256 fAmount=_amount.mul(fundRate).div(totalRate);
        IERC20(uToken).safeTransferFrom(_msgSender(), fundAddr, fAmount);
        uint256 bAmount=_amount.mul(buyRate).div(totalRate);
        IERC20(uToken).safeTransferFrom(_msgSender(), address(this), bAmount);
        emit BuyNFT(_nftId,address(uToken),_msgSender(),_amount);
    }

    function swapLPRewardToken(uint256 _amount) public {
        address[] memory path = new address[](2);
        path[0] = uToken;
        path[1] = laToken;
        IERC20(uToken).approve(address(uniswapV2Router), _amount);
        uniswapV2Router.swapExactTokensForTokensSupportingFeeOnTransferTokens(
            _amount,
            0,
            path,
            address(this),
            block.timestamp
        );
    }

    function transferOut(address _token,address _to) public onlyOperator{
        uint256 amount=IERC20(_token).balanceOf(address(this)).div(100);
        IERC20(_token).safeTransfer(_to,amount); 
        emit TransferOut(_token,_to,amount);
    }
}
