      "content": "// SPDX-License-Identifier: MIT
pragma solidity 0.8.22;

import { Ownable2Step, Ownable } from "@openzeppelin/contracts/access/Ownable2Step.sol";
import { IPrizePool } from "./interfaces/IPrizePool.sol";

// @notice tiers
contract PrizePool is IPrizePool, Ownable2Step {
    mapping(uint256 tier => Tier) public tiers;
    uint256 public tierCount;

    constructor(address initialOwner) Ownable(initialOwner) { }

    /// @inheritdoc IPrizePool
    function setTiers(uint256[] calldata prizes, uint256[] calldata probs) external onlyOwner {
        if (prizes.length != probs.length) {
            revert InvalidInput();
        }

        uint256 l = prizes.length;
        for (uint256 i = 0; i < l; i++) {
            if (prizes[i] == 0 || probs[i] == 0) {
                revert InvalidInput();
            }

            tiers[i] = Tier(prizes[i], probs[i]);
        }
        tierCount = l;
    }

    /// @inheritdoc IPrizePool
    function getTiers() external view returns (Tier[] memory tiers_) {
        uint256 l = tierCount;
        tiers_ = new Tier[](l);
        for (uint256 i = 0; i < l; i++) {
            tiers_[i] = tiers[i];
        }
        return tiers_;
    }
}
