    "/contracts/StreamSwapBridge.sol": {
      "content": "// SPDX-License-Identifier: Apache-2.0
pragma solidity 0.8.11;

import "./interfaces/IERC20Query.sol";
import "@openzeppelin/contracts/utils/Context.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "@openzeppelin/contracts/utils/Address.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

contract StreamSwapBridge is Context {
    using SafeERC20 for IERC20;
    using SafeMath for uint256;

    mapping(string => bool) private registeredBEP20;
    mapping(bytes32 => bool) public filledOtherChainsTx;

    address payable public owner;
    address public superAdmin;
    address payable public feeReceiver;
    uint256 public swapFee;
    uint256 public feePercentageInStream;

    event OwnershipTransferred(
        address indexed previousOwner,
        address indexed newOwner
    );
    event SuperAdminChanged(
        address indexed previousSuperAdmin,
        address indexed newSuperAdmin
    );
    event FeeReceiverUpdated(
        address indexed prevFeeReceiver,
        address indexed newFeeReceiver
    );
    event SwapPairRegisterFor(
        address indexed sponsor,
        address indexed bep20Addr,
        string name,
        string symbol,
        uint8 decimals,
        string pair
    );
    event SwapStarted(
        address indexed bep20Addr,
        address indexed fromAddr,
        uint256 amount,
        uint256 feeAmount,
        uint256 feeInStream,
        string chain
    );
    event SwapFilled(
        address indexed bep20Addr,
        bytes32 indexed otherChainTxHash,
        address indexed toAddress,
        uint256 amount
    );

    constructor(
        uint256 fee_Native,
        uint256 fee_PerStream,
        address payable fee_Receiver,
        address super_Admin
    ) {
        swapFee = fee_Native;
        feePercentageInStream = fee_PerStream;
        owner = payable(msg.sender);
        feeReceiver = fee_Receiver;
        superAdmin = super_Admin;
    }

    /**
     * Throws if called by any account other than the owner.
     */
    modifier onlyOwner() {
        require(owner == _msgSender(), "Ownable: caller is not the owner");
        _;
    }

    /**
     * Throws if called transferOwnership by any account other than the super admin.
     */
    modifier onlySuperAdmin() {
        require(
            superAdmin == _msgSender(),
            "Super Admin: caller is not the super admin"
        );
        _;
    }

    modifier notContract() {
        require(!isContract(msg.sender), "contract is not allowed to swap");
        _;
    }

    modifier noProxy() {
        require(msg.sender == tx.origin, "no proxy is allowed");
        _;
    }

    function isContract(address addr) internal view returns (bool) {
        uint256 size;
        assembly {
            size := extcodesize(addr)
        }
        return size > 0;
    }

    /**
     * Leaves the contract without owner. It will not be possible to call
     * `onlySuperAdmin` functions anymore. Can only be called by the current owner.
     *
     * NOTE: Renouncing ownership will leave the contract without an owner,
     * thereby removing any functionality that is only available to the owner.
     */
    function renounceOwnership() public onlySuperAdmin {
        emit OwnershipTransferred(owner, address(0));
        owner = payable(0);
    }

    /**
     * Transfers ownership of the contract to a new account (`newOwner`).
     * Can only be called by the current owner.
     */
    function transferOwnership(address payable newOwner) public onlySuperAdmin {
        require(
            newOwner != address(0),
            "Ownable: new owner is the zero address"
        );
        emit OwnershipTransferred(owner, newOwner);
        owner = newOwner;
    }

    /**
     * Change Super Admin of the contract to a new account (`newSuperAdmin`).
     * Can only be called by the current super admin.
     */
    function changeSuperAdmin(address newSuperAdmin) public onlySuperAdmin {
        require(
            newSuperAdmin != address(0),
            "Super Admin: new super admin is the zero address"
        );
        emit SuperAdminChanged(superAdmin, newSuperAdmin);
        superAdmin = newSuperAdmin;
    }

    /**
     * Transfers fee receiver to a new account (`newFeeReceiver`).
     * Can only be called by the current owner.
     */
    function changeFeeReceiver(address payable newFeeReceiver)
        public
        onlySuperAdmin
    {
        require(
            newFeeReceiver != address(0),
            "Fee Receiver: new fee receiver address is zero "
        );
        emit FeeReceiverUpdated(feeReceiver, newFeeReceiver);
        feeReceiver = newFeeReceiver;
    }

    /**
     * Returns set minimum swap fee from BEP20 to other chains
     */
    function setSwapFee(uint256 fee) external onlyOwner {
        swapFee = fee;
    }

    /**
     * Returns set minimum swap fee in STRM from BEP20 to other chains
     */
    function setSwapFeePercentageOfSTRM(uint256 _feePerStream)
        external
        onlyOwner
    {
        require(
            _feePerStream < 100000000000000000000,
            "feePercentageInStream: Greater than 100 %"
        );
        feePercentageInStream = _feePerStream;
    }

    /**
     * Register swap pair for chain
     */
    function registerSwapPair(address bep20Addr, string calldata chain)
        external
        onlyOwner
        returns (bool)
    {
        require(
            !registeredBEP20[string(abi.encode(bep20Addr, chain))],
            "already registered"
        );

        string memory name = IERC20Query(bep20Addr).name();
        string memory symbol = IERC20Query(bep20Addr).symbol();
        uint8 decimals = IERC20Query(bep20Addr).decimals();

        require(bytes(name).length > 0, "empty name");
        require(bytes(symbol).length > 0, "empty symbol");

        registeredBEP20[string(abi.encode(bep20Addr, chain))] = true;

        emit SwapPairRegisterFor(
            msg.sender,
            bep20Addr,
            name,
            symbol,
            decimals,
            chain
        );
        return true;
    }

    /**
     * Fill swap by BEP20
     */
    function fillSwap(
        bytes32 crossChainTxHash,
        address bep20Addr,
        address toAddress,
        uint256 amount,
        string calldata chain
    ) external onlyOwner returns (bool) {
        require(!filledOtherChainsTx[crossChainTxHash], "tx filled already");
        require(
            registeredBEP20[string(abi.encode(bep20Addr, chain))],
            "not registered token"
        );
        require(amount > 0, "Amount should be greater than 0");

        IERC20(bep20Addr).safeTransfer(toAddress, amount);
        filledOtherChainsTx[crossChainTxHash] = true;

        emit SwapFilled(bep20Addr, crossChainTxHash, toAddress, amount);
        return true;
    }

    /**
     * Swap BEP20 on other chain
     */
    function swapToken(
        address bep20Addr,
        uint256 amount,
        string calldata chain
    ) external payable notContract noProxy returns (bool) {
        require(
            registeredBEP20[string(abi.encode(bep20Addr, chain))],
            "not registered token"
        );
        require(msg.value >= swapFee, "swap fee is not enough");
        require(amount > 0, "Amount should be greater than 0");
        require(
            feePercentageInStream < 100000000000000000000,
            "feePercentageInStream: Greater than 100 %"
        );

        uint256 feeAmountInStrm = 0;
        if (feePercentageInStream > 0) {
            feeAmountInStrm = amount.mul(feePercentageInStream);
            feeAmountInStrm = feeAmountInStrm.div(100000000000000000000);
            amount = amount.sub(feeAmountInStrm);
            IERC20(bep20Addr).safeTransferFrom(
                msg.sender,
                feeReceiver,
                feeAmountInStrm
            );
        }

        if (msg.value != 0) {
            owner.transfer(msg.value);
        }

        IERC20(bep20Addr).safeTransferFrom(msg.sender, address(this), amount);

        emit SwapStarted(
            bep20Addr,
            msg.sender,
            amount,
            msg.value,
            feeAmountInStrm,
            chain
        );
        return true;
    }

    /**
     * Check the token pair
     */
    function getRegisteredPairs(address bep20Addr, string calldata chain)
        external
        view
        returns (bool)
    {
        return registeredBEP20[string(abi.encode(bep20Addr, chain))];
    }
}
"
