    "contracts/token.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "@openzeppelin/contracts/utils/cryptography/MerkleProof.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";

contract MezToken is ERC20, Ownable,ReentrancyGuard  {
    using SafeMath for uint256;

    bytes32 public merkleRoot;

    uint256 public isSeedActive = 0;
    uint256 public isPresaleActive = 0;
    uint256 public rate;

    uint256 public constant TOTAL_SUPPLY = 200_000_000;

    uint256 public constant PRESALE_PERCENTAGE = 50;
    uint256 public constant SEED_SALE_PERCENTAGE = 5;
    uint256 public constant DEX_LISTING_PERCENTAGE = 10;
    uint256 public constant MARKETING_PERCENTAGE = 10;
    uint256 public constant TEAM_PERCENTAGE = 7;
    uint256 public constant ADVISORS_PERCENTAGE = 3;
    uint256 public constant PARTNERSHIPS_PERCENTAGE = 5;
    uint256 public constant STAKING_REWARD_PERCENTAGE = 10;

    uint256 public constant PRESALE_ALLOCATION = TOTAL_SUPPLY * PRESALE_PERCENTAGE / 100;
    uint256 public constant SEED_SALE_ALLOCATION = TOTAL_SUPPLY * SEED_SALE_PERCENTAGE / 100;
    uint256 public constant DEX_LISTING_ALLOCATION = TOTAL_SUPPLY * DEX_LISTING_PERCENTAGE / 100;
    uint256 public constant TEAM_ALLOCATION = TOTAL_SUPPLY * TEAM_PERCENTAGE / 100;
    uint256 public constant ADVISORS_ALLOCATION = TOTAL_SUPPLY * ADVISORS_PERCENTAGE / 100;
    uint256 public constant MARKETING_ALLOCATION = TOTAL_SUPPLY * MARKETING_PERCENTAGE / 100;
    uint256 public constant PARTNERSHIPS_ALLOCATION = TOTAL_SUPPLY * PARTNERSHIPS_PERCENTAGE / 100;
    uint256 public constant STAKING_REWARD_ALLOCATION = TOTAL_SUPPLY * STAKING_REWARD_PERCENTAGE / 100;

    uint256 public presaleSoldAmount;
    uint256 public seedSaleSoldAmount;
    uint256 public rewardedStakeAmount;

    address public marketingAddress;
    address public teamAddress;
    address public advisorsAddress;
    address public partnershipsAddress;
    address public dexListingAddress;

    mapping(address => uint256) public vestingStart;
    mapping(address => uint256) public vestedAmount;

    event TokensPurchased(address indexed buyer, uint256 amount, uint256 bonus);
    event TokensBurned(address indexed burner, uint256 amount);
    event TokensRewarded(address indexed beneficiary, uint256 amount);

    constructor(
        address _teamAddress,
        address _advisorsAddress,
        address _marketingAddress,
        address _partnershipsAddress,
        address _dexListingAddress
    ) ERC20("MezGPT", "MEZ") {
        teamAddress = _teamAddress;
        advisorsAddress = _advisorsAddress;
        marketingAddress = _marketingAddress;
        partnershipsAddress = _partnershipsAddress;
        dexListingAddress = _dexListingAddress;

        _mint(marketingAddress, MARKETING_ALLOCATION * 10**decimals());
        _mint(partnershipsAddress, PARTNERSHIPS_ALLOCATION * 10**decimals());
        _mint(dexListingAddress, DEX_LISTING_ALLOCATION * 10**decimals());

        vestedAmount[teamAddress] = TEAM_ALLOCATION * 10**decimals();
        vestedAmount[advisorsAddress] = ADVISORS_ALLOCATION * 10**decimals();

        vestingStart[teamAddress] = block.timestamp;
        vestingStart[advisorsAddress] = block.timestamp;
    }

    function releaseVestedTokens() external onlyOwner {
        require(block.timestamp >= vestingStart[teamAddress] + 365 days, "Team vesting period has not ended");
        require(block.timestamp >= vestingStart[advisorsAddress] + 365 days, "Advisors vesting period has not ended");

        uint256 unreleasedTeamToken = vestedAmount[teamAddress];
        uint256 unreleasedAdvisorsToken = vestedAmount[advisorsAddress];

        require(unreleasedTeamToken > 0 || unreleasedAdvisorsToken > 0, "No tokens to release");

        vestedAmount[teamAddress] = 0;
        vestedAmount[advisorsAddress] = 0;

        _mint(teamAddress, unreleasedTeamToken);
        _mint(advisorsAddress, unreleasedAdvisorsToken);
    }

    function setMerkleRoot(bytes32 _merkleRoot) external onlyOwner {
        merkleRoot = _merkleRoot;
    }

    function setRate(uint256 _rate) external onlyOwner {
        rate = _rate;
    }

    function activatePresale() external onlyOwner {
        require(isPresaleActive == 0, "Presale is already active");
        isPresaleActive = 1;
    }

    function activateSeedSale() external onlyOwner {
        require(isSeedActive == 0, "Seed sale is already active");
        isSeedActive = 1;
    }

    function deactivatePresale() external onlyOwner {
        require(isPresaleActive == 1, "Presale is not active");
        isPresaleActive = 0;
    }

    function deactivateSeedSale() external onlyOwner {
        require(isSeedActive == 1, "Seed sale is not active");
        isSeedActive = 0;
    }

    function buyTokens(bytes32[] memory proof) external payable nonReentrant {
        require(msg.value > 0, "Amount must be greater than 0");
        require(isPresaleActive == 1 || isSeedActive == 1, "Token sales not active");

        bool isWhitelisted = MerkleProof.verify(proof, merkleRoot, keccak256(abi.encodePacked(msg.sender)));
        if (isSeedActive == 1) {
          require(isWhitelisted, "Address not whitelisted for the seed sale");
        }

        uint256 tokenAmount = (msg.value / rate) * 10**18;
        uint256 bonusAmount = 0;
        if (isPresaleActive == 1 && isSeedActive == 0) {
            if(isWhitelisted){
                bonusAmount = tokenAmount * 20 / 100;
                tokenAmount += bonusAmount;
            }

            require(presaleSoldAmount.add(tokenAmount) <= PRESALE_ALLOCATION  * 10**decimals(), "Presale allocation exceeded");
            presaleSoldAmount += tokenAmount;
        } else if (isPresaleActive == 0 && isSeedActive == 1) {
            require(seedSaleSoldAmount.add(tokenAmount) <= SEED_SALE_ALLOCATION  * 10**decimals(), "Seed sale allocation exceeded");
            seedSaleSoldAmount += tokenAmount;
        } else {
            revert("Undetermined sale");
        }

        _mint(msg.sender, tokenAmount);
        emit TokensPurchased(msg.sender, tokenAmount, bonusAmount);

        payable(teamAddress).transfer(msg.value);
    }

    function burn(uint256 amount) external onlyOwner {
        _burn(msg.sender, amount);
        emit TokensBurned(msg.sender, amount);
    }

    function reward(address beneficiary, uint256 amount) external  onlyOwner {
        require(rewardedStakeAmount.add(amount) <= STAKING_REWARD_ALLOCATION  * 10**decimals(), "Stake reward allocation exceeded");

        rewardedStakeAmount += amount;
        _mint(beneficiary, amount);
        emit TokensRewarded(beneficiary, amount);
    }

    receive() external payable {
        revert("Contract does not accept fee directly");
    }
}
